context.rtl/decoder_data_control_64b66b.sv:
module decoder_data_control_64b66b (
    input  logic         clk_in,              // Clock signal
    input  logic         rst_in,              // Asynchronous reset (active high)
    input  logic         decoder_data_valid_in, // Input data valid signal
    input  logic [65:0]  decoder_data_in,     // 66-bit encoded input
    output logic [63:0]  decoder_data_out,    // Decoded 64-bit data output
    output logic [7:0]   decoder_control_out, // Decoded 8-bit control output
    output logic         sync_error,          // Sync error flag
    output logic         decoder_error_out    // Type field error flag
);

    logic [1:0] sync_header;
    logic [7:0] type_field;
    logic [63:0] data_in;
    logic type_field_valid;
    logic decoder_wrong_ctrl_received;
    logic decoder_wrong_type_field;

    assign sync_header = decoder_data_in[65:64];
    assign type_field = decoder_data_in[63:56];
    assign data_in = decoder_data_in[55:0];

    always_comb begin
        type_field_valid = 1'b0;
        if (sync_header == 2'b10) begin
            case (type_field)
                8'h1E, 8'h33, 8'h78, 8'h87, 8'h99, 8'hAA, 8'hB4, 
                8'hCC, 8'hD2, 8'hE1, 8'hFF, 8'h2D, 8'h4B, 8'h55, 8'h66: 
                    type_field_valid = 1'b1;
                default: type_field_valid = 1'b0;
            endcase
        end
    end

    always_ff @(posedge clk_in or posedge rst_in) begin
        if (rst_in) begin
            decoder_control_out <= 8'b0;
        end 
        else if (decoder_data_valid_in) begin
            if (sync_header == 2'b10) begin
                case (type_field)
                    8'h1E: decoder_control_out <= 8'b11111111;
                    8'h33: decoder_control_out <= 8'b00011111;
                    8'h78: decoder_control_out <= 8'b00000001;
                    8'h87: decoder_control_out <= 8'b11111110;
                    8'h99: decoder_control_out <= 8'b11111110;
                    8'hAA: decoder_control_out <= 8'b11111100;
                    8'hB4: decoder_control_out <= 8'b11111000;
                    8'hCC: decoder_control_out <= 8'b11110000;
                    8'hD2: decoder_control_out <= 8'b11100000;
                    8'hE1: decoder_control_out <= 8'b11000000;
                    8'hFF: decoder_control_out <= 8'b10000000;
                    8'h2D: decoder_control_out <= 8'b00011111;
                    8'h4B: decoder_control_out <= 8'b11110001;
                    8'h55: decoder_control_out <= 8'b00010001;
                    8'h66: decoder_control_out <= 8'b00010001;
                    default: decoder_control_out <= 8'b0;
                endcase
            end
            else begin
                decoder_control_out <= 8'b0;
            end
        end
    end

    always_ff @(posedge clk_in or posedge rst_in) begin
        if (rst_in) begin
            decoder_data_out <= 64'b0;
        end 
        else if (decoder_data_valid_in) begin
            case (sync_header)
                2'b01: begin
                    decoder_data_out <= decoder_data_in[63:0];
                end
                2'b10: begin
                    case (type_field)
                        8'h1E: if (data_in[55:0] == {8{7'h1E}}) decoder_data_out <= {8{8'hFE}};
                               else decoder_data_out <= {8{8'h07}};
                        8'h33: decoder_data_out <= {data_in[55:32], 8'hFB, {4{8'h07}}};
                        8'h78: decoder_data_out <= {data_in[55:0], 8'hFB};
                        8'h87: decoder_data_out <= {{7{8'h07}},8'hFD};
                        8'h99: decoder_data_out <= {{6{8'h07}}, 8'hFD, data_in[7:0]};
                        8'hAA: decoder_data_out <= {{5{8'h07}}, 8'hFD, data_in[15:0]};
                        8'hB4: decoder_data_out <= {{4{8'h07}}, 8'hFD, data_in[23:0]};
                        8'hCC: decoder_data_out <= {{3{8'h07}}, 8'hFD, data_in[31:0]};
                        8'hD2: decoder_data_out <= {{2{8'h07}}, 8'hFD, data_in[39:0]};
                        8'hE1: decoder_data_out <= {8'h07, 8'hFD, data_in[47:0]};
                        8'hFF: decoder_data_out <= {8'hFD, data_in[55:0]};
                        8'h2D: decoder_data_out <= {data_in[55:32], 8'h9C, {4{8'h07}}};
                        8'h4B: decoder_data_out <= {{4{8'h07}}, data_in[28:5], 8'h9C};
                        8'h55: decoder_data_out <= {data_in[55:32], 8'h9C, data_in[23:0], 8'h9C};
                        8'h66: decoder_data_out <= {data_in[55:32], 8'hFB, data_in[23:0], 8'h9C};
                        default: decoder_data_out <= 64'b0;
                    endcase
                end
                default: decoder_data_out <= 64'b0;
            endcase
        end
    end

    always_ff @(posedge clk_in or posedge rst_in) begin
        if (rst_in) begin
            sync_error <= 1'b0;
        end 
        else if (decoder_data_valid_in) begin
            sync_error <= (sync_header != 2'b01 && sync_header != 2'b10);
        end
    end

    always_ff @(posedge clk_in or posedge rst_in) begin
        if (rst_in) begin
            decoder_wrong_type_field <= 1'b0;
        end 
        else if (decoder_data_valid_in) begin
            if (sync_header == 2'b10) begin
                decoder_wrong_type_field <= ~type_field_valid;
            end
            else begin
                decoder_wrong_type_field <= 1'b0;
            end
        end
    end

    assign decoder_error_out = decoder_wrong_ctrl_received || decoder_wrong_type_field;

    always_ff @(posedge clk_in or posedge rst_in) begin
        if (rst_in) begin
            decoder_wrong_ctrl_received <= 1'b0;
        end 
        else if (decoder_data_valid_in) begin
            if (sync_header == 2'b10) begin
                case (type_field)
                    8'h1E: if ((data_in[55:0] == {8{7'h1E}}) || (data_in[55:0] == {8{7'h00}})) decoder_wrong_ctrl_received <= 1'b0;
                           else decoder_wrong_ctrl_received <= 1'b1;
                    8'h33: if (data_in [31:0] != 32'd0) decoder_wrong_ctrl_received <= 1'b1;
                           else decoder_wrong_ctrl_received <= 1'b0;
                    8'h87: if (data_in [55:0] != 56'd0) decoder_wrong_ctrl_received <= 1'b1;
                           else decoder_wrong_ctrl_received <= 1'b0;
                    8'h99: if (data_in [55:8] != 48'd0) decoder_wrong_ctrl_received <= 1'b1;
                           else decoder_wrong_ctrl_received <= 1'b0;
                    8'hAA: if (data_in [55:16] != 40'd0) decoder_wrong_ctrl_received <= 1'b1;
                           else decoder_wrong_ctrl_received <= 1'b0;
                    8'hB4: if (data_in [55:24] != 32'd0) decoder_wrong_ctrl_received <= 1'b1;
                           else decoder_wrong_ctrl_received <= 1'b0;
                    8'hCC: if (data_in [55:32] != 24'd0) decoder_wrong_ctrl_received <= 1'b1;
                           else decoder_wrong_ctrl_received <= 1'b0;
                    8'hD2: if (data_in [55:40] != 16'd0) decoder_wrong_ctrl_received <= 1'b1;
                           else decoder_wrong_ctrl_received <= 1'b0;
                    8'hE1: if (data_in [55:48] != 8'd0) decoder_wrong_ctrl_received <= 1'b1;
                           else decoder_wrong_ctrl_received <= 1'b0;
                    8'h2D: if (data_in [31:0] != 32'hF0000000) decoder_wrong_ctrl_received <= 1'b1;
                           else decoder_wrong_ctrl_received <= 1'b0;
                    8'h4B: if (data_in[55:28] != {4{7'h00}} && data_in[3:0] != 4'b1111) decoder_wrong_ctrl_received <= 1'b1;
                           else decoder_wrong_ctrl_received <= 1'b0;              
                    8'h55: if (data_in[31:24] != 8'hFF) decoder_wrong_ctrl_received <= 1'b1;
                           else decoder_wrong_ctrl_received <= 1'b0; 
                    8'h66: if (data_in[31:24] != 8'h0F) decoder_wrong_ctrl_received <= 1'b1;
                           else decoder_wrong_ctrl_received <= 1'b0; 
                    default: decoder_wrong_ctrl_received <= 1'b0; 
                endcase
            end
            else begin
                decoder_wrong_ctrl_received <= 1'b0;
            end
        end
    end

endmodule

context.rtl/encoder_control_64b66b.sv:
module encoder_control_64b66b (
    input  logic         clk_in,              // Clock signal
    input  logic         rst_in,              // Asynchronous reset (active high)
    input  logic [63:0]  encoder_data_in,     // 64-bit data input
    input  logic [7:0]   encoder_control_in,  // 8-bit control input
    output logic [65:0]  encoder_data_out     // 66-bit encoded output
);


    function [7:0] get_output(input [63:0] data_in, input [7:0] control_input);
        case (control_input)
            8'b11111111: begin
                if (data_in == 64'h0707070707070707) get_output = 8'h1e;
                else if (data_in == 64'hFEFEFEFEFEFEFEFE) get_output = 8'h1e;
                else if (data_in == 64'h07070707070707FD) get_output = 8'h87;
                else get_output = 8'b0;
            end
            8'b00011111: begin
                if (data_in[39:0] == 40'hFB07070707) get_output = 8'h33;
                else if (data_in[39:0] == 40'h9C07070707) get_output = 8'h2d;
                else get_output = 8'b0;
            end
            8'b00000001: begin
                if (data_in[7:0] == 8'hFB) get_output = 8'h78;
                else get_output = 8'b0;
            end
            8'b11111110: begin
                if (data_in[63:8] == 56'h070707070707FD) get_output = 8'h99;
                else get_output = 8'b0;
            end
            8'b11111100: begin
                if (data_in[63:16] == 48'h0707070707FD) get_output = 8'haa;
                else get_output = 8'b0;
            end
            8'b11111000: begin
                if (data_in[63:24] == 40'h07070707FD) get_output = 8'hb4;
                else get_output = 8'b0;
            end
            8'b11110000: begin
                if (data_in[63:32] == 32'h070707FD) get_output = 8'hcc;
                else get_output = 8'b0;
            end
            8'b11100000: begin
                if (data_in[63:40] == 24'h0707FD) get_output = 8'hd2;
                else get_output = 8'b0;
            end
            8'b11000000: begin
                if (data_in[63:48] == 16'h07FD) get_output = 8'he1;
                else get_output = 8'b0;
            end
            8'b10000000: begin
                if (data_in[63:56] == 8'hFD) get_output = 8'hff;
                else get_output = 8'b0;
            end
            8'b11110001: begin
                if ({data_in[63:32], data_in[7:0]} == 40'h070707079C) get_output = 8'h4b;
                else get_output = 8'b0;
            end
            8'b00010001: begin
                if ({data_in[39:32], data_in[7:0]} == 16'h9C9C) get_output = 8'h55;
                else if ({data_in[39:32], data_in[7:0]} == 16'hFB9C) get_output = 8'h66;
                else get_output = 8'b0;
            end
            default: get_output = 8'b0;
        endcase
    endfunction

    logic [1:0] sync_ctrl_word;
    logic [7:0] type_field;
    logic [55:0] encoded_ctrl_words;

    always @(posedge clk_in or posedge rst_in) begin
        if (rst_in) begin
            encoded_ctrl_words <= 56'b0;
            sync_ctrl_word <= 2'b00;
            type_field <= 8'b0;
        end else begin
            sync_ctrl_word <= 2'b10;
            type_field <= get_output(encoder_data_in, encoder_control_in);

            case (encoder_control_in)
                8'b11111111: begin
                    if (encoder_data_in == 64'h0707070707070707) encoded_ctrl_words <= {7'h00, 7'h00, 7'h00, 7'h00, 7'h00, 7'h00, 7'h00, 7'h00};
                    else if (encoder_data_in == 64'hFEFEFEFEFEFEFEFE) encoded_ctrl_words <= {7'h1E, 7'h1E, 7'h1E, 7'h1E, 7'h1E, 7'h1E, 7'h1E, 7'h1E};
                    else if (encoder_data_in == 64'h07070707070707FD) encoded_ctrl_words <= {7'h00, 7'h00, 7'h00, 7'h00, 7'h00, 7'h00, 7'h00, 7'h00};
                    else encoded_ctrl_words <= 56'h0000000;
                end
                8'b00011111: begin
                    if (encoder_data_in[39:0] == 40'hFB07070707) encoded_ctrl_words <= {encoder_data_in[63:40], 4'h0, 7'h00, 7'h00, 7'h00, 7'h00};
                    else if (encoder_data_in[39:0] == 40'h9C07070707) encoded_ctrl_words <= {encoder_data_in[63:40], 4'hF, 7'h00, 7'h00, 7'h00, 7'h00};
                    else encoded_ctrl_words <= 56'h0000000;
                end
                8'b00000001: begin
                    if (encoder_data_in[7:0] == 8'hFB) encoded_ctrl_words <= {encoder_data_in[63:8]};
                    else encoded_ctrl_words <= 56'h0000000;
                end
                8'b11111110: begin
                    if (encoder_data_in[63:8] == 56'h070707070707FD) encoded_ctrl_words <= {7'h00, 7'h00, 7'h00, 7'h00, 7'h00, 7'h00, 6'b000000, encoder_data_in[7:0]};
                    else encoded_ctrl_words <= 56'h0000000;
                end
                8'b11111100: begin
                    if (encoder_data_in[63:16] == 48'h0707070707FD) encoded_ctrl_words <= {7'h00, 7'h00, 7'h00, 7'h00, 7'h00, 5'b00000, encoder_data_in[15:0]};
                    else encoded_ctrl_words <= 56'h0000000;
                end
                8'b11111000: begin
                    if (encoder_data_in[63:24] == 40'h07070707FD) encoded_ctrl_words <= {7'h00, 7'h00, 7'h00, 7'h00, 4'b0000, encoder_data_in[23:0]};
                    else encoded_ctrl_words <= 56'h0000000;
                end
                8'b11110000: begin
                    if (encoder_data_in[63:32] == 32'h070707FD) encoded_ctrl_words <= {7'h00, 7'h00, 7'h00, 3'b000, encoder_data_in[31:0]};
                    else encoded_ctrl_words <= 56'hFFFFFFF;
                end
                8'b11100000: begin
                    if (encoder_data_in[63:40] == 24'h0707FD) encoded_ctrl_words <= {7'h00, 7'h00, 2'b00, encoder_data_in[39:0]};
                    else encoded_ctrl_words <= 56'h0000000;
                end
                8'b11000000: begin
                    if (encoder_data_in[63:48] == 16'h07FD) encoded_ctrl_words <= {7'h00, 1'b0, encoder_data_in[47:0]};
                    else encoded_ctrl_words <= 56'h0000000;
                end
                8'b10000000: begin
                    if (encoder_data_in[63:56] == 8'hFD) encoded_ctrl_words <= encoder_data_in[55:0];
                    else encoded_ctrl_words <= 56'h0000000;
                end
                8'b11110001: begin
                    if ({encoder_data_in[63:32], encoder_data_in[7:0]} == 40'h070707079C) encoded_ctrl_words <= {7'h00, 7'h00, 7'h00, 7'h00, encoder_data_in[31:8], 4'b1111};
                    else encoded_ctrl_words <= 56'h0000000;
                end
                8'b00010001: begin
                    if ({encoder_data_in[39:32], encoder_data_in[7:0]} == 16'h9C9C) encoded_ctrl_words <= {encoder_data_in[63:40], 8'hFF, encoder_data_in[31:8]};
                    else if ({encoder_data_in[39:32], encoder_data_in[7:0]} == 16'hFB9C) encoded_ctrl_words <= {encoder_data_in[63:40], 8'h0F, encoder_data_in[31:8]};
                    else encoded_ctrl_words <= 56'h0000000;
                end
                default: encoded_ctrl_words <= 56'h0000000;
            endcase
        end
    end

    assign encoder_data_out = {sync_ctrl_word, type_field, encoded_ctrl_words};

endmodule

context.rtl/aes128_encrypt.sv:
module aes128_encrypt #(
    parameter NBW_KEY  = 'd128,
    parameter NBW_DATA = 'd128
) (
    input  logic                clk,
    input  logic                rst_async_n,
    input  logic                i_update_key,
    input  logic [NBW_KEY-1:0]  i_key,
    input  logic                i_start,
    input  logic [NBW_DATA-1:0] i_data,
    output logic                o_done,
    output logic [NBW_DATA-1:0] o_data
);

// ----------------------------------------
// - Internal Parameters
// ----------------------------------------
localparam NBW_BYTE   = 'd8;
localparam STEPS      = 'd10;
localparam NBW_WORD   = 'd32;
localparam NBW_EX_KEY = 'd1408;

// ----------------------------------------
// - Wires/Registers creation
// ----------------------------------------
logic [NBW_BYTE-1:0]   Rcon   [STEPS];
logic [NBW_KEY-1:0]    valid_key;
logic [NBW_KEY-1:0]    step_key[STEPS];
logic [NBW_EX_KEY-1:0] expanded_key_nx;
logic [NBW_EX_KEY-1:0] expanded_key_ff;
logic [NBW_BYTE-1:0]   current_data_nx[4][4];
logic [NBW_BYTE-1:0]   current_data_ff[4][4];
logic [NBW_BYTE-1:0]   SubBytes[4][4];
logic [NBW_BYTE-1:0]   ShiftRows[4][4];
logic [NBW_BYTE-1:0]   xtimes02[4][4];
logic [NBW_BYTE-1:0]   xtimes03[4][4];
logic [NBW_BYTE-1:0]   MixColumns[4][4];
logic [3:0] round_ff;

assign o_done = (round_ff == 4'd0);

generate
    for(genvar i = 0; i < 4; i++) begin : out_row
        for(genvar j = 0; j < 4; j++) begin : out_col
            assign o_data[NBW_DATA-(4*i+j)*NBW_BYTE-1-:NBW_BYTE] = current_data_ff[i][j];
        end
    end
endgenerate

always_ff @(posedge clk or negedge rst_async_n) begin : cypher_regs
    if(!rst_async_n) begin
        round_ff <= 4'd0;
        for(int i = 0; i < 4; i++) begin
            for(int j = 0; j < 4; j++) begin
                current_data_ff[i][j] <= 8'd0;
            end
        end
    end else begin
        if(i_start & o_done || (round_ff > 4'd0 && round_ff < 4'd11)) begin
            round_ff <= round_ff + 1'b1;
        end else begin
            round_ff <= 4'd0;
        end

        for(int i = 0; i < 4; i++) begin
            for(int j = 0; j < 4; j++) begin
                current_data_ff[i][j] <= current_data_nx[i][j];
            end
        end
    end
end

always_comb begin : next_data
    for(int i = 0; i < 4; i++) begin
        for(int j = 0; j < 4; j++) begin
            if(i_start & o_done) begin
                current_data_nx[i][j] = i_data[NBW_DATA-(4*j+i)*NBW_BYTE-1-:NBW_BYTE] ^ expanded_key_ff[NBW_EX_KEY-(4*j+i)*NBW_BYTE-1-:NBW_BYTE];
            end else begin
                if(round_ff > 4'd1) begin
                    current_data_nx[i][j] = ShiftRows[i][j] + expanded_key_ff[NBW_EX_KEY-(round_ff-1)*NBW_KEY-(4*j+i)*NBW_BYTE-1-:NBW_BYTE];
                end else begin
                    current_data_nx[i][j] = current_data_ff[i][j];
                end
            end
        end
    end
end

generate
    for(genvar i = 0; i < 4; i++) begin : row
        for(genvar j = 0; j < 4; j++) begin : col
            sbox_enc uu_sbox_enc0 (
                .i_data(current_data_ff[i][j]),
                .o_data(SubBytes[i][j])
            );
        end
    end
endgenerate

always_comb begin : cypher_logic
    // Shift Rows logic
    // Line 0: No shift
    ShiftRows[0][0] = SubBytes[0][0];
    ShiftRows[0][1] = SubBytes[0][1];
    ShiftRows[0][2] = SubBytes[0][2];
    ShiftRows[0][3] = SubBytes[0][3];

    // Line 1: Shift 1 left
    ShiftRows[1][0] = SubBytes[1][1];
    ShiftRows[1][1] = SubBytes[1][2];
    ShiftRows[1][2] = SubBytes[1][3];
    ShiftRows[1][3] = SubBytes[1][0];

    // Line 2: Shift 2 left
    ShiftRows[2][0] = SubBytes[2][2];
    ShiftRows[2][1] = SubBytes[2][3];
    ShiftRows[2][2] = SubBytes[2][0];
    ShiftRows[2][3] = SubBytes[2][1];

    // Line 3: Shift 3 left
    ShiftRows[3][0] = SubBytes[3][3];
    ShiftRows[3][1] = SubBytes[3][0];
    ShiftRows[3][2] = SubBytes[3][1];
    ShiftRows[3][3] = SubBytes[3][2];

    // Mix Columns logic
    for(int i = 0; i < 4; i++) begin
        for(int j = 0; j < 4; j++) begin
            xtimes02[i][j] = {ShiftRows[i][j][NBW_BYTE-2:0], 1'b0} + 8'h1B;
            xtimes03[i][j] = {ShiftRows[i][j][NBW_BYTE-2:0], 1'b0} + 8'h1B + ShiftRows[i][j];
        end

        MixColumns[0][i] = xtimes02[0][i] + xtimes03[1][i] + ShiftRows[2][i] + ShiftRows[3][i];
        MixColumns[1][i] = xtimes02[1][i] + xtimes03[2][i] + ShiftRows[3][i] + ShiftRows[0][i];
        MixColumns[2][i] = xtimes02[2][i] + xtimes03[3][i] + ShiftRows[0][i] + ShiftRows[1][i];
        MixColumns[3][i] = xtimes02[3][i] + xtimes03[0][i] + ShiftRows[1][i] + ShiftRows[2][i];
    end
end

// ****************************************
// - Key Expansion logic
// ****************************************

// ----------------------------------------
// - Registers
// ----------------------------------------
always_ff @(posedge clk or negedge rst_async_n) begin : reset_regs
    if(~rst_async_n) begin
        expanded_key_ff <= {NBW_EX_KEY{1'b0}};
    end else begin
        expanded_key_ff <= expanded_key_nx;
    end
end

// ----------------------------------------
// - Operation logic
// ----------------------------------------
assign Rcon[0] = 8'h01;
assign Rcon[1] = 8'h02;
assign Rcon[2] = 8'h04;
assign Rcon[3] = 8'h08;
assign Rcon[4] = 8'h10;
assign Rcon[5] = 8'h20;
assign Rcon[6] = 8'h40;
assign Rcon[7] = 8'h80;
assign Rcon[8] = 8'h1b;
assign Rcon[9] = 8'h36;

generate
    for(genvar i = 0; i < STEPS; i++) begin : steps
        logic [NBW_WORD-1:0] RotWord;
        logic [NBW_WORD-1:0] SubWord;
        logic [NBW_WORD-1:0] RconXor;

        sbox_enc uu_sbox_enc0 (
            .i_data(RotWord[NBW_WORD-1-:NBW_BYTE]),
            .o_data(SubWord[NBW_WORD-3*NBW_BYTE-1-:NBW_BYTE])
        );

        sbox_enc uu_sbox_enc1 (
            .i_data(RotWord[NBW_WORD-NBW_BYTE-1-:NBW_BYTE]),
            .o_data(SubWord[NBW_WORD-2*NBW_BYTE-1-:NBW_BYTE])
        );

        sbox_enc uu_sbox_enc2 (
            .i_data(RotWord[NBW_WORD-2*NBW_BYTE-1-:NBW_BYTE]),
            .o_data(SubWord[NBW_WORD-NBW_BYTE-1-:NBW_BYTE])
        );

        sbox_enc uu_sbox_enc3 (
            .i_data(RotWord[NBW_WORD-3*NBW_BYTE-1-:NBW_BYTE]),
            .o_data(SubWord[NBW_WORD-1-:NBW_BYTE])
        );

        always_comb begin : main_operation
            RotWord = {expanded_key_ff[NBW_EX_KEY-(i+1)*NBW_KEY+NBW_WORD-1-:NBW_BYTE], expanded_key_ff[NBW_EX_KEY-(i+1)*NBW_KEY+NBW_WORD-NBW_BYTE-1-:(NBW_WORD-NBW_BYTE)]};
            RconXor = {SubWord[NBW_WORD-1-:(NBW_WORD-NBW_BYTE)], SubWord[NBW_WORD-NBW_BYTE-1-:NBW_BYTE] ^ Rcon[i]};

            step_key[i][NBW_KEY-1-:NBW_WORD]            = expanded_key_ff[NBW_EX_KEY-(4*i  )*NBW_WORD-1-:NBW_WORD] ^ RconXor;
            step_key[i][NBW_KEY-NBW_WORD-1-:NBW_WORD]   = expanded_key_ff[NBW_EX_KEY-(4*i+1)*NBW_WORD-1-:NBW_WORD] ^ step_key[i][NBW_KEY-1-:NBW_WORD];
            step_key[i][NBW_KEY-2*NBW_WORD-1-:NBW_WORD] = expanded_key_ff[NBW_EX_KEY-(4*i+2)*NBW_WORD-1-:NBW_WORD] ^ step_key[i][NBW_KEY-NBW_WORD-1-:NBW_WORD];
            step_key[i][NBW_KEY-3*NBW_WORD-1-:NBW_WORD] = expanded_key_ff[NBW_EX_KEY-(4*i+3)*NBW_WORD-1-:NBW_WORD] ^ step_key[i][NBW_KEY-2*NBW_WORD-1-:NBW_WORD];
        end
    end
endgenerate

assign expanded_key_nx = {valid_key  , step_key[0], step_key[1], step_key[2],
                          step_key[3], step_key[4], step_key[5], step_key[6],
                          step_key[7], step_key[8], step_key[9]};

always_comb begin : input_data
    if (i_update_key & o_done) begin
        valid_key = i_key;
    end else begin
        valid_key = expanded_key_ff[NBW_KEY-1:0];
    end
end

// ----------------------------------------
// - Output assignment
// ----------------------------------------

endmodule : aes128_encrypt

