module tb_aes128_enc;

localparam NBW_KEY  = 'd128;
localparam NBW_DATA = 'd128;

logic                clk;
logic                rst_async_n;
logic                i_update_key;
logic [NBW_KEY-1:0]  i_key;
logic                i_start;
logic [NBW_DATA-1:0] i_data;
logic                o_done;
logic [NBW_DATA-1:0] o_data;

aes128_encrypt #(
    .NBW_KEY(NBW_KEY),
    .NBW_DATA(NBW_DATA)
) uu_aes128_encrypt (
    .clk(clk),
    .rst_async_n(rst_async_n),
    .i_update_key(i_update_key),
    .i_key(i_key),
    .i_start(i_start),
    .i_data(i_data),
    .o_done(o_done),
    .o_data(o_data)
);

task Simple_test(logic update_key);
    @(negedge clk);
    i_key = 128'h2b7e151628aed2a6abf7158809cf4f3c;
    i_data = 128'h3243f6a8885a308d313198a2e0370734;
    i_update_key = update_key;
    i_start = 1;

    @(negedge clk);
    i_start = 0;
    i_update_key = 0;
    i_key = 0;

    @(posedge o_done);
    @(negedge clk);

    if(o_data == 128'h3925841d02dc09fbdc118597196a0b32) begin
        $display("PASS");
    end else begin
        $display("FAIL");
        $display("Expected output: %h", 128'h3925841d02dc09fbdc118597196a0b32);
        $display("Observed output: %h", o_data);
    end
endtask

initial begin
    $dumpfile("test.vcd");
    $dumpvars(0,tb_aes128_enc);
end

always #5 clk = ~clk;

initial begin
    clk = 0;
    i_start = 0;
    rst_async_n = 1;
    #1;
    rst_async_n = 0;
    #2;
    rst_async_n = 1;
    @(negedge clk);

    // Tasks go here
    Simple_test(1'b1);
    Simple_test(1'b0);

    @(negedge clk);
    @(negedge clk);

    $finish();
end

endmodule

context.rtl/aes128_decrypt.sv:
module aes128_decrypt #(
    parameter NBW_KEY  = 'd128,
    parameter NBW_DATA = 'd128
) (
    input  logic                clk,
    input  logic                rst_async_n,
    input  logic                i_update_key,
    input  logic [NBW_KEY-1:0]  i_key,
    input  logic                i_start,
    input  logic [NBW_DATA-1:0] i_data,
    output logic                o_done,
    output logic [NBW_DATA-1:0] o_data
);

// ----------------------------------------
// - Internal Parameters
// ----------------------------------------
localparam NBW_BYTE   = 'd8;
localparam NBW_EX_KEY = 'd1408;

// ----------------------------------------
// - Wires/Registers creation
// ----------------------------------------
logic [NBW_BYTE-1:0]   current_data_nx[4][4];
logic [NBW_BYTE-1:0]   current_data_ff[4][4];
logic [NBW_BYTE-1:0]   AddRoundKey[4][4];
logic [NBW_BYTE-1:0]   SubBytes[4][4];
logic [NBW_BYTE-1:0]   ShiftRows[4][4];
logic [NBW_BYTE-1:0]   xtimes02[4][4];
logic [NBW_BYTE-1:0]   xtimes04[4][4];
logic [NBW_BYTE-1:0]   xtimes08[4][4];
logic [NBW_BYTE-1:0]   xtimes09[4][4];
logic [NBW_BYTE-1:0]   xtimes0b[4][4];
logic [NBW_BYTE-1:0]   xtimes0d[4][4];
logic [NBW_BYTE-1:0]   xtimes0e[4][4];
logic [NBW_BYTE-1:0]   MixColumns[4][4];
logic                  key_done;
logic [3:0]            round_ff;
logic [NBW_EX_KEY-1:0] expanded_key;

// ----------------------------------------
// - Output assignment
// ----------------------------------------
assign o_done = (round_ff == 4'd0);

generate
    for(genvar i = 0; i < 4; i++) begin : out_row
        for(genvar j = 0; j < 4; j++) begin : out_col
            assign o_data[NBW_DATA-(4*j+i)*NBW_BYTE-1-:NBW_BYTE] = current_data_ff[i][j];
        end
    end
endgenerate

always_ff @(posedge clk or negedge rst_async_n) begin : inv_cypher_regs
    if(!rst_async_n) begin
        round_ff <= 4'd0;
        for(int i = 0; i < 4; i++) begin
            for(int j = 0; j < 4; j++) begin
                current_data_ff[i][j] <= 8'd0;
            end
        end
    end else begin
        if(i_start & o_done) begin
            round_ff <= 4'd1;
        end else if((round_ff > 4'd1 && round_ff < 4'd12) || key_done) begin
            round_ff <= round_ff + 1'b1;
        end else begin
            if(round_ff >= 4'd12) begin
                round_ff <= 4'd0;
            end else begin
                round_ff <= 4'd2;
            end
        end

        for(int i = 0; i < 4; i++) begin
            for(int j = 0; j < 4; j++) begin
                current_data_ff[i][j] <= current_data_nx[i][j];
            end
        end
    end
end

always_comb begin : next_data
    for(int i = 0; i < 4; i++) begin
        for(int j = 0; j < 4; j++) begin
            if(i_start & o_done) begin
                current_data_nx[i][j] = i_data[NBW_DATA-(4*i+j)*NBW_BYTE-1-:NBW_BYTE];
            end else begin
                if(round_ff != 0) begin
                    if(round_ff != 11) begin
                        current_data_nx[i][j] = SubBytes[i][j];
                    end else begin
                        current_data_nx[i][j] = AddRoundKey[i][j];
                    end
                end else begin
                    current_data_nx[i][j] = current_data_ff[i][j];
                end
            end
        end
    end
end

generate
    for(genvar i = 0; i < 4; i++) begin : row
        for(genvar j = 0; j < 4; j++) begin : col
            inv_sbox uu_inv_sbox0 (
                .i_data(ShiftRows[i][j]),
                .o_data(SubBytes[i][j])
            );
        end
    end
endgenerate

always_comb begin : decypher_logic
    // Add Round Key logic
    for(int i = 0; i < 4; i++) begin : row_key
        for(int j = 0; j < 4; j++) begin : col_key
            if(round_ff > 4'd0) begin
                AddRoundKey[i][j] = current_data_ff[i][j] ^ expanded_key[NBW_EX_KEY-(11-round_ff)*NBW_KEY-(4*i+j)*NBW_BYTE-1-:NBW_BYTE];
            end else begin
                AddRoundKey[i][j] = 0;
            end
        end
    end

    // Mix Columns logic
    for(int i = 0; i < 4; i++) begin
        for(int j = 0; j < 4; j++) begin
            if(AddRoundKey[i][j][NBW_BYTE-1]) begin
                xtimes02[i][j] = {AddRoundKey[i][j][NBW_BYTE-2:0], 1'b0} ^ 8'h1B;
                xtimes04[i][j] = {xtimes02[i][j][NBW_BYTE-2:0], 1'b0} ^ 8'h1B;
                xtimes08[i][j] = {xtimes04[i][j][NBW_BYTE-2:0], 1'b0} ^ 8'h1B;
            end else begin
                xtimes02[i][j] = {AddRoundKey[i][j][NBW_BYTE-2:0], 1'b0};
                xtimes04[i][j] = {xtimes02[i][j][NBW_BYTE-2:0], 1'b0};
                xtimes08[i][j] = {xtimes04[i][j][NBW_BYTE-2:0], 1'b0};
            end

            xtimes0e[i][j] = xtimes08[i][j] ^ xtimes04[i][j] ^ xtimes02[i][j];
            xtimes0b[i][j] = xtimes08[i][j] ^ xtimes02[i][j] ^ AddRoundKey[i][j];
            xtimes0d[i][j] = xtimes08[i][j] ^ xtimes04[i][j] ^ AddRoundKey[i][j];
            xtimes09[i][j] = xtimes08[i][j] ^ AddRoundKey[i][j];
        end

        MixColumns[0][i] = xtimes0e[0][i] ^ xtimes0b[2][i] ^ xtimes0d[3][i] ^ xtimes09[0][i];
        MixColumns[1][i] = xtimes0e[1][i] ^ xtimes0b[3][i] ^ xtimes0d[0][i] ^ xtimes09[1][i];
        MixColumns[2][i] = xtimes0e[2][i] ^ xtimes0b[0][i] ^ xtimes0d[1][i] ^ xtimes09[2][i];
        MixColumns[3][i] = xtimes0e[3][i] ^ xtimes0b[1][i] ^ xtimes0d[2][i] ^ xtimes09[3][i];
    end

    // Shift Rows logic
    // Line 0: No shift
    ShiftRows[0][0] = MixColumns[0][0];
    ShiftRows[0][1] = MixColumns[0][1];
    ShiftRows[0][2] = MixColumns[0][2];
    ShiftRows[0][3] = MixColumns[0][3];

    // Line 1: Shift 1 right
    ShiftRows[1][0] = MixColumns[1][1];
    ShiftRows[1][1] = MixColumns[1][2];
    ShiftRows[1][2] = MixColumns[1][3];
    ShiftRows[1][3] = MixColumns[1][0];

    // Line 2: Shift 2 right
    ShiftRows[2][0] = MixColumns[2][2];
    ShiftRows[2][1] = MixColumns[2][3];
    ShiftRows[2][2] = MixColumns[2][0];
    ShiftRows[2][3] = MixColumns[2][1];

    // Line 3: Shift 3 right
    ShiftRows[3][0] = MixColumns[3][3];
    ShiftRows[3][1] = MixColumns[3][0];
    ShiftRows[3][2] = MixColumns[3][1];
    ShiftRows[3][3] = MixColumns[3][2];
end

aes128_key_expansion uu_aes128_key_expansion (
    .clk           (clk                            ),
    .rst_async_n   (rst_async_n                    ),
    .i_start       (i_start & i_update_key & o_done),
    .i_key         (i_key                          ),
    .o_done        (key_done                       ),
    .o_expanded_key(expanded_key                   )
);

endmodule : aes128_decrypt

context.rtl/aes128_key_expansion.sv:
module aes128_key_expansion #(
    parameter NBW_KEY = 'd128,
    parameter NBW_OUT = 'd1408
) (
    input  logic               clk,
    input  logic               rst_async_n,
    input  logic               i_start,
    input  logic [NBW_KEY-1:0] i_key,
    output logic               o_done,
    output logic [NBW_OUT-1:0] o_expanded_key
);

// ----------------------------------------
// - Parameters
// ----------------------------------------
localparam NBW_BYTE = 'd8;
localparam NBW_WORD = 'd32;
localparam STEPS    = 'd10;

// ----------------------------------------
// - Wires/registers creation
// ----------------------------------------
logic [NBW_BYTE-1:0] Rcon   [STEPS];
logic [NBW_OUT-1:0]  expanded_key_nx;
logic [NBW_OUT-1:0]  expanded_key_ff;
logic [NBW_KEY-1:0]  step_key[STEPS];
logic [NBW_KEY-1:0]  valid_key;
logic [STEPS:0]      key_exp_steps_ff;

// ----------------------------------------
// - Output assignment
// ----------------------------------------
assign o_expanded_key = expanded_key_ff;
assign o_done = key_exp_steps_ff[STEPS];

// ----------------------------------------
// - Registers
// ----------------------------------------
always_ff @(posedge clk or negedge rst_async_n) begin : reset_regs
    if(~rst_async_n) begin
        expanded_key_ff  <= {NBW_OUT{1'b0}};
        key_exp_steps_ff <= 0;
    end else begin
        expanded_key_ff <= expanded_key_nx;

        if(i_start) begin
            key_exp_steps_ff <= {{(STEPS-2){1'b0}}, 1'b1};
        end else begin
            if(key_exp_steps_ff[STEPS-1]) begin
                key_exp_steps_ff <= 0;
            end else begin
                key_exp_steps_ff <= key_exp_steps_ff << 1;
            end
        end
    end
end


// ----------------------------------------
// - Operation logic
// ----------------------------------------
assign Rcon[0] = 8'h01;
assign Rcon[1] = 8'h02;
assign Rcon[2] = 8'h04;
assign Rcon[3] = 8'h08;
assign Rcon[4] = 8'h10;
assign Rcon[5] = 8'h20;
assign Rcon[6] = 8'h40;
assign Rcon[7] = 8'h80;
assign Rcon[8] = 8'h1b;
assign Rcon[9] = 8'h36;

generate
    for(genvar i = 0; i < STEPS; i++) begin : steps
        logic [NBW_WORD-1:0] RotWord;
        logic [NBW_WORD-1:0] SubWord;
        logic [NBW_WORD-1:0] RconXor;

        sbox uu_sbox0 (
            .i_data(RotWord[NBW_WORD-1-:NBW_BYTE]),
            .o_data(SubWord[NBW_WORD-1-:NBW_BYTE])
        );

        sbox uu_sbox1 (
            .i_data(RotWord[NBW_WORD-NBW_BYTE-1-:NBW_BYTE]),
            .o_data(SubWord[NBW_WORD-NBW_BYTE-1-:NBW_BYTE])
        );

        sbox uu_sbox2 (
            .i_data(RotWord[NBW_WORD-2*NBW_BYTE-1-:NBW_BYTE]),
            .o_data(SubWord[NBW_WORD-2*NBW_BYTE-1-:NBW_BYTE])
        );

        sbox uu_sbox3 (
            .i_data(RotWord[NBW_WORD-3*NBW_BYTE-1-:NBW_BYTE]),
            .o_data(SubWord[NBW_WORD-3*NBW_BYTE-1-:NBW_BYTE])
        );

        always_comb begin : main_operation
            RotWord = {expanded_key_ff[NBW_OUT-(i+1)*NBW_KEY+NBW_WORD-NBW_BYTE-1-:(NBW_WORD-NBW_BYTE)], expanded_key_ff[NBW_OUT-(i+1)*NBW_KEY+NBW_WORD-1-:NBW_BYTE]};
            RconXor = {SubWord[NBW_WORD-1-:NBW_BYTE]^Rcon[i], SubWord[NBW_WORD-NBW_BYTE-1-:(NBW_WORD-NBW_BYTE)]};

            step_key[i][NBW_KEY-1-:NBW_WORD]            = expanded_key_ff[NBW_OUT-(4*i+3)*NBW_WORD-1-:NBW_WORD] ^ RconXor;
            step_key[i][NBW_KEY-NBW_WORD-1-:NBW_WORD]   = expanded_key_ff[NBW_OUT-(4*i+2)*NBW_WORD-1-:NBW_WORD] ^ step_key[i][NBW_KEY-1-:NBW_WORD];
            step_key[i][NBW_KEY-2*NBW_WORD-1-:NBW_WORD] = expanded_key_ff[NBW_OUT-(4*i+1)*NBW_WORD-1-:NBW_WORD] ^ step_key[i][NBW_KEY-NBW_WORD-1-:NBW_WORD];
            step_key[i][NBW_KEY-3*NBW_WORD-1-:NBW_WORD] = expanded_key_ff[NBW_OUT-(4*i  )*NBW_WORD-1-:NBW_WORD] ^ step_key[i][NBW_KEY-2*NBW_WORD-1-:NBW_WORD];
        end
    end
endgenerate

assign expanded_key_nx = {valid_key  , step_key[0], step_key[1], step_key[2],
                          step_key[3], step_key[4], step_key[5], step_key[6],
                          step_key[7], step_key[8], step_key[9]};

always_comb begin : input_data
    if (i_start) begin
        valid_key = i_key;
    end else begin
        valid_key = expanded_key_ff[NBW_OUT-1-:NBW_KEY];
    end
end

endmodule : aes128_key_expansion

