module tb_aes_decrypt;

localparam NBW_KEY  = 'd256;
localparam NBW_DATA = 'd128;

logic                clk;
logic                rst_async_n;
logic                i_update_key;
logic [NBW_KEY-1:0]  i_key;
logic                i_start;
logic [NBW_DATA-1:0] i_data;
logic                o_done;
logic [NBW_DATA-1:0] o_data;

aes_decrypt #(
    .NBW_KEY (NBW_KEY),
    .NBW_DATA(NBW_DATA)
) uu_aes_decrypt (
    .clk(clk),
    .rst_async_n(rst_async_n),
    .i_update_key(i_update_key),
    .i_key(i_key),
    .i_start(i_start),
    .i_data(i_data),
    .o_done(o_done),
    .o_data(o_data)
);

task Simple_test(logic update_key);
    @(negedge clk);
    i_key  = 256'h000102030405060708090a0b0c0d0e0f101112131415161718191a1b1c1d1e1f;
    i_data = 128'h8ea2b7ca516745bfeafc49904b496089;
    i_update_key = update_key;
    i_start = 1;

    @(negedge clk);
    i_start = 0;
    i_update_key = 0;
    i_key = 0;

    @(posedge o_done);
    @(negedge clk);

    if(o_data == 128'h00112233445566778899aabbccddeeff) begin
        $display("PASS");
    end else begin
        $display("FAIL");
        $display("Expected output: %h", 128'h00112233445566778899aabbccddeeff);
        $display("Observed output: %h", o_data);
    end
endtask

initial begin
    $dumpfile("test.vcd");
    $dumpvars(0,tb_aes_decrypt);
end

always #5 clk = ~clk;

initial begin
    clk = 0;
    i_start = 0;
    rst_async_n = 1;
    #1;
    rst_async_n = 0;
    #2;
    rst_async_n = 1;
    @(negedge clk);

    // Tasks go here
    Simple_test(1'b1);
    Simple_test(1'b0);

    @(negedge clk);
    @(negedge clk);

    $finish();
end

endmodule

context.rtl/aes_dec_top.sv:
module aes_dec_top #(
    parameter NBW_KEY  = 'd256,
    parameter NBW_DATA = 'd128,
    parameter NBW_MODE = 'd3,
    parameter NBW_CNTR = 'd32
) (
    input  logic                clk,
    input  logic                rst_async_n,
    input  logic                i_reset_counter,
    input  logic                i_update_iv,
    input  logic [NBW_DATA-1:0] i_iv,
    input  logic                i_update_mode,
    input  logic [NBW_MODE-1:0] i_mode,
    input  logic                i_update_key,
    input  logic [NBW_KEY-1:0]  i_key,
    input  logic                i_start,
    input  logic [NBW_DATA-1:0] i_ciphertext,
    output logic                o_done,
    output logic [NBW_DATA-1:0] o_plaintext
);

// ----------------------------------------
// - Wires/Registers creation
// ----------------------------------------
logic [NBW_MODE-1:0] mode_ff;
logic [NBW_DATA-1:0] ciphertext_ff;
logic [NBW_DATA-1:0] iv_ff;
logic [NBW_DATA-1:0] iv_nx;
logic [NBW_DATA-1:0] plaintext;
logic [NBW_DATA-1:0] dec_in;
logic [NBW_DATA-1:0] dec_out;
logic                update_key_ff;
logic                start_dec_ff;
logic                start_enc_ff;
logic                dec_done;
logic [NBW_KEY-1:0]  key_ff;
logic [NBW_CNTR-1:0] counter_ff;
logic                dec_sel;
logic [NBW_DATA-1:0] enc_out;
logic                enc_done;

// Possible operation modes
localparam ECB  = 3'd0;
localparam CBC  = 3'd1;
localparam PCBC = 3'd2;
localparam CFB  = 3'd3;
localparam OFB  = 3'd4;
localparam CTR  = 3'd5;

// Operation modes logic
always_comb begin
    case(mode_ff)
        ECB: begin
            dec_in    = ciphertext_ff;
            iv_nx     = iv_ff;
            plaintext = dec_out;
            dec_sel   = 1'b1;
        end
        CBC: begin
            dec_in    = ciphertext_ff;
            iv_nx     = ciphertext_ff;
            plaintext = dec_out ^ iv_ff;
            dec_sel   = 1'b1;
        end
        PCBC: begin
            dec_in    = ciphertext_ff;
            iv_nx     = ciphertext_ff ^ dec_out ^ iv_ff;
            plaintext = dec_out ^ iv_ff;
            dec_sel   = 1'b1;
        end
        CFB: begin
            dec_in    = iv_ff;
            iv_nx     = ciphertext_ff;
            plaintext = ciphertext_ff ^ enc_out;
            dec_sel   = 1'b0;
        end
        OFB: begin
            dec_in    = iv_ff;
            iv_nx     = enc_out;
            plaintext = ciphertext_ff ^ enc_out;
            dec_sel   = 1'b0;
        end
        CTR: begin
            dec_in    = {iv_ff[NBW_DATA-1:NBW_CNTR], counter_ff};
            iv_nx     = iv_ff;
            plaintext = ciphertext_ff ^ enc_out;
            dec_sel   = 1'b0;
        end
        default: begin
            dec_in    = ciphertext_ff;
            iv_nx     = iv_ff;
            plaintext = dec_out;
            dec_sel   = 1'b1;
        end
    endcase
end

always_ff @ (posedge clk) begin : data_regs
    if(i_start & o_done) begin
        ciphertext_ff <= i_ciphertext;
    end
end

always_ff @ (posedge clk or negedge rst_async_n) begin : reset_regs
    if(!rst_async_n) begin
        iv_ff        <= 128'd0;
        mode_ff      <= 3'd0;
        o_done       <= 1'b1;
        o_plaintext  <= 128'd0;
        counter_ff   <= 0;
        start_enc_ff <= 1'b0;
        start_dec_ff <= 1'b0;
    end else begin
        if(i_update_iv) begin
            iv_ff <= i_iv;
        end else begin
            if(dec_done | enc_done) begin
                iv_ff <= iv_nx;
            end
        end

        if(i_update_mode) begin
            mode_ff <= i_mode;
        end

        if(dec_done | enc_done) begin
            o_done <= 1'b1;
        end else begin
            if(i_start & o_done) begin
                o_done <= 1'b0;
            end
        end

        if(dec_done | enc_done) begin
            o_plaintext <= plaintext;
        end

        if(i_reset_counter) begin
            counter_ff <= 0;
        end else if((dec_done | enc_done) & mode_ff == CTR) begin
            counter_ff <= counter_ff + 1'b1;
        end

        start_enc_ff  <= (i_start & o_done & (~dec_sel));
        start_dec_ff  <= (i_start & o_done & dec_sel);
        update_key_ff <= (i_start & i_update_key & o_done);
        if(i_start & i_update_key & o_done) begin
            key_ff <= i_key;
        end
    end
end

aes_decrypt #(
    .NBW_KEY (NBW_KEY ),
    .NBW_DATA(NBW_DATA)
) uu_aes256_decrypt (
    .clk         (clk          ),
    .rst_async_n (rst_async_n  ),
    .i_update_key(update_key_ff),
    .i_key       (key_ff       ),
    .i_start     (start_dec_ff ),
    .i_data      (dec_in       ),
    .o_done      (dec_done     ),
    .o_data      (dec_out      )
);

aes_encrypt #(
    .NBW_KEY (NBW_KEY ),
    .NBW_DATA(NBW_DATA)
) uu_aes_encrypt (
    .clk         (clk          ),
    .rst_async_n (rst_async_n  ),
    .i_update_key(update_key_ff),
    .i_key       (key_ff       ),
    .i_start     (start_enc_ff ),
    .i_data      (dec_in       ),
    .o_data      (enc_out      ),
    .o_done      (enc_done     )
);

endmodule : aes_dec_top

context.rtl/aes_decrypt.sv:
module aes_decrypt #(
    parameter NBW_KEY  = 'd256,
    parameter NBW_DATA = 'd128
) (
    input  logic                clk,
    input  logic                rst_async_n,
    input  logic                i_update_key,
    input  logic [NBW_KEY-1:0]  i_key,
    input  logic                i_start,
    input  logic [NBW_DATA-1:0] i_data,
    output logic                o_done,
    output logic [NBW_DATA-1:0] o_data
);

// ----------------------------------------
// - Internal Parameters
// ----------------------------------------
localparam NBW_BYTE   = 'd8;
localparam NBW_EX_KEY = 'd1920;
localparam NBW_STEP   = NBW_KEY/2;

// ----------------------------------------
// - Wires/Registers creation
// ----------------------------------------
logic [NBW_BYTE-1:0]   current_data_nx[4][4];
logic [NBW_BYTE-1:0]   current_data_ff[4][4];
logic [NBW_BYTE-1:0]   AddRoundKey[4][4];
logic [NBW_BYTE-1:0]   SubBytes[4][4];
logic [NBW_BYTE-1:0]   ShiftRows[4][4];
logic [NBW_BYTE-1:0]   xtimes02[4][4];
logic [NBW_BYTE-1:0]   xtimes04[4][4];
logic [NBW_BYTE-1:0]   xtimes08[4][4];
logic [NBW_BYTE-1:0]   xtimes09[4][4];
logic [NBW_BYTE-1:0]   xtimes0b[4][4];
logic [NBW_BYTE-1:0]   xtimes0d[4][4];
logic [NBW_BYTE-1:0]   xtimes0e[4][4];
logic [NBW_BYTE-1:0]   MixColumns[4][4];
logic [3:0]            round_ff;
logic                  key_done;
logic                  key_idle;
logic [NBW_EX_KEY-1:0] expanded_key;

// ----------------------------------------
// - Output assignment
// ----------------------------------------
always_ff @ (posedge clk or negedge rst_async_n) begin
    if(!rst_async_n) begin
        o_done <= 1'b0;
    end else begin
        o_done <= (round_ff == 4'd15);
    end
end

generate
    for(genvar i = 0; i < 4; i++) begin : out_row
        for(genvar j = 0; j < 4; j++) begin : out_col
            assign o_data[NBW_DATA-(4*j+i)*NBW_BYTE-1-:NBW_BYTE] = current_data_ff[i][j];
        end
    end
endgenerate

always_ff @(posedge clk or negedge rst_async_n) begin : inv_cypher_regs
    if(!rst_async_n) begin
        round_ff <= 4'd0;
        for(int i = 0; i < 4; i++) begin
            for(int j = 0; j < 4; j++) begin
                current_data_ff[i][j] <= 8'd0;
            end
        end
    end else begin
        if(i_start) begin
            if(i_update_key) begin
                round_ff <= 4'd0;
            end else begin
                round_ff <= 4'd1;
            end
        end else if((round_ff > 4'd0 && round_ff < 4'd15) || key_done) begin
            round_ff <= round_ff + 1'b1;
        end else begin
            round_ff <= 4'd0;
        end

        for(int i = 0; i < 4; i++) begin
            for(int j = 0; j < 4; j++) begin
                current_data_ff[i][j] <= current_data_nx[i][j];
            end
        end
    end
end

always_comb begin : next_data
    for(int i = 0; i < 4; i++) begin
        for(int j = 0; j < 4; j++) begin
            if(i_start) begin
                current_data_nx[i][j] = i_data[NBW_DATA-(4*j+i)*NBW_BYTE-1-:NBW_BYTE];
            end else begin
                if(round_ff != 0) begin
                    if(round_ff != 15) begin
                        current_data_nx[i][j] = SubBytes[i][j];
                    end else begin
                        current_data_nx[i][j] = AddRoundKey[i][j];
                    end
                end else begin
                    current_data_nx[i][j] = current_data_ff[i][j];
                end
            end
        end
    end
end

generate
    for(genvar i = 0; i < 4; i++) begin : row
        for(genvar j = 0; j < 4; j++) begin : col
            inv_sbox uu_inv_sbox0 (
                .i_data(ShiftRows[i][j]),
                .o_data(SubBytes[i][j])
            );
        end
    end
endgenerate

always_comb begin : decypher_logic
    // Add Round Key logic
    for(int i = 0; i < 4; i++) begin : row_key
        for(int j = 0; j < 4; j++) begin : col_key
            if(round_ff > 4'd0) begin
                AddRoundKey[i][j] = current_data_ff[i][j] ^ expanded_key[NBW_EX_KEY-(15-round_ff)*NBW_STEP-(4*j+i)*NBW_BYTE-1-:NBW_BYTE];
            end else begin
                AddRoundKey[i][j] = 0;
            end
        end
    end

    // Mix Columns logic
    for(int i = 0; i < 4; i++) begin
        for(int j = 0; j < 4; j++) begin
            if(AddRoundKey[i][j][NBW_BYTE-1]) begin
                xtimes02[i][j] = {AddRoundKey[i][j][NBW_BYTE-2:0], 1'b0} ^ 8'h1B;
                if(AddRoundKey[i][j][NBW_BYTE-2]) begin
                    xtimes04[i][j] = {xtimes02[i][j][NBW_BYTE-2:0], 1'b0} ^ 8'h1B;
                    if(AddRoundKey[i][j][NBW_BYTE-3]) begin
                        xtimes08[i][j] = {xtimes04[i][j][NBW_BYTE-2:0], 1'b0} ^ 8'h1B;
                    end else begin
                        xtimes08[i][j] = {xtimes04[i][j][NBW_BYTE-2:0], 1'b0};
                    end
                end else begin
                    xtimes04[i][j] = {xtimes02[i][j][NBW_BYTE-2:0], 1'b0};
                    if(AddRoundKey[i][j][NBW_BYTE-3]) begin
                        xtimes08[i][j] = {xtimes04[i][j][NBW_BYTE-2:0], 1'b0} ^ 8'h1B;
                    end else begin
                        xtimes08[i][j] = {xtimes04[i][j][NBW_BYTE-2:0], 1'b0};
                    end
                end
            end else begin
                xtimes02[i][j] = {AddRoundKey[i][j][NBW_BYTE-2:0], 1'b0};
                if(AddRoundKey[i][j][NBW_BYTE-2]) begin
                    xtimes04[i][j] = {xtimes02[i][j][NBW_BYTE-2:0], 1'b0} ^ 8'h1B;
                    if(AddRoundKey[i][j][NBW_BYTE-3]) begin
                        xtimes08[i][j] = {xtimes04[i][j][NBW_BYTE-2:0], 1'b0} ^ 8'h1B;
                    end else begin
                        xtimes08[i][j] = {xtimes04[i][j][NBW_BYTE-2:0], 1'b0};
                    end
                end else begin
                    xtimes04[i][j] = {xtimes02[i][j][NBW_BYTE-2:0], 1'b0};
                    if(AddRoundKey[i][j][NBW_BYTE-3]) begin
                        xtimes08[i][j] = {xtimes04[i][j][NBW_BYTE-2:0], 1'b0} ^ 8'h1B;
                    end else begin
                        xtimes08[i][j] = {xtimes04[i][j][NBW_BYTE-2:0], 1'b0};
                    end
                end
            end

            xtimes0e[i][j] = xtimes08[i][j] ^ xtimes04[i][j] ^ xtimes02[i][j];
            xtimes0b[i][j] = xtimes08[i][j] ^ xtimes02[i][j] ^ AddRoundKey[i][j];
            xtimes0d[i][j] = xtimes08[i][j] ^ xtimes04[i][j] ^ AddRoundKey[i][j];
            xtimes09[i][j] = xtimes08[i][j] ^ AddRoundKey[i][j];
        end
    end

    for(int i = 0; i < 4; i++) begin
        MixColumns[0][i] = xtimes0e[0][i] ^ xtimes0b[1][i] ^ xtimes0d[2][i] ^ xtimes09[3][i];
        MixColumns[1][i] = xtimes0e[1][i] ^ xtimes0b[2][i] ^ xtimes0d[3][i] ^ xtimes09[0][i];
        MixColumns[2][i] = xtimes0e[2][i] ^ xtimes0b[3][i] ^ xtimes0d[0][i] ^ xtimes09[1][i];
        MixColumns[3][i] = xtimes0e[3][i] ^ xtimes0b[0][i] ^ xtimes0d[1][i] ^ xtimes09[2][i];
    end

    // Shift Rows logic
    if(round_ff == 4'd1) begin
        // Line 0: No shift
        ShiftRows[0][0] = AddRoundKey[0][0];
        ShiftRows[0][1] = AddRoundKey[0][1];
        ShiftRows[0][2] = AddRoundKey[0][2];
        ShiftRows[0][3] = AddRoundKey[0][3];

        // Line 1: Shift 1 right
        ShiftRows[1][0] = AddRoundKey[1][3];
        ShiftRows[1][1] = AddRoundKey[1][0];
        ShiftRows[1][2] = AddRoundKey[1][1];
        ShiftRows[1][3] = AddRoundKey[1][2];

        // Line 2: Shift 2 right
        ShiftRows[2][0] = AddRoundKey[2][2];
        ShiftRows[2][1] = AddRoundKey[2][3];
        ShiftRows[2][2] = AddRoundKey[2][0];
        ShiftRows[2][3] = AddRoundKey[2][1];

        // Line 3: Shift 3 right
        ShiftRows[3][0] = AddRoundKey[3][1];
        ShiftRows[3][1] = AddRoundKey[3][2];
        ShiftRows[3][2] = AddRoundKey[3][3];
        ShiftRows[3][3] = AddRoundKey[3][0];
    end else begin
        // Line 0: No shift
        ShiftRows[0][0] = MixColumns[0][0];
        ShiftRows[0][1] = MixColumns[0][1];
        ShiftRows[0][2] = MixColumns[0][2];
        ShiftRows[0][3] = MixColumns[0][3];

        // Line 1: Shift 1 right
        ShiftRows[1][0] = MixColumns[1][3];
        ShiftRows[1][1] = MixColumns[1][0];
        ShiftRows[1][2] = MixColumns[1][1];
        ShiftRows[1][3] = MixColumns[1][2];

        // Line 2: Shift 2 right
        ShiftRows[2][0] = MixColumns[2][2];
        ShiftRows[2][1] = MixColumns[2][3];
        ShiftRows[2][2] = MixColumns[2][0];
        ShiftRows[2][3] = MixColumns[2][1];

        // Line 3: Shift 3 right
        ShiftRows[3][0] = MixColumns[3][1];
        ShiftRows[3][1] = MixColumns[3][2];
        ShiftRows[3][2] = MixColumns[3][3];
        ShiftRows[3][3] = MixColumns[3][0];
    end

end

aes_ke uu_aes_ke (
    .clk           (clk                   ),
    .rst_async_n   (rst_async_n           ),
    .i_start       (i_start & i_update_key),
    .i_key         (i_key                 ),
    .o_idle        (key_idle              ),
    .o_done        (key_done              ),
    .o_expanded_key(expanded_key          )
);

endmodule : aes_decrypt

context.rtl/aes_enc_top.sv:
module aes_enc_top #(
    parameter NBW_KEY  = 'd256,
    parameter NBW_DATA = 'd128,
    parameter NBW_MODE = 'd3,
    parameter NBW_CNTR = 'd32
) (
    input  logic                clk,
    input  logic                rst_async_n,
    input  logic                i_reset_counter,
    input  logic                i_update_iv,
    input  logic [NBW_DATA-1:0] i_iv,
    input  logic                i_update_mode,
    input  logic [NBW_MODE-1:0] i_mode,
    input  logic                i_update_key,
    input  logic [NBW_KEY-1:0]  i_key,
    input  logic                i_start,
    input  logic [NBW_DATA-1:0] i_plaintext,
    output logic                o_done,
    output logic [NBW_DATA-1:0] o_ciphertext
);

// ----------------------------------------
// - Wires/Registers creation
// ----------------------------------------
logic [NBW_MODE-1:0] mode_ff;
logic [NBW_DATA-1:0] plaintext_ff;
logic [NBW_DATA-1:0] iv_ff;
logic [NBW_DATA-1:0] iv_nx;
logic [NBW_DATA-1:0] ciphertext;
logic [NBW_DATA-1:0] enc_in;
logic [NBW_DATA-1:0] enc_out;
logic                update_key_ff;
logic                start_ff;
logic                enc_done;
logic [NBW_KEY-1:0]  key_ff;
logic [NBW_CNTR-1:0] counter_ff;

// Possible operation modes
localparam ECB  = 3'd0;
localparam CBC  = 3'd1;
localparam PCBC = 3'd2;
localparam CFB  = 3'd3;
localparam OFB  = 3'd4;
localparam CTR  = 3'd5;

// Operation modes logic
always_comb begin
    case(mode_ff)
        ECB: begin
            enc_in     = plaintext_ff;
            iv_nx      = iv_ff;
            ciphertext = enc_out;
        end
        CBC: begin
            enc_in     = plaintext_ff ^ iv_ff;
            iv_nx      = enc_out;
            ciphertext = enc_out;
        end
        PCBC: begin
            enc_in     = plaintext_ff ^ iv_ff;
            iv_nx      = plaintext_ff ^ enc_out;
            ciphertext = enc_out;
        end
        CFB: begin
            enc_in     = iv_ff;
            iv_nx      = plaintext_ff ^ enc_out;
            ciphertext = plaintext_ff ^ enc_out;
        end
        OFB: begin
            enc_in     = iv_ff;
            iv_nx      = enc_out;
            ciphertext = plaintext_ff ^ enc_out;
        end
        CTR: begin
            enc_in     = {iv_ff[NBW_DATA-1:NBW_CNTR], counter_ff};
            iv_nx      = iv_ff;
            ciphertext = plaintext_ff ^ enc_out;
        end
        default: begin
            enc_in     = plaintext_ff;
            iv_nx      = iv_ff;
            ciphertext = enc_out;
        end
    endcase
end

// Registers
always_ff @ (posedge clk) begin : data_regs
    if(i_start & o_done) begin
        plaintext_ff <= i_plaintext;
    end
end

always_ff @ (posedge clk or negedge rst_async_n) begin : reset_regs
    if(!rst_async_n) begin
        iv_ff        <= 128'd0;
        mode_ff      <= 3'd0;
        o_done       <= 1'b1;
        o_ciphertext <= 128'd0;
        counter_ff   <= 0;
    end else begin
        if(i_update_iv) begin
            iv_ff <= i_iv;
        end else begin
            if(enc_done) begin
                iv_ff <= iv_nx;
            end
        end

        if(i_update_mode) begin
            mode_ff <= i_mode;
        end

        if(enc_done) begin
            o_done <= 1'b1;
        end else begin
            if(i_start & o_done) begin
                o_done <= 1'b0;
            end
        end

        if(enc_done) begin
            o_ciphertext <= ciphertext;
        end

        if(i_reset_counter) begin
            counter_ff <= 0;
        end else if(enc_done & mode_ff == CTR) begin
            counter_ff <= counter_ff + 1'b1;
        end

        start_ff <= (i_start & o_done);
        update_key_ff <= (i_start & i_update_key & o_done);
        if(i_start & i_update_key & o_done) begin
            key_ff <= i_key;
        end
    end
end

// Encryption module instantiation
aes_encrypt #(
    .NBW_KEY (NBW_KEY ),
    .NBW_DATA(NBW_DATA)
) uu_aes_encrypt (
    .clk         (clk          ),
    .rst_async_n (rst_async_n  ),
    .i_update_key(update_key_ff),
    .i_key       (key_ff       ),
    .i_start     (start_ff     ),
    .i_data      (enc_in       ),
    .o_done      (enc_done     ),
    .o_data      (enc_out      )
);

endmodule : aes_enc_top

context.rtl/aes_encrypt.sv:
module aes_encrypt #(
    parameter NBW_KEY  = 'd256,
    parameter NBW_DATA = 'd128
) (
    input  logic                clk,
    input  logic                rst_async_n,
    input  logic                i_update_key,
    input  logic [NBW_KEY-1:0]  i_key,
    input  logic                i_start,
    input  logic [NBW_DATA-1:0] i_data,
    output logic                o_done,
    output logic [NBW_DATA-1:0] o_data
);

// ----------------------------------------
// - Internal Parameters
// ----------------------------------------
localparam NBW_BYTE   = 'd8;
localparam STEPS      = 'd14;
localparam NBW_WORD   = 'd32;
localparam NBW_EX_KEY = 'd1920;
localparam NBW_STEP   = NBW_KEY/2;

// ----------------------------------------
// - Wires/Registers creation
// ----------------------------------------
logic [NBW_BYTE-1:0]   Rcon   [STEPS/2];
logic [NBW_KEY-1:0]    valid_key;
logic [NBW_STEP-1:0]   step_key[STEPS];
logic [NBW_EX_KEY-1:0] expanded_key_nx;
logic [NBW_EX_KEY-1:0] expanded_key_ff;
logic [NBW_BYTE-1:0]   current_data_nx[4][4];
logic [NBW_BYTE-1:0]   current_data_ff[4][4];
logic [NBW_BYTE-1:0]   SubBytes[4][4];
logic [NBW_BYTE-1:0]   ShiftRows[4][4];
logic [NBW_BYTE-1:0]   xtimes02[4][4];
logic [NBW_BYTE-1:0]   xtimes03[4][4];
logic [NBW_BYTE-1:0]   MixColumns[4][4];
logic [3:0] round_ff;

// ----------------------------------------
// - Output assignment
// ----------------------------------------
always_ff @ (posedge clk or negedge rst_async_n) begin : done_assignment
    if(!rst_async_n) begin
        o_done <= 1'b0;
    end else begin
        o_done <= (round_ff == 4'd14);
    end
end

generate
    for(genvar i = 0; i < 4; i++) begin : out_row
        for(genvar j = 0; j < 4; j++) begin : out_col
            assign o_data[NBW_DATA-(4*j+i)*NBW_BYTE-1-:NBW_BYTE] = current_data_ff[i][j];
        end
    end
endgenerate

always_ff @(posedge clk or negedge rst_async_n) begin : cypher_regs
    if(!rst_async_n) begin
        round_ff <= 4'd0;
        for(int i = 0; i < 4; i++) begin
            for(int j = 0; j < 4; j++) begin
                current_data_ff[i][j] <= 8'd0;
            end
        end
    end else begin
        if(i_start || (round_ff > 4'd0 && round_ff < 4'd14)) begin
            round_ff <= round_ff + 1'b1;
        end else begin
            round_ff <= 4'd0;
        end

        for(int i = 0; i < 4; i++) begin
            for(int j = 0; j < 4; j++) begin
                current_data_ff[i][j] <= current_data_nx[i][j];
            end
        end
    end
end

always_comb begin : next_data
    for(int i = 0; i < 4; i++) begin
        for(int j = 0; j < 4; j++) begin
            if(i_start) begin
                if(i_update_key) begin
                    current_data_nx[i][j] = i_data[NBW_DATA-(4*j+i)*NBW_BYTE-1-:NBW_BYTE] ^ i_key[NBW_KEY-(4*j+i)*NBW_BYTE-1-:NBW_BYTE];
                end else begin
                    current_data_nx[i][j] = i_data[NBW_DATA-(4*j+i)*NBW_BYTE-1-:NBW_BYTE] ^ expanded_key_ff[NBW_EX_KEY-(4*j+i)*NBW_BYTE-1-:NBW_BYTE];
                end
            end else begin
                if(round_ff != 4'd0) begin
                    if(round_ff != 4'd14) begin
                        current_data_nx[i][j] = MixColumns[i][j] ^ expanded_key_ff[NBW_EX_KEY-round_ff*NBW_STEP-(4*j+i)*NBW_BYTE-1-:NBW_BYTE];
                    end else begin
                        current_data_nx[i][j] = ShiftRows[i][j] ^ expanded_key_ff[NBW_EX_KEY-round_ff*NBW_STEP-(4*j+i)*NBW_BYTE-1-:NBW_BYTE];
                    end
                end else begin
                    current_data_nx[i][j] = current_data_ff[i][j];
                end
            end
        end
    end
end

generate
    for(genvar i = 0; i < 4; i++) begin : row
        for(genvar j = 0; j < 4; j++) begin : col
            sbox uu_sbox0 (
                .i_data(current_data_ff[i][j]),
                .o_data(SubBytes[i][j])
            );
        end
    end
endgenerate

always_comb begin : cypher_logic
    // Shift Rows logic
    // Line 0: No shift
    ShiftRows[0][0] = SubBytes[0][0];
    ShiftRows[0][1] = SubBytes[0][1];
    ShiftRows[0][2] = SubBytes[0][2];
    ShiftRows[0][3] = SubBytes[0][3];

    // Line 1: Shift 1 left
    ShiftRows[1][0] = SubBytes[1][1];
    ShiftRows[1][1] = SubBytes[1][2];
    ShiftRows[1][2] = SubBytes[1][3];
    ShiftRows[1][3] = SubBytes[1][0];

    // Line 2: Shift 2 left
    ShiftRows[2][0] = SubBytes[2][2];
    ShiftRows[2][1] = SubBytes[2][3];
    ShiftRows[2][2] = SubBytes[2][0];
    ShiftRows[2][3] = SubBytes[2][1];

    // Line 3: Shift 3 left
    ShiftRows[3][0] = SubBytes[3][3];
    ShiftRows[3][1] = SubBytes[3][0];
    ShiftRows[3][2] = SubBytes[3][1];
    ShiftRows[3][3] = SubBytes[3][2];

    // Mix Columns logic
    for(int i = 0; i < 4; i++) begin
        for(int j = 0; j < 4; j++) begin
            if(ShiftRows[i][j][NBW_BYTE-1]) begin
                xtimes02[i][j] = {ShiftRows[i][j][NBW_BYTE-2:0], 1'b0} ^ 8'h1B;
                xtimes03[i][j] = {ShiftRows[i][j][NBW_BYTE-2:0], 1'b0} ^ 8'h1B ^ ShiftRows[i][j];
            end else begin
                xtimes02[i][j] = {ShiftRows[i][j][NBW_BYTE-2:0], 1'b0};
                xtimes03[i][j] = {ShiftRows[i][j][NBW_BYTE-2:0], 1'b0} ^ ShiftRows[i][j];
            end
        end
    end

    for(int i = 0; i < 4; i++) begin
        MixColumns[0][i] = xtimes02[0][i] ^ xtimes03[1][i] ^ ShiftRows[2][i] ^ ShiftRows[3][i];
        MixColumns[1][i] = xtimes02[1][i] ^ xtimes03[2][i] ^ ShiftRows[3][i] ^ ShiftRows[0][i];
        MixColumns[2][i] = xtimes02[2][i] ^ xtimes03[3][i] ^ ShiftRows[0][i] ^ ShiftRows[1][i];
        MixColumns[3][i] = xtimes02[3][i] ^ xtimes03[0][i] ^ ShiftRows[1][i] ^ ShiftRows[2][i];
    end
end

// ****************************************
// - Key Expansion logic
// ****************************************

// ----------------------------------------
// - Registers
// ----------------------------------------
always_ff @(posedge clk or negedge rst_async_n) begin : reset_regs
    if(~rst_async_n) begin
        expanded_key_ff <= {NBW_EX_KEY{1'b0}};
    end else begin
        expanded_key_ff <= expanded_key_nx;
    end
end


// ----------------------------------------
// - Operation logic
// ----------------------------------------
assign Rcon[0] = 8'h01;
assign Rcon[1] = 8'h02;
assign Rcon[2] = 8'h04;
assign Rcon[3] = 8'h08;
assign Rcon[4] = 8'h10;
assign Rcon[5] = 8'h20;
assign Rcon[6] = 8'h40;

generate
    for(genvar i = 0; i < STEPS; i++) begin : steps
        if(i%2 == 0) begin
            logic [NBW_WORD-1:0] RotWord;
            logic [NBW_WORD-1:0] SubWord;
            logic [NBW_WORD-1:0] RconXor;

            sbox uu_sbox0 (
                .i_data(RotWord[NBW_WORD-1-:NBW_BYTE]),
                .o_data(SubWord[NBW_WORD-1-:NBW_BYTE])
            );

            sbox uu_sbox1 (
                .i_data(RotWord[NBW_WORD-NBW_BYTE-1-:NBW_BYTE]),
                .o_data(SubWord[NBW_WORD-NBW_BYTE-1-:NBW_BYTE])
            );

            sbox uu_sbox2 (
                .i_data(RotWord[NBW_WORD-2*NBW_BYTE-1-:NBW_BYTE]),
                .o_data(SubWord[NBW_WORD-2*NBW_BYTE-1-:NBW_BYTE])
            );

            sbox uu_sbox3 (
                .i_data(RotWord[NBW_WORD-3*NBW_BYTE-1-:NBW_BYTE]),
                .o_data(SubWord[NBW_WORD-3*NBW_BYTE-1-:NBW_BYTE])
            );

            always_comb begin : main_operation
                RotWord = {expanded_key_ff[NBW_EX_KEY-NBW_KEY-i*NBW_STEP+NBW_WORD-NBW_BYTE-1-:(NBW_WORD-NBW_BYTE)], expanded_key_ff[NBW_EX_KEY-NBW_KEY-i*NBW_STEP+NBW_WORD-1-:NBW_BYTE]};
                RconXor = {SubWord[NBW_WORD-1-:NBW_BYTE]^Rcon[i/2], SubWord[NBW_WORD-NBW_BYTE-1-:(NBW_WORD-NBW_BYTE)]};

                step_key[i][NBW_STEP-1-:NBW_WORD]            = expanded_key_ff[NBW_EX_KEY-(4*i  )*NBW_WORD-1-:NBW_WORD] ^ RconXor;
                step_key[i][NBW_STEP-NBW_WORD-1-:NBW_WORD]   = expanded_key_ff[NBW_EX_KEY-(4*i+1)*NBW_WORD-1-:NBW_WORD] ^ step_key[i][NBW_STEP-1-:NBW_WORD];
                step_key[i][NBW_STEP-2*NBW_WORD-1-:NBW_WORD] = expanded_key_ff[NBW_EX_KEY-(4*i+2)*NBW_WORD-1-:NBW_WORD] ^ step_key[i][NBW_STEP-NBW_WORD-1-:NBW_WORD];
                step_key[i][NBW_STEP-3*NBW_WORD-1-:NBW_WORD] = expanded_key_ff[NBW_EX_KEY-(4*i+3)*NBW_WORD-1-:NBW_WORD] ^ step_key[i][NBW_STEP-2*NBW_WORD-1-:NBW_WORD];
            end
        end else begin
            logic [NBW_WORD-1:0] SubWord;

            sbox uu_sbox0 (
                .i_data(expanded_key_ff[NBW_EX_KEY-NBW_KEY+NBW_WORD-i*NBW_STEP-1-:NBW_BYTE]),
                .o_data(SubWord[NBW_WORD-1-:NBW_BYTE])
            );

            sbox uu_sbox1 (
                .i_data(expanded_key_ff[NBW_EX_KEY-NBW_KEY+NBW_WORD-i*NBW_STEP-NBW_BYTE-1-:NBW_BYTE]),
                .o_data(SubWord[NBW_WORD-NBW_BYTE-1-:NBW_BYTE])
            );

            sbox uu_sbox2 (
                .i_data(expanded_key_ff[NBW_EX_KEY-NBW_KEY+NBW_WORD-i*NBW_STEP-2*NBW_BYTE-1-:NBW_BYTE]),
                .o_data(SubWord[NBW_WORD-2*NBW_BYTE-1-:NBW_BYTE])
            );

            sbox uu_sbox3 (
                .i_data(expanded_key_ff[NBW_EX_KEY-NBW_KEY+NBW_WORD-i*NBW_STEP-3*NBW_BYTE-1-:NBW_BYTE]),
                .o_data(SubWord[NBW_WORD-3*NBW_BYTE-1-:NBW_BYTE])
            );

            always_comb begin : main_operation
                step_key[i][NBW_STEP-1-:NBW_WORD]            = expanded_key_ff[NBW_EX_KEY-(4*i  )*NBW_WORD-1-:NBW_WORD] ^ SubWord;
                step_key[i][NBW_STEP-NBW_WORD-1-:NBW_WORD]   = expanded_key_ff[NBW_EX_KEY-(4*i+1)*NBW_WORD-1-:NBW_WORD] ^ step_key[i][NBW_STEP-1-:NBW_WORD];
                step_key[i][NBW_STEP-2*NBW_WORD-1-:NBW_WORD] = expanded_key_ff[NBW_EX_KEY-(4*i+2)*NBW_WORD-1-:NBW_WORD] ^ step_key[i][NBW_STEP-NBW_WORD-1-:NBW_WORD];
                step_key[i][NBW_STEP-3*NBW_WORD-1-:NBW_WORD] = expanded_key_ff[NBW_EX_KEY-(4*i+3)*NBW_WORD-1-:NBW_WORD] ^ step_key[i][NBW_STEP-2*NBW_WORD-1-:NBW_WORD];
            end
        end
    end
endgenerate

assign expanded_key_nx = {valid_key   , step_key[0 ], step_key[1 ], step_key[2 ],
                          step_key[3 ], step_key[4 ], step_key[5 ], step_key[6 ],
                          step_key[7 ], step_key[8 ], step_key[9 ], step_key[10],
                          step_key[11], step_key[12]};

always_comb begin : input_data
    if (i_update_key) begin
        valid_key = i_key;
    end else begin
        valid_key = expanded_key_ff[NBW_EX_KEY-1-:NBW_KEY];
    end
end

endmodule : aes_encrypt

context.rtl/aes_ke.sv:
module aes_ke #(
    parameter NBW_KEY = 'd256,
    parameter NBW_OUT = 'd1920
) (
    input  logic               clk,
    input  logic               rst_async_n,
    input  logic               i_start,
    input  logic [NBW_KEY-1:0] i_key,
    output logic               o_idle,
    output logic               o_done,
    output logic [NBW_OUT-1:0] o_expanded_key
);

// ----------------------------------------
// - Internal Parameters
// ----------------------------------------
localparam NBW_BYTE = 'd8;
localparam STEPS    = 'd14;
localparam NBW_WORD = 'd32;
localparam NBW_STEP = NBW_KEY/2;

// ----------------------------------------
// - Wires/Registers creation
// ----------------------------------------
logic [NBW_BYTE-1:0]   Rcon   [STEPS/2];
logic [NBW_KEY-1:0]    valid_key;
logic [NBW_STEP-1:0] step_key[STEPS];
logic [NBW_OUT-1:0] expanded_key_nx;
logic [NBW_OUT-1:0] expanded_key_ff;
logic [STEPS:0] key_exp_steps_ff;

// ----------------------------------------
// - Output assignment
// ----------------------------------------
assign o_done = key_exp_steps_ff[STEPS];
assign o_idle = ~(|key_exp_steps_ff);
assign o_expanded_key = expanded_key_ff;

// ----------------------------------------
// - Registers
// ----------------------------------------
always_ff @(posedge clk or negedge rst_async_n) begin : done_regs
    if(!rst_async_n) begin
        key_exp_steps_ff <= 0;
    end else begin
        key_exp_steps_ff <= {key_exp_steps_ff[STEPS-1:0], i_start};
    end
end

always_ff @(posedge clk or negedge rst_async_n) begin : key_regs
    if(~rst_async_n) begin
        expanded_key_ff <= {NBW_OUT{1'b0}};
    end else begin
        expanded_key_ff <= expanded_key_nx;
    end
end


// ----------------------------------------
// - Operation logic
// ----------------------------------------
assign Rcon[0] = 8'h01;
assign Rcon[1] = 8'h02;
assign Rcon[2] = 8'h04;
assign Rcon[3] = 8'h08;
assign Rcon[4] = 8'h10;
assign Rcon[5] = 8'h20;
assign Rcon[6] = 8'h40;

generate
    for(genvar i = 0; i < STEPS; i++) begin : steps
        if(i%2 == 0) begin
            logic [NBW_WORD-1:0] RotWord;
            logic [NBW_WORD-1:0] SubWord;
            logic [NBW_WORD-1:0] RconXor;

            sbox uu_sbox0 (
                .i_data(RotWord[NBW_WORD-1-:NBW_BYTE]),
                .o_data(SubWord[NBW_WORD-1-:NBW_BYTE])
            );

            sbox uu_sbox1 (
                .i_data(RotWord[NBW_WORD-NBW_BYTE-1-:NBW_BYTE]),
                .o_data(SubWord[NBW_WORD-NBW_BYTE-1-:NBW_BYTE])
            );

            sbox uu_sbox2 (
                .i_data(RotWord[NBW_WORD-2*NBW_BYTE-1-:NBW_BYTE]),
                .o_data(SubWord[NBW_WORD-2*NBW_BYTE-1-:NBW_BYTE])
            );

            sbox uu_sbox3 (
                .i_data(RotWord[NBW_WORD-3*NBW_BYTE-1-:NBW_BYTE]),
                .o_data(SubWord[NBW_WORD-3*NBW_BYTE-1-:NBW_BYTE])
            );

            always_comb begin : main_operation
                RotWord = {expanded_key_ff[NBW_OUT-NBW_KEY-i*NBW_STEP+NBW_WORD-NBW_BYTE-1-:(NBW_WORD-NBW_BYTE)], expanded_key_ff[NBW_OUT-NBW_KEY-i*NBW_STEP+NBW_WORD-1-:NBW_BYTE]};
                RconXor = {SubWord[NBW_WORD-1-:NBW_BYTE]^Rcon[i/2], SubWord[NBW_WORD-NBW_BYTE-1-:(NBW_WORD-NBW_BYTE)]};

                step_key[i][NBW_STEP-1-:NBW_WORD]            = expanded_key_ff[NBW_OUT-(4*i  )*NBW_WORD-1-:NBW_WORD] ^ RconXor;
                step_key[i][NBW_STEP-NBW_WORD-1-:NBW_WORD]   = expanded_key_ff[NBW_OUT-(4*i+1)*NBW_WORD-1-:NBW_WORD] ^ step_key[i][NBW_STEP-1-:NBW_WORD];
                step_key[i][NBW_STEP-2*NBW_WORD-1-:NBW_WORD] = expanded_key_ff[NBW_OUT-(4*i+2)*NBW_WORD-1-:NBW_WORD] ^ step_key[i][NBW_STEP-NBW_WORD-1-:NBW_WORD];
                step_key[i][NBW_STEP-3*NBW_WORD-1-:NBW_WORD] = expanded_key_ff[NBW_OUT-(4*i+3)*NBW_WORD-1-:NBW_WORD] ^ step_key[i][NBW_STEP-2*NBW_WORD-1-:NBW_WORD];
            end
        end else begin
            logic [NBW_WORD-1:0] SubWord;

            sbox uu_sbox0 (
                .i_data(expanded_key_ff[NBW_OUT-NBW_KEY+NBW_WORD-i*NBW_STEP-1-:NBW_BYTE]),
                .o_data(SubWord[NBW_WORD-1-:NBW_BYTE])
            );

            sbox uu_sbox1 (
                .i_data(expanded_key_ff[NBW_OUT-NBW_KEY+NBW_WORD-i*NBW_STEP-NBW_BYTE-1-:NBW_BYTE]),
                .o_data(SubWord[NBW_WORD-NBW_BYTE-1-:NBW_BYTE])
            );

            sbox uu_sbox2 (
                .i_data(expanded_key_ff[NBW_OUT-NBW_KEY+NBW_WORD-i*NBW_STEP-2*NBW_BYTE-1-:NBW_BYTE]),
                .o_data(SubWord[NBW_WORD-2*NBW_BYTE-1-:NBW_BYTE])
            );

            sbox uu_sbox3 (
                .i_data(expanded_key_ff[NBW_OUT-NBW_KEY+NBW_WORD-i*NBW_STEP-3*NBW_BYTE-1-:NBW_BYTE]),
                .o_data(SubWord[NBW_WORD-3*NBW_BYTE-1-:NBW_BYTE])
            );

            always_comb begin : main_operation
                step_key[i][NBW_STEP-1-:NBW_WORD]            = expanded_key_ff[NBW_OUT-(4*i  )*NBW_WORD-1-:NBW_WORD] ^ SubWord;
                step_key[i][NBW_STEP-NBW_WORD-1-:NBW_WORD]   = expanded_key_ff[NBW_OUT-(4*i+1)*NBW_WORD-1-:NBW_WORD] ^ step_key[i][NBW_STEP-1-:NBW_WORD];
                step_key[i][NBW_STEP-2*NBW_WORD-1-:NBW_WORD] = expanded_key_ff[NBW_OUT-(4*i+2)*NBW_WORD-1-:NBW_WORD] ^ step_key[i][NBW_STEP-NBW_WORD-1-:NBW_WORD];
                step_key[i][NBW_STEP-3*NBW_WORD-1-:NBW_WORD] = expanded_key_ff[NBW_OUT-(4*i+3)*NBW_WORD-1-:NBW_WORD] ^ step_key[i][NBW_STEP-2*NBW_WORD-1-:NBW_WORD];
            end
        end
    end
endgenerate

assign expanded_key_nx = {valid_key   , step_key[0 ], step_key[1 ], step_key[2 ],
                          step_key[3 ], step_key[4 ], step_key[5 ], step_key[6 ],
                          step_key[7 ], step_key[8 ], step_key[9 ], step_key[10],
                          step_key[11], step_key[12]};

always_comb begin : input_data
    if (i_start) begin
        valid_key = i_key;
    end else begin
        valid_key = expanded_key_ff[NBW_OUT-1-:NBW_KEY];
    end
end

endmodule : aes_ke

