`timescale 1ns / 1ps

module tb_Min_Hamming_Distance_Finder;

    // Parameters for the testbench
    parameter BIT_WIDTH = 8;
    parameter REFERENCE_COUNT = 4;

    // Testbench signals
    reg  [BIT_WIDTH-1:0]                      input_query;
    reg  [REFERENCE_COUNT*BIT_WIDTH-1:0]      references;
    wire [$clog2(REFERENCE_COUNT)-1:0]        best_match_index;
    wire [$clog2(BIT_WIDTH+1)-1:0]            min_distance;

    // Instantiate the DUT
    Min_Hamming_Distance_Finder #(
        .BIT_WIDTH(BIT_WIDTH),
        .REFERENCE_COUNT(REFERENCE_COUNT)
    ) dut (
        .input_query(input_query),
        .references(references),
        .best_match_index(best_match_index),
        .min_distance(min_distance)
    );

    // Function to compute Hamming distance (popcount) between two vectors
    function [$clog2(BIT_WIDTH+1)-1:0] compute_expected_difference;
        input [BIT_WIDTH-1:0] data_A;
        input [BIT_WIDTH-1:0] data_B;
        integer i;
        reg [BIT_WIDTH-1:0] xor_result;
        reg [$clog2(BIT_WIDTH+1)-1:0] pop_count;
        begin
            xor_result = data_A ^ data_B;
            pop_count = 0;
            for (i = 0; i < BIT_WIDTH; i = i + 1) begin
                pop_count = pop_count + xor_result[i];
            end
            compute_expected_difference = pop_count;
        end
    endfunction

    // Task to compute expected best match index and minimum Hamming distance
    task compute_expected_results(
        input  [BIT_WIDTH-1:0] query,
        input  [REFERENCE_COUNT*BIT_WIDTH-1:0] refs,
        output integer expected_index,
        output integer expected_distance
    );
        integer i;
        integer curr_distance;
        reg [BIT_WIDTH-1:0] ref_vector;
        begin
            expected_distance = BIT_WIDTH + 1; // initialize with a max value
            expected_index = 0;
            for (i = 0; i < REFERENCE_COUNT; i = i + 1) begin
                // Extract the i-th reference vector using part-select
                ref_vector = refs[i*BIT_WIDTH +: BIT_WIDTH];
                curr_distance = compute_expected_difference(query, ref_vector);
                if (curr_distance < expected_distance) begin
                    expected_distance = curr_distance;
                    expected_index = i;
                end
            end
        end
    endtask

    // Coverage tracking
    integer total_tests = 0;
    integer passed_tests = 0;
    integer failed_tests = 0;

    // Task to validate the output of the Min_Hamming_Distance_Finder
    task validate_output(
        input [BIT_WIDTH-1:0] test_query,
        input [REFERENCE_COUNT*BIT_WIDTH-1:0] test_references,
        input string testcase_name
    );
        integer exp_index, exp_distance;
        begin
            input_query = test_query;
            references  = test_references;
            #10; // Wait for combinational logic to settle

            total_tests += 1;
            compute_expected_results(test_query, test_references, exp_index, exp_distance);

            if ((best_match_index === exp_index) && (min_distance === exp_distance)) begin
                passed_tests += 1;
                $display("[PASS] %s: Query=%b, Refs=%b -> Expected: index=%0d, dist=%0d; Got: index=%0d, dist=%0d",
                         testcase_name, test_query, test_references, exp_index, exp_distance, best_match_index, min_distance);
            end else begin
                failed_tests += 1;
                $error("[FAIL] %s: Query=%b, Refs=%b -> Expected: index=%0d, dist=%0d; Got: index=%0d, dist=%0d",
                         testcase_name, test_query, test_references, exp_index, exp_distance, best_match_index, min_distance);
            end
        end
    endtask

    // Task for testing specific edge cases
    task test_edge_cases;
        reg [BIT_WIDTH-1:0] ref_vector;
        reg [REFERENCE_COUNT*BIT_WIDTH-1:0] refs_temp;
        integer i;
        begin
            $display("Starting Edge Case Testing...");

            // Case 1: All references equal to input_query (zero distance)
            ref_vector = 8'b10101010;
            for (i = 0; i < REFERENCE_COUNT; i = i + 1) begin
                refs_temp[i*BIT_WIDTH +: BIT_WIDTH] = ref_vector;
            end
            validate_output(ref_vector, refs_temp, "All references equal to query");

            // Case 2: One reference is an exact match and others are completely different.
            input_query = 8'b11110000;
            // Set reference 0 to be completely different, reference 1 slightly different, reference 2 exact match, reference 3 different.
            refs_temp = {8'b00000000, 8'b11100000, 8'b11110000, 8'b10101010};
            validate_output(input_query, refs_temp, "Exact match among others");

            // Case 3: Test when the first reference is the closest
            input_query = 8'b01010101;
            refs_temp = {8'b01010100, 8'b10101010, 8'b11110000, 8'b00001111};
            validate_output(input_query, refs_temp, "First reference is closest");
        end
    endtask

    // Task for testing random inputs
    task test_random_inputs;
        integer i;
        reg [BIT_WIDTH-1:0] random_query;
        reg [REFERENCE_COUNT*BIT_WIDTH-1:0] random_refs;
        begin
            $display("Starting Randomized Testing...");
            for (i = 0; i < 100; i = i + 1) begin
                random_query = $urandom;
                random_refs  = $urandom;
                validate_output(random_query, random_refs, $sformatf("Random Test %0d", i+1));
            end
        end
    endtask

    // Task to print the summary
    task print_summary;
        begin
            $display("=================================================");
            $display("Test Summary:");
            $display("Total Tests Run: %0d", total_tests);
            $display("Tests Passed   : %0d", passed_tests);
            $display("Tests Failed   : %0d", failed_tests);
            $display("=================================================");
            if (failed_tests > 0)
                $error("Some tests failed. Check the logs for details.");
            else
                $display("All tests passed successfully!");
        end
    endtask

    initial begin
        $display("Starting testbench for Min_Hamming_Distance_Finder...");
        test_edge_cases();
        test_random_inputs();
        print_summary();
        $finish;
    end

endmodule

context.rtl/arithmetic_progression_generator.sv:
module arithmetic_progression_generator #(
    parameter DATA_WIDTH = 16,  // Width of the input data
    parameter SEQUENCE_LENGTH = 10 // Number of terms in the progression
)(
    clk,
    resetn,
    enable,
    start_val,
    step_size,
    out_val,
    done
);
  // ----------------------------------------
  // - Local parameter definition
  // ----------------------------------------

    localparam WIDTH_OUT_VAL = $clog2(SEQUENCE_LENGTH) + DATA_WIDTH; // Bit width of out_val to prevent overflow

  // ----------------------------------------
  // - Interface Definitions
  // ----------------------------------------
    input logic clk;                          // Clock signal
    input logic resetn;                       // Active-low reset
    input logic enable;                       // Enable signal for the generator
    input logic [DATA_WIDTH-1:0] start_val;   // Start value of the sequence
    input logic [DATA_WIDTH-1:0] step_size;   // Step size of the sequence
    output logic [WIDTH_OUT_VAL-1:0] out_val; // Current value of the sequence
    output logic done;                        // High when sequence generation is complete


  // ----------------------------------------
  // - Internal signals
  // ----------------------------------------
    logic [WIDTH_OUT_VAL-1:0] current_val;  // Register to hold the current value
    logic [$clog2(SEQUENCE_LENGTH)-1:0] counter;  // Counter to track sequence length

  // ----------------------------------------
  // - Procedural block
  // ----------------------------------------
    always_ff @(posedge clk or negedge resetn) begin
        if (!resetn) begin
            current_val <= 0;
            counter <= 0;
            done <= 1'b0;
        end else if (enable) begin
            if (!done) begin
                if (counter == 0) begin
                    current_val <= start_val; // Initialize with start value
                end else begin
                    current_val <= current_val + step_size; // Compute next term
                end

                if (counter < SEQUENCE_LENGTH - 1) begin
                    counter <= counter + 1; // Increment counter
                end else begin
                    done <= 1'b1; // Mark completion
                end
            end
        end
    end

  // ----------------------------------------
  // - Combinational Assignments
  // ----------------------------------------
    assign out_val = current_val;

endmodule

context.rtl/fifo_memory.sv:
module fifo_memory
    #(
        parameter p_data_width = 32,    // Memory data word width
        parameter p_addr_width = 16     // Number of memory address bits
    ) (
        input  wire                i_wr_clk,       // Write clock
        input  wire                i_wr_clk_en,    // Write clock enable
        input  wire [p_addr_width-1:0] i_wr_addr,    // Write address
        input  wire [p_data_width-1:0] i_wr_data,    // Write data
        input  wire                i_wr_full,      // Write full flag
        input  wire                i_rd_clk,       // Read clock
        input  wire                i_rd_clk_en,    // Read clock enable
        input  wire [p_addr_width-1:0] i_rd_addr,    // Read address
        output wire [p_data_width-1:0] o_rd_data     // Read data output
    );

    // Calculate the depth of the memory based on the address size
    localparam p_depth = 1 << p_addr_width;

    // Define the memory array with depth p_depth and data width p_data_width
    reg [p_data_width-1:0] r_memory [0:p_depth-1];
    reg [p_data_width-1:0] r_rd_data;  // Register to hold read data

    // Write operation
    always @(posedge i_wr_clk) begin
        if (i_wr_clk_en && !i_wr_full)          // If write is enabled and FIFO is not full
            r_memory[i_wr_addr] <= i_wr_data;   // Write data to memory at specified address
    end

    // Read operation
    always @(posedge i_rd_clk) begin
        if (i_rd_clk_en)                        // If read is enabled
            r_rd_data <= r_memory[i_rd_addr];   // Read data from memory at specified address
    end

    // Assign the read data register to the output
    assign o_rd_data = r_rd_data;

endmodule

context.rtl/read_to_write_pointer_sync.sv:
module read_to_write_pointer_sync 
    #(
        parameter p_addr_width = 16  // Parameter to define the address width of the FIFO
    )(
        input  wire              i_wr_clk,           // Write clock
        input  wire              i_wr_rst_n,         // Write reset (active low)
        input  wire [p_addr_width:0] i_rd_grey_addr, // Gray-coded read address from the read clock domain
        output reg  [p_addr_width:0] o_rd_ptr_sync   // Synchronized read pointer in the write clock domain
    );

    // Internal register to hold the intermediate synchronized read pointer
    reg [p_addr_width:0] r_rd_ptr_ff;

    // Always block for synchronizing the read pointer to the write clock domain
    always @(posedge i_wr_clk or negedge i_wr_rst_n) 
    begin
        if (!i_wr_rst_n) begin
            // If reset is asserted (active low), reset the synchronized pointers to 0
            o_rd_ptr_sync <= {p_addr_width+1{1'b0}};
            r_rd_ptr_ff <= {p_addr_width+1{1'b0}};
        end else begin
            // If reset is not asserted, synchronize the read pointer to the write clock domain
            r_rd_ptr_ff <= i_rd_grey_addr;  // First stage of synchronization
            o_rd_ptr_sync <= r_rd_ptr_ff;   // Second stage of synchronization
        end
    end

endmodule

context.rtl/rptr_empty.sv:
module rptr_empty 
    #(
        parameter p_addr_width = 16  // Parameter to define the address width of the FIFO
    )(
        input  wire                i_rd_clk,         // Read clock
        input  wire                i_rd_rst_n,       // Read reset (active low)
        input  wire                i_rd_en,          // Read enable signal
        input  wire [p_addr_width  :0] i_wr_ptr_sync, // Synchronized write pointer from the write clock domain
        output reg                 o_fifo_empty,     // Output flag indicating if the FIFO is empty
        output wire [p_addr_width-1:0] o_rd_bin_addr, // Output binary read address
        output reg  [p_addr_width  :0] o_rd_grey_addr // Output Gray-coded read address
    );

    // Internal registers and wires
    reg  [p_addr_width:0] r_rd_bin_addr_pointer; // Register to store the current binary read address
    wire [p_addr_width:0] w_rd_next_grey_addr_pointer; // Wire for the next Gray-coded read address
    wire [p_addr_width:0] w_rd_next_bin_addr_pointer; // Wire for the next binary read address
    wire                  w_rd_empty;             // Wire indicating if the FIFO is empty

    //-------------------
    // GRAYSTYLE2 pointer
    //-------------------
    always @(posedge i_rd_clk or negedge i_rd_rst_n) 
    begin
        if (!i_rd_rst_n) begin
            // Reset the read address pointers to 0 on reset
            r_rd_bin_addr_pointer <= {p_addr_width+1{1'b0}};
            o_rd_grey_addr <= {p_addr_width+1{1'b0}};
        end else begin
            // Update the read address pointers on each clock edge
            r_rd_bin_addr_pointer <= w_rd_next_bin_addr_pointer;
            o_rd_grey_addr <= w_rd_next_grey_addr_pointer;
        end
    end

    // Memory read-address pointer (binary addressing for memory access)
    assign o_rd_bin_addr = r_rd_bin_addr_pointer[p_addr_width-1:0];

    // Calculate the next binary read address, increment only if read enable is active and FIFO is not empty
    assign w_rd_next_bin_addr_pointer = r_rd_bin_addr_pointer + (i_rd_en & ~o_fifo_empty);

    // Convert the next binary read address to Gray code
    assign w_rd_next_grey_addr_pointer = (w_rd_next_bin_addr_pointer >> 1) ^ w_rd_next_bin_addr_pointer;

    //---------------------------------------------------------------
    // FIFO is empty when the next Gray-coded read address matches the synchronized write pointer or on reset
    //---------------------------------------------------------------
    assign w_rd_empty = (w_rd_next_grey_addr_pointer == i_wr_ptr_sync);

    // Always block for updating the FIFO empty flag
    always @(posedge i_rd_clk or negedge i_rd_rst_n) begin
        if (!i_rd_rst_n) begin
            // Reset the FIFO empty flag to 1 on reset
            o_fifo_empty <= 1'b1;
        end else begin
            // Update the FIFO empty flag based on the calculated empty condition
            o_fifo_empty <= w_rd_empty;
        end
    end

endmodule

context.rtl/wptr_full.sv:
module wptr_full 
    #(
        parameter p_addr_width = 16  // Parameter to define the address width of the FIFO
    )(
        input  wire                     i_wr_clk,         // Write clock
        input  wire                     i_wr_rst_n,       // Write reset (active low)
        input  wire                     i_wr_en,          // Write enable signal
        input  wire [p_addr_width  :0]  i_rd_ptr_sync,    // Synchronized read pointer from the read clock domain
        output reg                      o_fifo_full,      // Output flag indicating if the FIFO is full
        output wire [p_addr_width-1:0]  o_wr_bin_addr,    // Output binary write address
        output reg  [p_addr_width  :0]  o_wr_grey_addr    // Output Gray-coded write address
    );

    // Internal registers and wires
    reg  [p_addr_width:0] r_wr_bin_addr_pointer;  // Register to store the current binary write address
    wire [p_addr_width:0] w_wr_next_bin_addr_pointer; // Wire for the next binary write address
    wire [p_addr_width:0] w_wr_next_grey_addr_pointer; // Wire for the next Gray-coded write address
    wire                  w_wr_full;             // Wire indicating if the FIFO is full

    // Always block for updating the write address pointers
    // GRAYSTYLE2 pointer update mechanism
    always @(posedge i_wr_clk or negedge i_wr_rst_n) begin
        if (!i_wr_rst_n) begin
            // Reset the write address pointers to 0 on reset
            r_wr_bin_addr_pointer <= {p_addr_width{1'b0}};
            o_wr_grey_addr <= {p_addr_width{1'b0}};
        end else begin
            // Update the write address pointers on each clock edge
            r_wr_bin_addr_pointer <= w_wr_next_bin_addr_pointer;
            o_wr_grey_addr <= w_wr_next_grey_addr_pointer;
        end
    end

    // Assign the binary write address for addressing the memory
    assign o_wr_bin_addr = r_wr_bin_addr_pointer[p_addr_width-1:0];

    // Calculate the next binary write address, only increment if write enable is active and FIFO is not full
    assign w_wr_next_bin_addr_pointer  = r_wr_bin_addr_pointer + (i_wr_en & ~o_fifo_full);

    // Convert the next binary write address to Gray code
    assign w_wr_next_grey_addr_pointer = (w_wr_next_bin_addr_pointer >> 1) ^ w_wr_next_bin_addr_pointer;

    // Check if the FIFO is full by comparing the next Gray-coded write address with the synchronized read pointer
    // FIFO is full if the next write address matches the read pointer with the MSB inverted
    assign w_wr_full = (w_wr_next_grey_addr_pointer == {~i_rd_ptr_sync[p_addr_width:p_addr_width-1], i_rd_ptr_sync[p_addr_width-2:0]});

    // Always block for updating the FIFO full flag
    always @(posedge i_wr_clk or negedge i_wr_rst_n) begin
        if (!i_wr_rst_n) begin
            // Reset the FIFO full flag to 0 on reset
            o_fifo_full <= 1'b0;
        end else begin
            // Update the FIFO full flag based on the calculated full condition
            o_fifo_full <= w_wr_full;
        end
    end

endmodule

context.rtl/write_to_read_pointer_sync.sv:
module write_to_read_pointer_sync 
    #(
        parameter p_addr_width = 16  // Parameter to define the address width of the FIFO
    )(
        input  wire              i_rd_clk,         // Read clock
        input  wire              i_rd_rst_n,       // Read reset (active low)
        input  wire [p_addr_width:0] i_wr_grey_addr, // Input Gray-coded write address
        output reg  [p_addr_width:0] o_wr_ptr_sync // Output synchronized write pointer

    );

    // Internal register to hold the intermediate synchronized write pointer
    reg [p_addr_width:0] r_wr_ptr_ff;

    // Always block for synchronizing the write pointer to the read clock domain
    always @(posedge i_rd_clk or negedge i_rd_rst_n) 
    begin
        if (!i_rd_rst_n) begin
            // If reset is asserted (active low), reset the synchronized pointers to 0
            o_wr_ptr_sync <= {p_addr_width+1{1'b0}};
            r_wr_ptr_ff <= {p_addr_width+1{1'b0}};
        end else begin
            // If reset is not asserted, synchronize the write pointer to the read clock domain
            r_wr_ptr_ff <= i_wr_grey_addr;  // First stage of synchronization
            o_wr_ptr_sync <= r_wr_ptr_ff;   // Second stage of synchronization
        end
    end

endmodule

context.verif/async_filo_tb.sv:
`timescale 1ns / 1ps

module async_filo_tb ();

  // Parameters
  localparam DATA_WIDTH = 8;
  localparam DEPTH = 8;

  // Testbench Signals
  reg w_clk;
  reg r_clk;
  reg w_rst;
  reg r_rst;
  reg push;
  reg pop;
  reg [DATA_WIDTH-1:0] w_data;
  wire [DATA_WIDTH-1:0] r_data;
  wire r_empty;
  wire w_full;

  // Local Flags and Counter
  integer counter;
  logic empty, full;
  reg [DATA_WIDTH-1:0] pushed_data[0:DEPTH-1];
  reg [DATA_WIDTH-1:0] rd_data;

  // Instantiate the DUT (Device Under Test)
  async_filo #(
      .DATA_WIDTH(DATA_WIDTH),
      .DEPTH(DEPTH)
  ) async_filo_inst (
      .w_clk(w_clk),
      .w_rst(w_rst),
      .push(push),
      .r_rst(r_rst),
      .r_clk(r_clk),
      .pop(pop),
      .w_data(w_data),
      .r_data(r_data),
      .r_empty(r_empty),
      .w_full(w_full)
  );

  initial begin
    w_clk = 0;
    forever #5 w_clk = ~w_clk;
  end

  initial begin
    r_clk = 0;
    forever #7 r_clk = ~r_clk;
  end

  initial begin

    counter = 0;
    empty = 1;
    full = 0;

    w_rst = 1;
    r_rst = 1;
    push = 0;
    pop = 0;
    w_data = 0;


    $display("Applying Reset...");
    #20;
    w_rst = 0;
    r_rst = 0;
    $display("Reset Complete");
    $display("Depth = 8");
    $display("Empty Status: %0d | Full Status: %0d", empty, full);

    simulate_filo_behavior();

    $display("-------------------------------");
    $display("Performing 3 Push Operations...");
    push_data($urandom_range(0, (1 << DATA_WIDTH) - 1));
    push_data($urandom_range(0, (1 << DATA_WIDTH) - 1));
    push_data($urandom_range(0, (1 << DATA_WIDTH) - 1));

    $display("Performing 3 Pop Operations...");
    pop_data();
    pop_data();
    pop_data();

    // End Simulation
    $display("Test Completed.");
    #100;
    $finish;
  end

  task simulate_filo_behavior;
    begin
      $display("Simulating FILO Behavior - Push Operations...");
      for (int i = 0; i < DEPTH; i++) begin
        if (!full) begin
          push_data($urandom_range(0, (1 << DATA_WIDTH) - 1));
        end
      end

      $display("Simulating FILO Behavior - Pop Operations...");
      for (int i = 0; i < DEPTH; i++) begin
        if (!empty) begin
          pop_data();
        end
      end
    end
  endtask

  task push_data(input [DATA_WIDTH-1:0] data_in);
    begin
      if (!full) begin
        push = 1;
        w_data = data_in;
        pushed_data[counter] = data_in;
        @(posedge w_clk);
        push    = 0;
        counter = counter + 1;
        full    = (counter == DEPTH);
        empty   = 0;

        $display("Pushed Data: %h | Counter: %0d | Full: %0d | Empty: %0d ", data_in, counter,
                 full, empty);
      end else begin
        $display("Cannot Push, FILO is Full.");
      end
    end
  endtask

  task pop_data;
    reg [DATA_WIDTH-1:0] expected_data;
    begin
      if (!empty) begin
        rd_data = pushed_data[counter-1];
        pop = 1;
        full    = 0;
        @(posedge r_clk);
        pop = 0;

        $display("Popped Data: %h | Counter: %0d | Full: %0d | Empty: %0d", rd_data, counter - 1,
                 full, (counter == 1));

        counter = counter - 1;

      end else begin
        $display("Cannot Pop, FILO is Empty.");
      end
    end
  endtask

endmodule

context.rtl/axi4lite_to_pcie_cfg_bridge.sv:
`timescale 1ns/1ps

module axi4lite_to_pcie_cfg_bridge #(

    parameter ADDR_WIDTH = 8,
    parameter DATA_WIDTH = 32  
    )(
    // AXI4-Lite Interface
    input  logic        aclk,           
    input  logic        aresetn,        
    input  logic [ADDR_WIDTH-1:0] awaddr,         
    input  logic        awvalid,        
    output logic        awready,        
    input  logic [DATA_WIDTH-1:0] wdata,          
    input  logic [DATA_WIDTH/8-1:0]  wstrb,          
    input  logic        wvalid,         
    output logic        wready,         
    output logic [1:0]  bresp,          
    output logic        bvalid,         
    input  logic        bready,         

    // PCIe Configuration Space Interface
    output logic [ADDR_WIDTH/4-1:0]  pcie_cfg_addr,  
    output logic [DATA_WIDTH-1:0] pcie_cfg_wdata, 
    output logic        pcie_cfg_wr_en, 
    input  logic [DATA_WIDTH-1:0] pcie_cfg_rdata, 
    input  logic        pcie_cfg_rd_en  
);

    // FSM States
    typedef enum logic [2:0] {
        IDLE,           
        ADDR_CAPTURE,  
        DATA_CAPTURE,  
        PCIE_WRITE,    
        SEND_RESPONSE  
    } state_t;

    state_t current_state, next_state;

    // Internal registers
    logic [ADDR_WIDTH-1:0] awaddr_reg;  
    logic [DATA_WIDTH-1:0] wdata_reg;   
    logic [DATA_WIDTH/8-1:0]  wstrb_reg;   

    // FSM State Transition
    always_ff @(posedge aclk or negedge aresetn) begin
        if (!aresetn) begin
            current_state <= IDLE;
        end else begin
            current_state <= next_state;
        end
    end

    // FSM Next State Logic
    always_comb begin
        next_state = current_state;
        case (current_state)
            IDLE: begin
                if (awvalid && wvalid) begin
                    next_state = ADDR_CAPTURE;
                end
            end

            ADDR_CAPTURE: begin
                next_state = DATA_CAPTURE;
            end

            DATA_CAPTURE: begin
                next_state = PCIE_WRITE;
            end

            PCIE_WRITE: begin
                next_state = SEND_RESPONSE;
            end

            SEND_RESPONSE: begin
                if (bready) begin
                    next_state = IDLE;
                end
            end

            default: begin
                next_state = IDLE;
            end
        endcase
    end

    // FSM Output Logic
    always_ff @(posedge aclk or negedge aresetn) begin
        if (!aresetn) begin
            awready <= 1'b0;
            wready <= 1'b0;
            bvalid <= 1'b0;
            bresp <= 2'b00; // OKAY response
            pcie_cfg_wr_en <= 1'b0;
            pcie_cfg_wdata <= 32'h0;
            pcie_cfg_addr <= 8'h0;
            awaddr_reg <= 32'h0;
            wdata_reg <= 32'h0;
            wstrb_reg <= 4'h0;
        end else begin
            case (current_state)
                IDLE: begin
                    awready <= 1'b0;
                    wready <= 1'b0;
                    bvalid <= 1'b0;
                    pcie_cfg_wr_en <= 1'b0;
                end

                ADDR_CAPTURE: begin
                    awready <= 1'b1;
                    awaddr_reg <= awaddr;
                end

                DATA_CAPTURE: begin
                    wready <= 1'b1;
                    wdata_reg <= wdata;
                    wstrb_reg <= wstrb;
                end

                PCIE_WRITE: begin
                    pcie_cfg_wr_en <= 1'b1;
                    pcie_cfg_addr <= awaddr_reg[7:0]; // 8-bit PCIe address

                    // Apply wstrb to write only the selected bytes
                    for (int i = 0; i < (DATA_WIDTH/8); i++) begin
                        pcie_cfg_wdata[(i*8)+:8] <= (wstrb_reg[i]) ? wdata_reg[(i*8)+:8] : pcie_cfg_rdata[(i*8)+:8];
                    end
                end

                SEND_RESPONSE: begin
                    pcie_cfg_wr_en <= 1'b0;
                    bvalid <= 1'b1;
                end

                default: begin
                    // Default outputs
                end
            endcase
        end
    end

endmodule

context.rtl/axis_broadcast.sv:
module axis_broadcast (
     input  wire              clk,
     input  wire              rst_n,
     // AXI Stream Input
     input  wire [8-1:0]      s_axis_tdata,
     input  wire              s_axis_tvalid,
     output reg               s_axis_tready,
     // AXI Stream Outputs
     output wire  [8-1:0]     m_axis_tdata_1,
     output wire              m_axis_tvalid_1,
     input  wire              m_axis_tready_1,

     output wire  [8-1:0]     m_axis_tdata_2,
     output wire              m_axis_tvalid_2,
     input  wire              m_axis_tready_2,

     output wire  [8-1:0]     m_axis_tdata_3,
     output wire              m_axis_tvalid_3,
     input  wire              m_axis_tready_3
 );
 wire s_axis_tready_t1;

 reg [7:0]m_axis_tdata_1_reg;
 reg [7:0]m_axis_tvalid_1_reg;
 reg [7:0]m_axis_tdata_2_reg;
 reg [7:0]m_axis_tvalid_2_reg;
 reg [7:0]m_axis_tdata_3_reg;
 reg [7:0]m_axis_tvalid_3_reg;
 // Broadcast logic: forward input to all outputs
 assign s_axis_tready_t1 = m_axis_tready_1 && m_axis_tready_2 && m_axis_tready_3 ;  // Ready only if all outputs are ready

 // Generate output signals
 always @(posedge clk or negedge rst_n) 
 begin
     if (~rst_n) 
     begin
         m_axis_tdata_1_reg <= 0;
         m_axis_tvalid_1_reg <= 0;
         m_axis_tdata_2_reg <= 0;
         m_axis_tvalid_2_reg <= 0;
         m_axis_tdata_3_reg <= 0;
         m_axis_tvalid_3_reg <= 0;
     end 
     else if (s_axis_tready_t1)
     begin
         m_axis_tdata_1_reg  <= s_axis_tdata;
         m_axis_tvalid_1_reg <= s_axis_tvalid;
         m_axis_tdata_2_reg  <= s_axis_tdata;
         m_axis_tvalid_2_reg <= s_axis_tvalid;
         m_axis_tdata_3_reg  <= s_axis_tdata;
         m_axis_tvalid_3_reg <= s_axis_tvalid;
     end
 end

 always @(posedge clk or negedge rst_n) 
 begin
     if (~rst_n) 
          s_axis_tready <= 0;
     else 
          s_axis_tready <= s_axis_tready_t1 ;
 end

 assign m_axis_tdata_1 = m_axis_tdata_1_reg;
 assign m_axis_tvalid_1 = m_axis_tvalid_1_reg;
 assign m_axis_tdata_2 = m_axis_tdata_2_reg;
 assign m_axis_tvalid_2 = m_axis_tvalid_2_reg;
 assign m_axis_tdata_3 = m_axis_tdata_3_reg;
 assign m_axis_tvalid_3 = m_axis_tvalid_3_reg;

endmodule

