`timescale 1ns/1ps

module tb_fifo_buffer;


  parameter int unsigned NUM_OF_REQS = 2;
  parameter bit          ResetAll    = 1'b0; 


  logic                clk_i;
  logic                rst_i;
  logic                clear_i;
  logic [NUM_OF_REQS-1:0] busy_o;

  logic                in_valid_i;
  logic [31:0]         in_addr_i;
  logic [31:0]         in_rdata_i;
  logic                in_err_i;

  logic                out_valid_o;
  logic                out_ready_i;
  logic [31:0]         out_addr_o;
  logic [31:0]         out_rdata_o;
  logic                out_err_o;
  logic                out_err_plus2_o;


  fifo_buffer #(
    .NUM_OF_REQS(NUM_OF_REQS),
    .ResetAll(ResetAll)
  ) dut (
    .clk_i(clk_i),
    .rst_i(rst_i),
    .clear_i(clear_i),
    .busy_o(busy_o),
    .in_valid_i(in_valid_i),
    .in_addr_i(in_addr_i),
    .in_rdata_i(in_rdata_i),
    .in_err_i(in_err_i),
    .out_valid_o(out_valid_o),
    .out_ready_i(out_ready_i),
    .out_addr_o(out_addr_o),
    .out_rdata_o(out_rdata_o),
    .out_err_o(out_err_o),
    .out_err_plus2_o(out_err_plus2_o)
  );

  initial begin
    clk_i = 0;
    forever #5 clk_i = ~clk_i;
  end

  initial begin
    rst_i = 0;
    #20;
    rst_i = 1;
  end

  initial begin
    clear_i     = 0;
    in_valid_i  = 0;
    in_addr_i   = 32'h0000_0000;
    in_rdata_i  = 32'h0;
    in_err_i    = 0;
    out_ready_i = 0;

    @(posedge rst_i);
    #10;

    $display("\n*** Test 1: Clear FIFO (Aligned PC) ***");
    clear_i   = 1;
    in_addr_i = 32'h0000_0000;
    #10;
    clear_i = 0;
    #10;

    $display("\n*** Test 2: Single Instruction Fetch (Aligned) ***");
    in_valid_i = 1;
    in_rdata_i = 32'h8C218363;
    in_err_i   = 0;
    #10;
    in_valid_i = 0;
    #10;

    out_ready_i = 1;
    #10;
    out_ready_i = 0;
    #10;

    $display("\n*** Test 3: FIFO Depth Test ***");
    in_valid_i = 1;
    in_rdata_i = 32'h6C2183E3;
    in_err_i   = 0;
    #10;
    in_rdata_i = 32'h926CF16F;
    #10;
    in_valid_i = 0;
    #10;

    out_ready_i = 1;
    repeat (3) begin
      #10;
    end
    out_ready_i = 0;
    #10;

    $display("\n*** Test 4: Unaligned Instruction Fetch ***");
    clear_i   = 1;
    in_addr_i = 32'h0000_0002;
    #10;
    clear_i = 0;
    #10;

    in_valid_i = 1;
    in_rdata_i = 32'hF63101E7;
    in_err_i   = 0;
    #10;

    in_rdata_i = 32'h763101E7;
    #10;
    in_valid_i = 0;
    #10;

    out_ready_i = 1;
    repeat (3) begin
      #10;
    end
    out_ready_i = 0;
    #10;

    $display("\n*** Test 5: Error Handling ***");
    clear_i   = 1;
    in_addr_i = 32'h0000_0000;
    #10;
    clear_i = 0;
    #10;

    in_valid_i = 1;
    in_rdata_i = 32'h4840006F;
    in_err_i   = 1;
    #10;
    in_valid_i = 0;
    #10;

    out_ready_i = 1;
    #10;
    out_ready_i = 0;
    #10;

    $display("\n*** End of Simulation ***");
    $finish;
  end

  initial begin
    $display("Time\tclear in_valid in_addr    in_rdata      in_err | out_valid out_addr    out_rdata     out_err out_err_plus2 | busy");
    $monitor("%0t\t%b      %b      %h   %h    %b    | %b      %h  %h   %b    %b    | %h",
             $time, clear_i, in_valid_i, in_addr_i, in_rdata_i, in_err_i,
             out_valid_o, out_addr_o, out_rdata_o, out_err_o, out_err_plus2_o,
             busy_o);
  end

endmodule

context.verif/tb_direct_map_cache.sv:
`timescale 1ns/1ps

module tb_direct_map_cache;

    parameter CACHE_SIZE   = 256; // Number of cache lines
    parameter DATA_WIDTH   = 16;  // Width of data
    parameter TAG_WIDTH    = 5;   // Width of the tag
    parameter OFFSET_WIDTH = 3;   // Width of the offset
    localparam INDEX_WIDTH = $clog2(CACHE_SIZE); // Width of the index

    reg enable;
    reg [INDEX_WIDTH-1:0] index;
    reg [OFFSET_WIDTH-1:0] offset;
    reg comp;
    reg write;
    reg [TAG_WIDTH-1:0] tag_in;
    reg [DATA_WIDTH-1:0] data_in;
    reg valid_in;
    reg clk;
    reg rst;

    wire hit;
    wire dirty;
    wire [TAG_WIDTH-1:0] tag_out;
    wire [DATA_WIDTH-1:0] data_out;
    wire valid;
    wire error;

    direct_map_cache #(
        .CACHE_SIZE(CACHE_SIZE),
        .DATA_WIDTH(DATA_WIDTH),
        .TAG_WIDTH(TAG_WIDTH),
        .OFFSET_WIDTH(OFFSET_WIDTH)
    ) uut (
        .enable(enable),
        .index(index),
        .offset(offset),
        .comp(comp),
        .write(write),
        .tag_in(tag_in),
        .data_in(data_in),
        .valid_in(valid_in),
        .clk(clk),
        .rst(rst),
        .hit(hit),
        .dirty(dirty),
        .tag_out(tag_out),
        .data_out(data_out),
        .valid(valid),
        .error(error)
    );

    initial begin
        clk = 0;
        forever #5 clk = ~clk; 
    end

    reg [INDEX_WIDTH-1:0] stored_index;
    reg [OFFSET_WIDTH-1:0] stored_offset;
    reg [TAG_WIDTH-1:0]    stored_tag;
    reg [DATA_WIDTH-1:0]   stored_data;

    initial begin
        reset();

        // 1) Write operation with comp=0 (Write_Comp0)
        //    We'll do a random write, then read it back with comp=1 expecting a hit
        write_comp0();
        @(negedge clk);

        // 2) Read operation for compare=1 => expect a hit if the same index/tag/offset
        read_comp1();
        @(negedge clk);

        // 3) Write operation for compare=1 => random data, same index/tag to see if dirty is set
        write_comp1();
        @(negedge clk);

        // 4) Read again using compare=1 => should be a hit, check data matches
        read_comp1();
        @(negedge clk);

        // 5) Miss test => choose a new random index to force a miss
        miss_test();
        @(negedge clk);

        // 6) Write again with compare=1 => same index/tag as stored to see if we get a hit
        write_comp1();
        @(negedge clk);

        // 7) Read with compare=0 => different path, check signals
        read_comp0();
        @(negedge clk);

        // 8) Force an error by setting offset’s LSB=1
        //    This should set error=1 and force the design to respond with hit=0, valid=0
        force_offset_error();
        @(negedge clk);

        // Wait a bit and finish
        #50;
        $finish;
    end

    task reset();
        begin
            rst     = 1;
            enable  = 0;
            comp    = 0;
            write   = 0;
            index   = 0;
            offset  = 0;
            tag_in  = 0;
            data_in = 0;
            valid_in= 0;

            @(negedge clk);
            rst = 0;
            @(negedge clk);
            $display("\n[RESET] Completed at time %0t", $time);
        end
    endtask

    // ------------------------------------------------------
    // TASK: WRITE with comp=0
    //       "Access Write (comp=0, write=1)"
    // ------------------------------------------------------
    task write_comp0();
        begin
            enable   = 1;
            comp     = 0;
            write    = 1;
            valid_in = 1'b1;

            stored_index = $random % CACHE_SIZE;
            // Force offset’s LSB=0 so there is no error
            stored_offset = ($random % (1<<OFFSET_WIDTH)) & ~1;
            stored_tag    = $random % (1<<TAG_WIDTH);
            stored_data   = $random % (1<<DATA_WIDTH);

            index   = stored_index;
            offset  = stored_offset;
            tag_in  = stored_tag;
            data_in = stored_data;

            @(negedge clk);
            $display("\n[WRITE_COMP0] @time %0t", $time);
            $display("  -> index=%0d, offset=%0d, tag_in=%b, data_in=%0h", 
                      index, offset, tag_in, data_in);
            $display("  -> comp=%b, write=%b, valid_in=%b", comp, write, valid_in);

            // After a comp=0 write, the design typically sets hit=0.
            // We'll just check that there's no error and that valid is eventually set inside the cache.
            if (error == 1) begin
                $display("  **ERROR** Unexpected error during write_comp0!");
            end
        end
    endtask

    // ------------------------------------------------------
    // TASK: READ with comp=1
    //       "Compare Read (comp=1, write=0)"
    // ------------------------------------------------------
    task read_comp1();
        begin
            comp  = 1;
            write = 0;
            // We re-apply the same stored index/tag to expect a hit
            index   = stored_index;
            offset  = stored_offset;
            tag_in  = stored_tag;

            @(negedge clk);
            $display("\n[READ_COMP1] @time %0t", $time);
            $display("  -> index=%0d, offset=%0d, tag_in=%b, data_out=%0h, valid=%b, hit=%b",
                     index, offset, tag_in, data_out, valid, hit);

            // Check if we got a hit, valid line, and correct data
            if (hit && valid && (data_out == stored_data)) begin
                $display("  PASS: Expected read hit and correct data.");
            end else begin
                $display("  FAIL: Expected a read hit or data mismatch!");
            end

            // Also check that 'error' is 0
            if (error == 1) begin
                $display("  **ERROR** Unexpected error during read_comp1!");
            end
        end
    endtask

    // ------------------------------------------------------
    // TASK: WRITE with comp=1
    //       "Compare Write (comp=1, write=1)"
    //       - If the same tag/index is used, line should go dirty.
    // ------------------------------------------------------
    task write_comp1();
        begin
            comp   = 1;
            write  = 1;
            enable = 1;
            valid_in = 1'b1;

            // Keep the same stored_index, stored_tag to see if we get a "hit"
            // but randomize data again
            index   = stored_index;
            offset  = stored_offset;
            tag_in  = stored_tag;
            stored_data = $random % (1<<DATA_WIDTH);
            data_in = stored_data;

            @(negedge clk);
            $display("\n[WRITE_COMP1] @time %0t", $time);
            $display("  -> index=%0d, offset=%0d, tag_in=%b, data_in=%0h, comp=%b, write=%b",
                     index, offset, tag_in, data_in, comp, write);

            // If the tag matches and valid was set, we should see a hit and the line become dirty.
            if (hit == 1 && valid == 1) begin
                $display("  => Compare write was a hit. Checking dirty bit...");
                if (dirty == 1) begin
                    $display("  PASS: dirty=1 as expected for Compare Write on an existing line.");
                end else begin
                    $display("  FAIL: dirty bit not set, unexpected!");
                end
            end
            else begin
                $display("  => Compare write was a miss or invalid line. The line is newly allocated.");
                // Possibly the line's dirty bit is reset to 0 in a real design, 
                // or it might be set depending on policy. Check your DUT logic.
            end
        end
    endtask

    // ------------------------------------------------------
    // TASK: READ with comp=0
    //       "Access Read (comp=0, write=0)"
    // ------------------------------------------------------
    task read_comp0();
        begin
            comp  = 0;
            write = 0;
            // We'll continue using the same stored index/tag
            index   = stored_index;
            offset  = stored_offset;
            tag_in  = stored_tag;

            @(negedge clk);
            $display("\n[READ_COMP0] @time %0t", $time);
            $display("  -> index=%0d, offset=%0d, tag_in=%b, data_out=%0h, valid=%b, hit=%b", 
                     index, offset, tag_in, data_out, valid, hit);

            // Typically comp=0 read does not check tag => hit=0 in the given code
            // We'll confirm there's no error
            if (error == 1) begin
                $display("  **ERROR** Unexpected error during read_comp0!");
            end
        end
    endtask

    // ------------------------------------------------------
    // TASK: MISS TEST
    //       Force a different index or tag so we get a miss.
    // ------------------------------------------------------
    task miss_test();
        reg [INDEX_WIDTH-1:0] new_index;
        begin
            comp  = 1;
            write = 0;
            enable = 1;

            // Force a new index to differ from stored_index so we get a guaranteed miss
            new_index = (stored_index + 1) % CACHE_SIZE;
            index = new_index;
            // Keep offset’s LSB=0 to avoid error
            offset = ($random % (1<<OFFSET_WIDTH)) & ~1;
            // We can reuse stored_tag or randomize it
            tag_in = $random % (1<<TAG_WIDTH);

            @(negedge clk);
            $display("\n[MISS_TEST] @time %0t", $time);
            $display("  -> new_index=%0d, offset=%0d, tag_in=%b, data_out=%0h, valid=%b, hit=%b",
                     new_index, offset, tag_in, data_out, valid, hit);

            if (!hit) begin
                $display("  PASS: Expected MISS, got hit=0");
            end else begin
                $display("  FAIL: Unexpected hit=1, was supposed to be a miss!");
            end

            // Also check there's no unexpected error
            if (error == 1) begin
                $display("  **ERROR** Unexpected error during miss_test!");
            end
        end
    endtask

    // ------------------------------------------------------
    // TASK: Force offset’s LSB=1 to generate an ERROR
    // ------------------------------------------------------
    task force_offset_error();
        begin
            $display("\n[OFFSET_ERROR_TEST] Forcing offset LSB=1, expecting 'error=1'.");
            offset = 3'b001; // LSB=1
            // Keep any values for comp/write
            comp   = 0; 
            write  = 0;
            index  = 0;
            tag_in = 0;
            data_in= 0;
            @(negedge clk);

            if (error == 1) begin
                $display("  PASS: 'error' asserted as expected when offset LSB=1.");
            end else begin
                $display("  FAIL: 'error' did not assert with offset LSB=1!");
            end
        end
    endtask

    initial begin
        $dumpfile("test.vcd");
        $dumpvars(0, tb_direct_map_cache);
    end

endmodule

`timescale 1ns/1ps

module tb_direct_map_cache;

    // Parameters
    parameter CACHE_SIZE = 256;               // Number of cache lines
    parameter DATA_WIDTH = 16;                // Width of data
    parameter TAG_WIDTH = 5;                  // Width of the tag
    parameter OFFSET_WIDTH = 3;               // Width of the offset

    // Inputs
    reg enable;                               // Enable signal for cache
    reg [7:0] index;                          // 8 bits for indexing into the cache
    reg [OFFSET_WIDTH-1:0] offset;            // 3 bits for offset
    reg comp;                                 // Compare signal
    reg write;                                // Write signal
    reg [TAG_WIDTH-1:0] tag_in;               // Tag input
    reg [DATA_WIDTH-1:0] data_in;             // Data input
    reg valid_in;                             // Valid input for cache line
    reg clk;                                  // Clock signal
    reg rst;                                  // Reset signal
    reg [DATA_WIDTH-1:0] data;                // Data variable for tasks

    // Outputs
    wire hit;                                 // Hit indication
    wire dirty;                               // Dirty state indication
    wire [TAG_WIDTH-1:0] tag_out;             // Output tag of the cache line
    wire [DATA_WIDTH-1:0] data_out;           // Output data from the cache line
    wire valid;                               // Valid state output
    wire error;                               // Error indication

    integer i;                                

    // Instantiate the cache
    direct_map_cache #(
                                    .CACHE_SIZE(CACHE_SIZE),
                                    .DATA_WIDTH(DATA_WIDTH),
                                    .TAG_WIDTH(TAG_WIDTH),
                                    .OFFSET_WIDTH(OFFSET_WIDTH)
                                   )
			                   uut (
                                    .enable(enable),
                                    .index(index),
                                    .offset(offset),
                                    .comp(comp),
                                    .write(write),
                                    .tag_in(tag_in),
                                    .data_in(data_in),
                                    .valid_in(valid_in),
                                    .clk(clk),
                                    .rst(rst),
                                    .hit(hit),
                                    .dirty(dirty),
                                    .tag_out(tag_out),
                                    .data_out(data_out),
                                    .valid(valid),
                                    .error(error)
                                   );

    // Clock generation
    initial begin
        clk = 0;
        forever #5 clk = ~clk;  // 10 ns period
    end

    // Test procedure
    initial begin
        // Initialize inputs
        enable = 0;
        rst = 1;
        @(posedge clk);
        rst = 0;
        @(posedge clk); 

        // Enable cache
        enable = 1;
        // Pseudo-Random Replacement Condition Checking
        // Fill the cache to trigger replacement
        write_task(8'h02, 3'b000, 5'b00000, 1'b1,1'b1);
        read_task(8'h02, 3'b000, 5'b00000, 1'b1);
        write_task(8'h02, 3'b000, 5'b00001, 1'b1,1'b1);
        read_task(8'h02, 3'b000, 5'b00001, 1'b1);
        write_task(8'h02, 3'b000, 5'b00010, 1'b1,1'b1);
        read_task(8'h02, 3'b000, 5'b00010, 1'b1);
        write_task(8'h02, 3'b000, 5'b00011, 1'b1,1'b1);
        read_task(8'h02, 3'b000, 5'b00011, 1'b1);

        @(posedge clk); 

        // Write to cache without compare
        write_task(8'h00, 3'b000, 5'b00001, 1'b0,1'b1);

	    // Read from cache without compare
        read_task(8'h00, 3'b000, 5'b00001, 1'b0);

        // Write to cache with compare
        write_task(8'h00, 3'b000, 5'b00001, 1'b1,1'b1);

        // Read from cache  with compare
        read_task(8'h00, 3'b000, 5'b00001, 1'b1);

        // Write to cache with out compare 
        write_task(8'h01, 3'b110, 5'b00010, 1'b0,1'b1);

        // Read from cache  with compare
        read_task(8'h01, 3'b110, 5'b00010, 1'b1);

        // Error condition
        write_task(8'h01, 3'b001, 5'b00010, 1'b0,1'b1);      
        @(posedge clk); 
        check_error(3'b001, error);
        // Finalize simulation
        $finish;
    end

    // Task to handle writing to the cache
    task write_task(input [7:0] indx, input [2:0] off, input [4:0] tag, input compr, valid);
        begin
            index = indx;  
            offset = off; 
            tag_in = tag; 
            data_in = $random; 
            valid_in = valid;    
            write = 1'b1;       
            comp = compr; 
            @(posedge clk); 

             // Display after the clock
            $display("\n[WRITE_TASK] @time %0t", $time);
            $display("  -> index=%0d (0x%0h), offset=%0d (0x%0h), tag_in=%b, data_in=%0h",
                      index, index, offset, offset, tag_in, data_in);
            $display("  -> comp=%b, write=%b, valid_in=%b", comp, write, valid_in);
            $display("  -> hit=%b, dirty=%b, tag_out=%b, data_out=%0h, valid=%b, error=%b",
                      hit, dirty, tag_out, data_out, valid, error);
        end
    endtask 

    // Task to handle reading from the cache
    task read_task(input [7:0] indx, input [2:0] off, input [4:0] tag, input compr);
        begin
            index = indx;  
            offset = off; 
            tag_in = tag; 
            comp = compr;
            @(posedge clk);        
            write = 1'b0;
            @(posedge clk);

            $display("\n[READ_TASK] @time %0t", $time);
            $display("  -> index=%0d (0x%0h), offset=%0d (0x%0h), tag_in=%b",
                      index, index, offset, offset, tag_in);
            $display("  -> comp=%b, write=%b", comp, write);
            $display("  -> hit=%b, dirty=%b, tag_out=%b, data_out=%0h, valid=%b, error=%b",
                      hit, dirty, tag_out, data_out, valid, error);


            if (data_in !== data_out)
                $display("  -> [Error] Data mismatch! data_in=%0h, data_out=%0h",
                          data_in, data_out);
            else
                $display("  -> [Pass] Data matched.");
            @(posedge clk); 
        end
    endtask

    task check_error(input [2:0] offset_val, input error_signal);
        begin
            if (offset_val[0]) begin
                if (error_signal)
                    $display("  -> [PASS] ERROR correctly asserted for misaligned offset (offset[0]=1)");
                else
                    $display("  -> [FAIL] ERROR was expected for misaligned offset, but not asserted!");
                end else begin
                if (error_signal)
                    $display("  -> [FAIL] ERROR was unexpectedly asserted on aligned offset (offset[0]=0)");
                else
                    $display("  -> [PASS] No error as expected (aligned offset)");
            end
        end
    endtask

    // Waveform dumping for simulation analysis
    initial begin
        $dumpfile("direct_map_cachet.vcd");    // Specify the VCD file for waveform dumping
        $dumpvars(0, tb_direct_map_cache);     // Dump all variables in the testbench
    end
endmodule

