`timescale 1ns / 1ps

module dual_port_memory_tb;

    // Parameters
    parameter DATA_WIDTH = 4;
    parameter ADDR_WIDTH = 5;

    // Signals
    reg clk;
    reg we_a, we_b;
    reg [ADDR_WIDTH-1:0] addr_a, addr_b;
    reg [DATA_WIDTH-1:0] data_in_a, data_in_b;
    wire [DATA_WIDTH-1:0] data_out_a, data_out_b;

    // Instantiate the dual-port memory module
    dual_port_memory #(
        .DATA_WIDTH(DATA_WIDTH),
        .ADDR_WIDTH(ADDR_WIDTH)
    ) uut (
        .clk(clk),
        .we_a(we_a),
        .we_b(we_b),
        .addr_a(addr_a),
        .addr_b(addr_b),
        .data_in_a(data_in_a),
        .data_in_b(data_in_b),
        .data_out_a(data_out_a),
        .data_out_b(data_out_b)
    );

    // Clock generation
    always #5 clk = ~clk; // 10ns period

    // Monitor for real-time tracking
    initial begin
        $monitor("Time = %0t | we_a=%b addr_a=%d data_in_a=%b data_out_a=%b | we_b=%b addr_b=%d data_in_b=%b data_out_b=%b", 
                 $time, we_a, addr_a, data_in_a, data_out_a, we_b, addr_b, data_in_b, data_out_b);
    end

    initial begin
        // Initialize signals
        clk = 0;
        we_a = 0;
        we_b = 0;
        addr_a = 0;
        addr_b = 0;
        data_in_a = 0;
        data_in_b = 0;

        // Apply test cases
        #10;

        // Write to port A and port B at different addresses
        we_a = 1; addr_a = 5; data_in_a = 4'b1010;
        we_b = 1; addr_b = 10; data_in_b = 4'b1100;
        $display("Time = %0t | Writing 1010 to addr 5 on Port A, Writing 1100 to addr 10 on Port B", $time);
        #10;

        // Disable write enables and read back
        we_a = 0; addr_a = 5;
        we_b = 0; addr_b = 10;
        #10;
        $display("Time = %0t | Reading from addr 5 on Port A: %b, addr 10 on Port B: %b", $time, data_out_a, data_out_b);

        // Check output values
        if (data_out_a !== 4'b1010) $display("Test Failed: Port A Read Mismatch");
        if (data_out_b !== 4'b1100) $display("Test Failed: Port B Read Mismatch");

        // Write to the same address with both ports
        we_a = 1; addr_a = 7; data_in_a = 4'b0110;
        we_b = 1; addr_b = 7; data_in_b = 4'b1001;
        $display("Time = %0t | Writing 0110 to addr 7 on Port A, Writing 1001 to addr 7 on Port B", $time);
        #10;

        // Disable write enables
        we_a = 0; we_b = 0;
        addr_a = 7; addr_b = 7;
        #10;
        $display("Time = %0t | Reading from addr 7 on both ports: Port A: %b, Port B: %b", $time, data_out_a, data_out_b);

        // Check last write dominance (port B writes last)
        if (data_out_a !== 4'b1001 || data_out_b !== 4'b1001) $display("Test Failed: Last Write Dominance Issue");

        // End simulation
        $display("Time = %0t | Test Completed", $time);
        $finish;
    end

endmodule

module dual_port_memory_tb;

    // Parameters
    localparam DATA_WIDTH = 4;
    localparam ECC_WIDTH = 3;
    localparam ADDR_WIDTH = 5;
    localparam MEM_DEPTH = 1 << ADDR_WIDTH;

    // Signals
    reg clk;
    reg rst_n;
    reg we;
    reg [ADDR_WIDTH-1:0] addr_a;
    reg [ADDR_WIDTH-1:0] addr_b;
    reg [DATA_WIDTH-1:0] data_in;
    wire [DATA_WIDTH-1:0] data_out;
    wire ecc_error;

    // DUT instance
    dual_port_memory #(
        .DATA_WIDTH(DATA_WIDTH),
        .ECC_WIDTH(ECC_WIDTH),
        .ADDR_WIDTH(ADDR_WIDTH)
    ) dut (
        .clk(clk),
        .rst_n(rst_n),
        .we(we),
        .addr_a(addr_a),
        .addr_b(addr_b),
        .data_in(data_in),
        .data_out(data_out),
        .ecc_error(ecc_error)
    );

    // Clock generation
    initial clk = 0;
    always #5 clk = ~clk;  // 10ns clock period

    // Test procedure
    initial begin
        $display("==== Starting Dual Port Memory ECC Testbench ====");
        // Init
        rst_n = 0;
        we = 0;
        addr_a = 0;
        addr_b = 0;
        data_in = 0;
        #20;
        rst_n = 1;
        $display("[%0t ns] Reset complete.", $time);

        // === Test 1: Write and Read back ===
        $display("\n=== Test 1: Write and Read back ===");
        addr_a = 5'd3;
        data_in = 4'b1010;
        we = 1;
        $display("[%0t ns] Writing data 0x%0h to addr_a = %0d", $time, data_in, addr_a);
        #10;
        we = 0;

        addr_b = 5'd3;
        $display("[%0t ns] Reading from addr_b = %0d", $time, addr_b);
        #20;
        $display("[%0t ns] Data out = 0x%0h, ECC error = %0b", $time, data_out, ecc_error);
        if (data_out !== 4'b1010 || ecc_error !== 1'b0) begin
            $display(" FAIL: Data mismatch or unexpected ECC error");
        end else begin
            $display(" PASS: Read data OK, ECC OK");
        end

        // === Test 2: Inject ECC error ===
        $display("\n=== Test 2: Inject ECC error manually ===");
        dut.ram_data[3] = 4'b1011;  // Flip one bit in stored data
        $display("[%0t ns] Manually corrupted RAM at address 3: expected 0xA, now = 0x%0h", $time, dut.ram_data[3]);

        #10;
        addr_b = 5'd3;
        $display("[%0t ns] Reading from corrupted addr_b = %0d", $time, addr_b);
        #20;
        $display("[%0t ns] Data out = 0x%0h, ECC error = %0b", $time, data_out, ecc_error);
        if (ecc_error !== 1'b1) begin
            $display(" FAIL: ECC error not detected on corrupted data");
        end else begin
            $display(" PASS: ECC error correctly detected");
        end

        $display("\n==== All tests completed ====");
        $finish;
    end

endmodule

context.rtl/coeff_update.sv:
module coeff_update #(
    parameter TAP_NUM     = 7,
    parameter DATA_WIDTH  = 16,
    parameter COEFF_WIDTH = 16,
    parameter MU          = 15  // Learning rate = 2^(-15)
)(
    input  logic                          clk,
    input  logic                          rst_n,
    input  logic signed [DATA_WIDTH-1:0]  data_real [TAP_NUM-1:0],
    input  logic signed [DATA_WIDTH-1:0]  data_imag [TAP_NUM-1:0],
    input  logic signed [DATA_WIDTH-1:0]  error_real,
    input  logic signed [DATA_WIDTH-1:0]  error_imag,
    output logic signed [COEFF_WIDTH-1:0] coeff_real [TAP_NUM-1:0],
    output logic signed [COEFF_WIDTH-1:0] coeff_imag [TAP_NUM-1:0]
);
    // Internal signals
    logic signed [DATA_WIDTH+COEFF_WIDTH:0] coeff_real_aux [TAP_NUM-1:0];
    logic signed [DATA_WIDTH+COEFF_WIDTH:0] coeff_imag_aux [TAP_NUM-1:0];

    logic signed [DATA_WIDTH+COEFF_WIDTH:0] coeff_real_rnd [TAP_NUM-1:0];
    logic signed [DATA_WIDTH+COEFF_WIDTH:0] coeff_imag_rnd [TAP_NUM-1:0];

    logic signed [COEFF_WIDTH-1:0] coeff_real_acc [TAP_NUM-1:0];
    logic signed [COEFF_WIDTH-1:0] coeff_imag_acc [TAP_NUM-1:0];

    // Update of LMS coefficients
    always_comb begin
        for (int i = 0; i < TAP_NUM; ++i) begin
            coeff_real_aux[i] = (error_real * data_real[i] + error_imag * data_imag[i]);
            coeff_real_rnd[i] = coeff_real_aux[i] <<< 4;
            coeff_real_acc[i] = coeff_real_rnd[i][DATA_WIDTH+COEFF_WIDTH -: DATA_WIDTH] >>> MU;

            coeff_imag_aux[i] = (error_imag * data_real[i] - error_real * data_imag[i]);
            coeff_imag_rnd[i] = coeff_imag_aux[i] <<< 4;
            coeff_imag_acc[i] = coeff_imag_rnd[i][DATA_WIDTH+COEFF_WIDTH -: DATA_WIDTH] >>> MU;
        end
    end

    always_ff @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            for (int i = 0; i < TAP_NUM; i++) begin
                if ((i == (TAP_NUM-1)/2)) begin
                    coeff_real[i] <= 16'h2000; // Initialize central tap to 1 in signed Q2.13
                    coeff_imag[i] <= 16'h0000;
                end else begin
                    coeff_real[i] <= '0;
                    coeff_imag[i] <= '0;
                end
            end
        end else begin
            for (int i = 0; i < TAP_NUM; i++) begin
                coeff_real[i] <= coeff_real[i] + coeff_real_acc[i];
                coeff_imag[i] <= coeff_imag[i] + coeff_imag_acc[i];
            end
        end
    end

endmodule

context.rtl/dynamic_equalizer.sv:
module dynamic_equalizer #(
    parameter TAP_NUM     = 7,
    parameter DATA_WIDTH  = 16,
    parameter COEFF_WIDTH = 16,
    parameter MU          = 15  // Learning rate = 2^(-15)
)(
    input  logic                         clk,
    input  logic                         rst_n,
    input  logic signed [DATA_WIDTH-1:0] data_in_real,
    input  logic signed [DATA_WIDTH-1:0] data_in_imag,
    input  logic signed [DATA_WIDTH-1:0] desired_real,
    input  logic signed [DATA_WIDTH-1:0] desired_imag,
    output logic signed [DATA_WIDTH-1:0] data_out_real,
    output logic signed [DATA_WIDTH-1:0] data_out_imag
);

    // Input storage (shift register)
    logic signed [DATA_WIDTH-1:0] shift_real      [TAP_NUM-1:0];
    logic signed [DATA_WIDTH-1:0] shift_imag      [TAP_NUM-1:0];
    logic signed [DATA_WIDTH-1:0] desired_real_ff [(TAP_NUM-1)/2:0];
    logic signed [DATA_WIDTH-1:0] desired_imag_ff [(TAP_NUM-1)/2:0];

    // Coefficients signals
    logic signed [COEFF_WIDTH-1:0] coeff_real [TAP_NUM-1:0];
    logic signed [COEFF_WIDTH-1:0] coeff_imag [TAP_NUM-1:0];

    // Saída temporária
    logic signed [DATA_WIDTH+COEFF_WIDTH:0] acc_real_rnd;
    logic signed [DATA_WIDTH+COEFF_WIDTH:0] acc_imag_rnd;

    logic signed [DATA_WIDTH+COEFF_WIDTH:0] acc_real;
    logic signed [DATA_WIDTH+COEFF_WIDTH:0] acc_imag;

    // Error
    logic signed [DATA_WIDTH-1:0] error_real;
    logic signed [DATA_WIDTH-1:0] error_imag;

    // Shift register
    always_ff @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            for (int i = 0; i < TAP_NUM; i++) begin
                shift_real[i] <= '0;
                shift_imag[i] <= '0;
            end
        end else begin
            for (int i = TAP_NUM-1; i > 0; i--) begin
                shift_real[i] <= shift_real[i-1];
                shift_imag[i] <= shift_imag[i-1];
            end
            shift_real[0] <= data_in_real;
            shift_imag[0] <= data_in_imag;
        end
    end

    // Synchronizes the desired signal
    always_ff @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            for (int i = 0; i < TAP_NUM; i++) begin
                desired_real_ff[i] <= '0;
                desired_imag_ff[i] <= '0;
            end
        end else begin
            for (int i = TAP_NUM-1; i > 0; i--) begin
                desired_real_ff[i] <= desired_real_ff[i-1];
                desired_imag_ff[i] <= desired_imag_ff[i-1];
            end
            desired_real_ff[0] <= desired_real;
            desired_imag_ff[0] <= desired_imag;
        end
    end

    // Complex sum FIR
    always_comb begin
        acc_real = 0;
        acc_imag = 0;
        for (int i = 0; i < TAP_NUM; i++) begin
            acc_real += (shift_real[i] * coeff_real[i]) - (shift_imag[i] * coeff_imag[i]);
            acc_imag += (shift_real[i] * coeff_imag[i]) + (shift_imag[i] * coeff_real[i]);
        end
    end

    // Round the result
    assign acc_real_rnd = acc_real <<< 4;
    assign acc_imag_rnd = acc_imag <<< 4;

    assign data_out_real = acc_real_rnd[DATA_WIDTH+COEFF_WIDTH -: DATA_WIDTH];
    assign data_out_imag = acc_imag_rnd[DATA_WIDTH+COEFF_WIDTH -: DATA_WIDTH];

    // Error calculation
    error_calc #(
        .DATA_WIDTH (DATA_WIDTH)
    ) uu_error_calc (
        .data_real    (data_out_real                 ),
        .data_imag    (data_out_imag                 ),
        .desired_real (desired_real_ff[(TAP_NUM-1)/2]),
        .desired_imag (desired_imag_ff[(TAP_NUM-1)/2]),
        .error_real   (error_real                    ),
        .error_imag   (error_imag                    )
    );

    // Update of coefficients
    coeff_update #(
        .TAP_NUM     (TAP_NUM    ),
        .DATA_WIDTH  (DATA_WIDTH ),
        .COEFF_WIDTH (COEFF_WIDTH),
        .MU          (MU         )
    ) uu_coeff_update (
        .clk        (clk       ),
        .rst_n      (rst_n     ),
        .data_real  (shift_real),
        .data_imag  (shift_imag),
        .error_real (error_real),
        .error_imag (error_imag),
        .coeff_real (coeff_real),
        .coeff_imag (coeff_imag)
    );

endmodule

context.rtl/error_calc.sv:
module error_calc #(
    parameter DATA_WIDTH = 16
)(
    input  logic signed [DATA_WIDTH-1:0] data_real,
    input  logic signed [DATA_WIDTH-1:0] data_imag,
    input  logic signed [DATA_WIDTH-1:0] desired_real,
    input  logic signed [DATA_WIDTH-1:0] desired_imag,
    output logic signed [DATA_WIDTH-1:0] error_real,
    output logic signed [DATA_WIDTH-1:0] error_imag
);

    // Error calculation using LMS (Least Mean Squares) algorithm
    always_comb begin
            error_real = (desired_real - data_real);
            error_imag = (desired_imag - data_imag);
    end

endmodule

