    logic signed [DATA_WIDTH-1:0] error_imag;

    // Shift register
    always_ff @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            for (int i = 0; i < TAP_NUM; i++) begin
                shift_real[i] <= '0;
                shift_imag[i] <= '0;
            end
        end else begin
            for (int i = TAP_NUM-1; i > 0; i--) begin
                shift_real[i] <= shift_real[i-1];
                shift_imag[i] <= shift_imag[i-1];
            end
            shift_real[0] <= data_in_real;
            shift_imag[0] <= data_in_imag;
        end
    end

    // Complex sum FIR
    always_comb begin
        acc_real = 0;
        acc_imag = 0;
        for (int i = 0; i < TAP_NUM; i++) begin
            acc_real += (shift_real[i] * coeff_real[i]) - (shift_imag[i] * coeff_imag[i]);
            acc_imag += (shift_real[i] * coeff_imag[i]) + (shift_imag[i] * coeff_real[i]);
        end
    end

    // Round the result
    assign acc_real_rnd = acc_real <<< 4;
    assign acc_imag_rnd = acc_imag <<< 4;

    assign data_out_real = acc_real_rnd[DATA_WIDTH+COEFF_WIDTH -: DATA_WIDTH];
    assign data_out_imag = acc_imag_rnd[DATA_WIDTH+COEFF_WIDTH -: DATA_WIDTH];

    // Error calculation
    error_calc #(
        .DATA_WIDTH (DATA_WIDTH)
    ) uu_error_calc (
        .data_real    (data_out_real),
        .data_imag    (data_out_imag),
        .error_real   (error_real   ),
        .error_imag   (error_imag   )
    );

    // Update of coefficients
    coeff_update #(
        .TAP_NUM     (TAP_NUM    ),
        .DATA_WIDTH  (DATA_WIDTH ),
        .COEFF_WIDTH (COEFF_WIDTH),
        .MU          (MU         )
    ) uu_coeff_update (
        .clk        (clk       ),
        .rst_n      (rst_n     ),
        .data_real  (shift_real),
        .data_imag  (shift_imag),
        .error_real (error_real),
        .error_imag (error_imag),
        .coeff_real (coeff_real),
        .coeff_imag (coeff_imag)
    );
endmodule

context.rtl/error_calc.sv:
module error_calc #(
    parameter DATA_WIDTH = 16
)(
    input  logic signed [DATA_WIDTH-1:0] data_real,
    input  logic signed [DATA_WIDTH-1:0] data_imag,
    input  logic signed [DATA_WIDTH-1:0] desired_real,
    input  logic signed [DATA_WIDTH-1:0] desired_imag,
    output logic signed [DATA_WIDTH-1:0] error_real,
    output logic signed [DATA_WIDTH-1:0] error_imag
);
    // Local parameters
    localparam DATA_SQ    = DATA_WIDTH * 2;
    localparam DATA_RAD   = DATA_SQ + 1;
    localparam ERROR_FULL = DATA_WIDTH + DATA_RAD;

    // Internal signals
    logic signed [DATA_SQ-1:0] data_real_sq;
    logic signed [DATA_SQ-1:0] data_imag_sq;

    logic signed [DATA_RAD-1:0] data_real_rad;
    logic signed [DATA_RAD-1:0] data_imag_rad;

    logic signed [ERROR_FULL-1:0] error_real_full;
    logic signed [ERROR_FULL-1:0] error_imag_full;

    logic signed [ERROR_FULL-1:0] error_real_rnd;
    logic signed [ERROR_FULL-1:0] error_imag_rnd;

    // Error calculation using MCMA (Multimodulus Constant Modulus Algorithm) algorithm
    always_comb begin
        data_real_sq    = data_real * data_real;
        data_real_rad   = data_real_sq - 32'h04000000;
        error_real_full = data_real_rad * data_real;
        error_real_rnd  = error_real_full <<< 6;

        data_imag_sq    = data_imag * data_imag;
        data_imag_rad   = data_imag_sq - 32'h04000000;
        error_imag_full = data_imag_rad * data_imag;
        error_imag_rnd  = error_imag_full <<< 6;

        error_real = error_real_rnd[(ERROR_FULL - 1) -: DATA_WIDTH];
        error_imag = error_imag_rnd[(ERROR_FULL - 1) -: DATA_WIDTH];
    end

endmodule

context.rtl/elevator_control_system.sv:
/*
 * Elevator Control System
 * 
 * This module implements an FSM-based elevator control system capable of managing multiple floors,
 * handling call requests, and responding to emergency stops. The elevator transitions between 
 * five main states: Idle, Moving Up, Moving Down, Emergency Halt, Door Open. It prioritizes floor requests 
 * based on direction, moving to the highest or lowest requested floor depending on the current direction.
*/
module elevator_control_system #(
    parameter N = 8, //Number of floors
    parameter DOOR_OPEN_TIME_MS = 500 // Door open time in milliseconds
) ( 
    input wire clk,                   // 100MHz clock input
    input wire reset,                 // Active-high reset signal
    input wire [N-1:0] call_requests, // External Floor call requests
    input wire emergency_stop,        // Emergency stop signal
    output wire [$clog2(N)-1:0] current_floor, // Current floor of the elevator
    output reg direction,             // Elevator direction: 1 = up, 0 = down
    output reg door_open,             // Door open signal
    output reg [2:0] system_status    // Elevator system state indicator
);

    typedef enum logic [2:0] {
        IDLE           = 3'b000,
        MOVING_UP      = 3'b001,
        MOVING_DOWN    = 3'b010,
        EMERGENCY_HALT = 3'b011,
        DOOR_OPEN   = 3'b100
    } state_t;

    state_t state, next_state;

// Internal registers
reg [N-1:0] call_requests_internal;   // Internal copy of call requests
reg [$clog2(N)-1:0] max_request;     // Highest requested floor
reg [$clog2(N)-1:0] min_request;    // Lowest requested floor


// Door open time configuration
`ifdef SIMULATION
    localparam CLK_FREQ_MHZ = 100;  // Clock frequency in MHz
    localparam SIM_DOOR_OPEN_TIME_MS = 0.05; // Shorter door open time for simulation
    localparam DOOR_OPEN_CYCLES = (SIM_DOOR_OPEN_TIME_MS * CLK_FREQ_MHZ * 1000); // Door open cycles for simulation   
`else
    // Calculating door open cycles based on time and clock frequency
    localparam CLK_FREQ_MHZ = 100;  // Clock frequency in MHz
    localparam DOOR_OPEN_CYCLES = (DOOR_OPEN_TIME_MS * CLK_FREQ_MHZ * 1000);   // Door open cycles for real implementation
`endif


reg [$clog2(DOOR_OPEN_CYCLES)-1:0] door_open_counter;   // Counter for door open duration


reg [$clog2(N)-1:0] current_floor_reg, current_floor_next=0;

assign current_floor = current_floor_reg;

// FSM state transition
always_ff @(posedge clk or posedge reset) begin
    if(reset)begin
        state <= IDLE;
        system_status <= IDLE;
        current_floor_reg <= 0;
        max_request <= 0;
        min_request <= N-1;        
    end else begin
        state <= next_state;
        system_status <= next_state;
        current_floor_reg <= current_floor_next;

        // Calculate max_request and min_request based on active requests
        max_request = 0;
        min_request = N-1;
        for (integer i = 0; i < N; i = i + 1) begin
            if (call_requests_internal[i]) begin
                if (i > max_request) max_request = i;
                if (i < min_request) min_request = i;
            end
        end
    end
end

    always_comb begin
    next_state = state;
    current_floor_next = current_floor_reg;

    case(state)
        IDLE:begin
            if(emergency_stop)begin
                next_state = EMERGENCY_HALT;
            end else if(call_requests_internal != 0)begin
                if(max_request > current_floor_reg)begin
                    next_state = MOVING_UP;
                end else if(min_request < current_floor_reg) begin
                    next_state = MOVING_DOWN;
                end
            end
        end

        MOVING_UP: begin
            if(emergency_stop)begin
                next_state = EMERGENCY_HALT;
            end else if(call_requests_internal[current_floor_reg+1]) begin
                current_floor_next = current_floor_reg + 1;
                next_state = DOOR_OPEN;
            end else if(current_floor_reg >= max_request) begin
                // If we reach the highest request, go idle
                next_state = IDLE;
            end else begin
                current_floor_next = current_floor_reg + 1;
                next_state = MOVING_UP;
            end
        end

        MOVING_DOWN: begin
            if(emergency_stop)begin
                next_state = EMERGENCY_HALT;
            end else if(call_requests_internal[current_floor_reg-1]) begin
                current_floor_next = current_floor_reg - 1;
                next_state = DOOR_OPEN;
            end else if(current_floor_reg <= min_request) begin
                // If we reach the lowest request, go idle
                next_state = IDLE;
            end else begin
                current_floor_next = current_floor_reg - 1;
                next_state = MOVING_DOWN;
            end
        end

        EMERGENCY_HALT: begin
            if (!emergency_stop) begin
                next_state = IDLE;
                current_floor_next = 0; // Optionally reset to ground floor
            end
        end
        DOOR_OPEN: begin
            if (door_open_counter == 0) begin
                next_state = IDLE;
            end else begin
                next_state = DOOR_OPEN;
            end
        end
    endcase
end


// Door open control logic
always_ff @(posedge clk or posedge reset) begin
    if (reset) begin
        door_open_counter <= 0;
        door_open <= 0;
    end else begin
        if (state == DOOR_OPEN) begin
            if (door_open_counter > 0) begin
                door_open <= 1;
                door_open_counter <= door_open_counter - 1;
            end else begin
                door_open <= 0;
                next_state = IDLE;
            end
        end else begin
            door_open <= 0;
            door_open_counter <= DOOR_OPEN_CYCLES; // Reset door open counter
        end
    end
end

// Call request management
    always_comb begin
    if(reset) begin
        call_requests_internal = 0;
    end else begin
        if(call_requests_internal[current_floor_reg])begin
            call_requests_internal[current_floor_reg] = 0;      // Clear served request
        end
        call_requests_internal = call_requests_internal | call_requests;    // Update requests
    end
end

// Direction control logic
    always_comb begin
        if (reset) direction = 1;
        else if (state == MOVING_UP) direction = 1;
        else if (state == MOVING_DOWN) direction = 0;
        else direction = 1;
    end

endmodule

context.rtl/ethernet_fifo_cdc.sv:
// FIFO with separate read/write clocks, sized to hold full 1518-byte Ethernet frame
// 32-bit data width (4 bytes), so need at least 380 entries (1518 / 4)

module ethernet_fifo_cdc #(
    parameter WIDTH = 38,
    parameter DEPTH = 512,
    parameter ADDR_WIDTH = $clog2(DEPTH)
) (
    input                   wr_clk_i,       // FIFO write clock (MII domain)
    input                   wr_rst_i,       // FIFO write reset
    input                   wr_push_i,      // Write enable signal
    input  [WIDTH-1:0]      wr_data_i,      // Input data to FIFO
    output                  wr_full_o,      // FIFO full indicator

    input                   rd_clk_i,       // FIFO read clock (AXI domain)
    input                   rd_rst_i,       // FIFO read reset
    input                   rd_pop_i,       // Read enable signal
    output [WIDTH-1:0]      rd_data_o,      // Output data from FIFO
    output                  rd_empty_o      // FIFO empty indicator
);

    // Memory
    reg [WIDTH-1:0] mem [0:DEPTH-1];

    // Write side
    reg [ADDR_WIDTH:0] wr_ptr_q,wr_bin_q;
    wire [ADDR_WIDTH-1:0] wr_addr_w = wr_bin_q[ADDR_WIDTH-1:0];
    wire [ADDR_WIDTH:0] wr_ptr_next_w = wr_bin_q + 1'b1;
    integer i;
    wire [ADDR_WIDTH:0] wgray_next;   // Next write pointer in gray and binary code
    assign wgray_next = (wr_ptr_next_w>>1) ^ wr_ptr_next_w;    // Convert binary to gray code

    always @(posedge wr_clk_i or posedge wr_rst_i) begin
	if (wr_rst_i) begin
            wr_ptr_q <= 0;
            wr_bin_q <= 0;
            for (i = 0; i < DEPTH; i = i + 1)
                mem[i] <= {WIDTH{1'b0}};
        end
        else if (wr_push_i && !wr_full_o) begin
            mem[wr_addr_w] <= wr_data_i;
            {wr_bin_q, wr_ptr_q} <= {wr_ptr_next_w, wgray_next}; // assign memory address in binary and pointer in gray
        end
    end

    // Read side
    reg [ADDR_WIDTH:0] rd_ptr_q,rd_bin_q;
    wire [ADDR_WIDTH-1:0] rd_addr_w = rd_bin_q[ADDR_WIDTH-1:0];
    wire [ADDR_WIDTH:0] rd_ptr_next_w = rd_bin_q + 1'b1;
    wire [ADDR_WIDTH:0] rgray_next;

    assign rgray_next = (rd_bin_q>>1) ^ rd_bin_q;     // Convert binary to gray code

    reg [WIDTH-1:0] rd_data_r;
    always @(posedge rd_clk_i or posedge rd_rst_i) begin
        if (rd_rst_i) begin
            rd_ptr_q <= 0;
            rd_bin_q <= 0;
            rd_data_r <= 0;
        end else if (rd_pop_i && !rd_empty_o) begin
            rd_data_r <= mem[rd_addr_w];
            {rd_bin_q, rd_ptr_q} <= {rd_ptr_next_w, rgray_next}; // assign memory address in binary and pointer in gray
        end
    end
    assign rd_data_o = rd_data_r;

    // Cross-domain pointer sync
    reg [ADDR_WIDTH:0] wr_ptr_rdclk_1, wr_ptr_rdclk_2;
    reg [ADDR_WIDTH:0] rd_ptr_wrclk_1, rd_ptr_wrclk_2;

    always @(posedge rd_clk_i or posedge rd_rst_i) begin
        if (rd_rst_i) begin
            wr_ptr_rdclk_1 <= 0;
            wr_ptr_rdclk_2 <= 0;
        end else begin
            wr_ptr_rdclk_1 <= wr_ptr_q;
            wr_ptr_rdclk_2 <= wr_ptr_rdclk_1;
        end
    end

    always @(posedge wr_clk_i or posedge wr_rst_i) begin
        if (wr_rst_i) begin
            rd_ptr_wrclk_1 <= 0;
            rd_ptr_wrclk_2 <= 0;
        end else begin
            rd_ptr_wrclk_1 <= rd_ptr_q;
            rd_ptr_wrclk_2 <= rd_ptr_wrclk_1;
        end
    end

    // Full & empty detection
    assign wr_full_o = (wgray_next == {~rd_ptr_wrclk_2[ADDR_WIDTH:ADDR_WIDTH-1], rd_ptr_wrclk_2[ADDR_WIDTH-2:0]});
    assign rd_empty_o = (rgray_next == wr_ptr_rdclk_2);

endmodule

context.verif/event_scheduler_tb.sv:
`timescale 1ns/1ps
module event_scheduler_tb;


    reg clk;
    reg reset;
    reg add_event;
    reg cancel_event;
    reg [3:0] event_id;
    reg [15:0] timestamp;
    reg [3:0] priority_in;
    reg [3:0] trig_id;
    reg [15:0] trig_time;
    reg [15:0] future_time;
    wire event_triggered;
    wire [3:0] triggered_event_id;
    wire error;
    wire [15:0] current_time;


    event_scheduler dut (
        .clk(clk),
        .reset(reset),
        .add_event(add_event),
        .cancel_event(cancel_event),
        .event_id(event_id),
        .timestamp(timestamp),
        .priority_in(priority_in),
        .event_triggered(event_triggered),
        .triggered_event_id(triggered_event_id),
        .error(error),
        .current_time(current_time)
    );


    initial begin
        clk = 0;
        forever #5 clk = ~clk;
    end


    task wait_clock;
        @(posedge clk);
    endtask


    task wait_for_trigger(output [3:0] trig_id, output [15:0] trig_time);
        begin

            while (event_triggered !== 1) begin
                wait_clock;
            end
            trig_id = triggered_event_id;
            trig_time = current_time;

            wait_clock;
        end
    endtask

    initial begin

        reset = 1;
        add_event = 0;
        cancel_event = 0;
        event_id = 0;
        timestamp = 0;
        priority_in = 0;


        repeat (2) wait_clock;
        reset = 0;

        wait_clock;
        add_event = 1;
        event_id = 4;
        timestamp = 16'd20;
        priority_in = 4'd2;
        wait_clock; 
        add_event = 0;

        wait_for_trigger(trig_id, trig_time);
        if (trig_id == 4)
            $display("Test Case 1 Passed: Event 4 triggered at time %0d ns", trig_time);
        else
            $display("Test Case 1 Failed: Expected event 4 trigger, got %0d at time %0d ns", trig_id, trig_time);


        wait_clock;
        future_time = current_time + 40;

        add_event = 1;
        event_id = 5;
        timestamp = future_time;
        priority_in = 4'd3;
        wait_clock;
        add_event = 0;


        wait_clock;
        add_event = 1;
        event_id = 6;
        timestamp = future_time;
        priority_in = 4'd1;
        wait_clock;
        add_event = 0;


        while (current_time < future_time)
            wait_clock;
        wait_for_trigger(trig_id, trig_time);
        if (trig_id == 5)
            $display("Test Case 2 Passed: Event 5 (priority 3) triggered over Event 6 at time %0d ns", trig_time);
        else
            $display("Test Case 2 Failed: Incorrect event triggered (got %0d) at time %0d ns", trig_id, trig_time);


        wait_clock;
        add_event = 1;
        event_id = 7;
        timestamp = current_time + 20;
        priority_in = 4'd2;
        wait_clock;
        add_event = 0;

        wait_clock;
        cancel_event = 1;
        event_id = 7;
        wait_clock;
        cancel_event = 0;

        repeat (4) wait_clock;
        if (event_triggered && (triggered_event_id == 7))
            $display("Test Case 3 Failed: Event 7 triggered despite cancellation at time %0d ns", current_time);
        else
            $display("Test Case 3 Passed: Event 7 cancelled successfully (no trigger) at time %0d ns", current_time);

        #50;
        $finish;
    end

    initial begin
        $dumpfile("event_scheduler.vcd");
        $dumpvars(0, event_scheduler_tb);
    end

endmodule

context.rtl/event_scheduler.sv:
module event_scheduler(
    input clk,
    input reset,
    input add_event,
    input cancel_event,
    input [3:0] event_id,
    input [15:0] timestamp,
    input [3:0] priority_in,
    output reg event_triggered,
    output reg [3:0] triggered_event_id,
    output reg error,
    output reg [15:0] current_time
);


    reg [15:0] event_timestamps [15:0];
    reg [3:0]  event_priorities [15:0];
    reg        event_valid      [15:0];
    reg [15:0] tmp_current_time;
    reg [15:0] tmp_event_timestamps [15:0];
    reg [3:0]  tmp_event_priorities [15:0];
    reg        tmp_event_valid [15:0];
    integer i, j;
    integer chosen_event;

    always @(posedge clk or posedge reset) begin
        if (reset) begin
            current_time <= 0;
            event_triggered <= 0;
            triggered_event_id <= 0;
            error <= 0;
            for (i = 0; i < 16; i = i + 1) begin
                event_timestamps[i] <= 0;
                event_priorities[i] <= 0;
                event_valid[i] <= 0;
            end
        end else begin
            tmp_current_time = current_time + 10;
            for (j = 0; j < 16; j = j + 1) begin
                tmp_event_timestamps[j] = event_timestamps[j];
                tmp_event_priorities[j] = event_priorities[j];
                tmp_event_valid[j] = event_valid[j];
            end

            if (add_event) begin
                if (tmp_event_valid[event_id]) begin
                    error <= 1; 
                end else begin
                    tmp_event_timestamps[event_id] = timestamp;
                    tmp_event_priorities[event_id] = priority_in;
                    tmp_event_valid[event_id] = 1;
                    error <= 0;
                end
            end

            if (cancel_event) begin
                if (tmp_event_valid[event_id]) begin
                    tmp_event_valid[event_id] = 0;
                    error <= 0;
                end else begin
                    error <= 1; 
                end
            end

            chosen_event = -1;
            for (j = 0; j < 16; j = j + 1) begin
                if (tmp_event_valid[j] && (tmp_event_timestamps[j] <= tmp_current_time)) begin
                    if ((chosen_event == -1) || (tmp_event_priorities[j] > tmp_event_priorities[chosen_event])) begin
                        chosen_event = j;
                    end
                end
            end

            if (chosen_event != -1) begin
                event_triggered <= 1;
                triggered_event_id <= chosen_event;
                tmp_event_valid[chosen_event] = 0;
            end else begin
                event_triggered <= 0;
            end

            current_time <= tmp_current_time;
            for (j = 0; j < 16; j = j + 1) begin
                event_timestamps[j] <= tmp_event_timestamps[j];
                event_priorities[j] <= tmp_event_priorities[j];
                event_valid[j] <= tmp_event_valid[j];
            end
        end
    end

endmodule

context.verif/tb_event_scheduler.sv:
`timescale 1ns/1ps

module tb_event_scheduler;


  reg clk;
  reg reset;
  reg add_event;
  reg cancel_event;
  reg [3:0] event_id;
  reg [15:0] timestamp;
  reg [3:0] priority_in;
  reg modify_event;
  reg [15:0] new_timestamp;
  reg [3:0] new_priority;
  reg recurring_event;
  reg [15:0] recurring_interval;
  wire event_triggered;
  wire [3:0] triggered_event_id;
  wire error;
  wire [15:0] current_time;
  wire [15:0] log_event_time;
  wire [3:0] log_event_id;


  event_scheduler dut (
    .clk(clk),
    .reset(reset),
    .add_event(add_event),
    .cancel_event(cancel_event),
    .event_id(event_id),
    .timestamp(timestamp),
    .priority_in(priority_in),
    .modify_event(modify_event),
    .new_timestamp(new_timestamp),
    .new_priority(new_priority),
    .recurring_event(recurring_event),
    .recurring_interval(recurring_interval),
    .event_triggered(event_triggered),
    .triggered_event_id(triggered_event_id),
    .error(error),
    .current_time(current_time),
    .log_event_time(log_event_time),
    .log_event_id(log_event_id)
  );


  initial begin
    clk = 0;
    forever #5 clk = ~clk;
  end


  task clear_signals;
    begin
      add_event         = 0;
      cancel_event      = 0;
      modify_event      = 0;
      event_id          = 4'b0;
      timestamp         = 16'b0;
      priority_in       = 4'b0;
      new_timestamp     = 16'b0;
      new_priority      = 4'b0;
      recurring_event   = 0;
      recurring_interval= 16'b0;
    end
  endtask


  task do_reset;
    begin
      clear_signals;
      reset = 1;
      #12;  
      reset = 0;
      #10;  
    end
  endtask


  task wait_for_trigger;
    begin
      wait (event_triggered == 1);
      #1; 
    end
  endtask


  initial begin
    $display("Starting Modified Testbench with Correct Sampling...");


    do_reset();
    $display("\nTC1: Adding event ID=1 with timestamp=30, priority=2");
    clear_signals;
    event_id    = 4'd1;
    timestamp   = 16'd30;
    priority_in = 4'd2;
    add_event   = 1;
    #10;
    clear_signals;
    wait_for_trigger();
    $display("TC1 Outputs: event_triggered=%0d, triggered_event_id=%0d, error=%0d, current_time=%0d, log_event_time=%0d, log_event_id=%0d",
             event_triggered, triggered_event_id, error, current_time, log_event_time, log_event_id);


    do_reset();
    $display("\nTC2: Adding event ID=2 then canceling it");
    clear_signals;
    event_id    = 4'd2;
    timestamp   = 16'd50;
    priority_in = 4'd3;
    add_event   = 1;
    #10;
    clear_signals;
    event_id    = 4'd2;
    cancel_event= 1;
    #10;
    clear_signals;
    repeat (5) #10;
    $display("TC2 Outputs: event_triggered=%0d, triggered_event_id=%0d, error=%0d, current_time=%0d, log_event_time=%0d, log_event_id=%0d",
             event_triggered, triggered_event_id, error, current_time, log_event_time, log_event_id);


    do_reset();
    $display("\nTC3: Adding event ID=3 then modifying its timestamp and priority");
