module gcd_datapath_6  #(
   parameter WIDTH = 4
)(
   input                     clk,
   input                     rst,
   input      [WIDTH-1:0]    A,
   input      [WIDTH-1:0]    B,
   input      [1:0]          controlpath_state,
   output logic              equal,
   output logic [WIDTH-1:0]  OUT
);
   logic [WIDTH-1:0] A_ff, B_ff, OUT_ff;
   logic [$clog2(WIDTH+1):0] k_ff;

   logic [WIDTH-1:0] next_A_ff, next_B_ff, next_OUT;
   logic [$clog2(WIDTH+1):0] next_k_ff;

   logic [WIDTH-1:0] diff;
   logic [WIDTH-1:0] gcd_val;
   logic both_even, a_even, b_even;

   localparam S0 = 2'd0;
   localparam S1 = 2'd1;
   localparam S2 = 2'd2;

   always_comb begin
      next_A_ff = A_ff;
      next_B_ff = B_ff;
      next_k_ff = k_ff;
      next_OUT  = OUT_ff;
      gcd_val   = OUT_ff;
      diff      = 'b0;

      a_even    = (A_ff[0] == 1'b0);
      b_even    = (B_ff[0] == 1'b0);
      both_even = a_even && b_even;
      equal     = (A_ff == B_ff);

      case (controlpath_state)
         S0: begin
            next_A_ff = A;
            next_B_ff = B;
            next_k_ff = 'b0;
            next_OUT  = 'b0;
         end

         S1: begin
            if (A_ff == 0 && B_ff == 0) begin
               gcd_val = 0;
            end else if (A_ff == 0) begin
               gcd_val = (B_ff << k_ff);
            end else begin
               gcd_val = (A_ff << k_ff);
            end
            next_OUT = gcd_val;
         end

         S2: begin
            if ((A_ff != 0) && (B_ff != 0)) begin
               if (both_even) begin
                  next_A_ff = A_ff >> 1;
                  next_B_ff = B_ff >> 1;
                  next_k_ff = k_ff + 1;
               end else if (a_even && !b_even) begin
                  next_A_ff = A_ff >> 1;
               end else if (b_even && !a_even) begin
                  next_B_ff = B_ff >> 1;
               end else begin
                  if (A_ff >= B_ff) begin
                     diff = A_ff - B_ff;
                     next_A_ff = diff >> 1;
                     next_B_ff = B_ff;
                  end else begin
                     diff = B_ff - A_ff;
                     next_B_ff = diff >> 1;
                     next_A_ff = A_ff;
                  end
               end
            end else if (A_ff == 0 && B_ff != 0) begin
               next_A_ff = B_ff;
               next_B_ff = B_ff;
            end else if (B_ff == 0 && A_ff != 0) begin
               next_B_ff = A_ff;
               next_A_ff = A_ff;
            end
         end

         default: begin
            next_A_ff = 'b0;
            next_B_ff = 'b0;
            next_k_ff = 'b0;
            next_OUT  = 'b0;
         end
      endcase
   end

   always_ff @(posedge clk) begin
      if (rst) begin
         A_ff  <= 'b0;
         B_ff  <= 'b0;
         k_ff  <= 'b0;
         OUT_ff <= 'b0;
      end else begin
         A_ff  <= next_A_ff;
         B_ff  <= next_B_ff;
         k_ff  <= next_k_ff;
         OUT_ff <= next_OUT;
      end
   end

   assign OUT = OUT_ff;

endmodule

context.rtl/modular_exponentiation.sv:
`timescale 1ns/1ps
//-----------------------------------------------------------------------------
// modular_exponentiation module: Computes (base^exponent) mod mod_val using the
// square-and-multiply algorithm.
// This module reuses the modular_multiplier module for multiplication operations.
//-----------------------------------------------------------------------------
module modular_exponentiation #(
    parameter WIDTH = 8
)(
    input                     clk,
    input                     rst,
    input                     start,
    input      [WIDTH-1:0]    base,
    input      [WIDTH-1:0]    exponent,
    input      [WIDTH-1:0]    mod_val,
    output reg [WIDTH-1:0]    result,
    output reg                done
);
    // FSM state definitions.
    localparam STATE_IDLE        = 0;
    localparam STATE_INIT        = 1;
    localparam STATE_CHECK       = 2;
    localparam STATE_WAIT_RESULT = 3;
    localparam STATE_MULT_BASE   = 4;
    localparam STATE_WAIT_BASE   = 5;
    localparam STATE_SHIFT       = 6;
    localparam STATE_DONE        = 7;

    reg [3:0]       state;
    reg [WIDTH-1:0] res_reg;
    reg [WIDTH-1:0] base_reg;
    reg [WIDTH-1:0] exp_reg;

    // Signals for the modular multiplier instance.
    reg mult_start;
    reg [WIDTH-1:0] mult_A, mult_B, mult_mod;
    wire [WIDTH-1:0] mult_result;
    wire mult_done;

    // Instantiate the modular_multiplier.
    modular_multiplier #(
        .WIDTH(WIDTH)
    ) mod_mult_inst (
        .clk(clk),
        .rst(rst),
        .start(mult_start),
        .A(mult_A),
        .B(mult_B),
        .mod_val(mult_mod),
        .result(mult_result),
        .done(mult_done)
    );

    always @(posedge clk) begin
        if(rst) begin
            state      <= STATE_IDLE;
            res_reg    <= 0;
            base_reg   <= 0;
            exp_reg    <= 0;
            result     <= 0;
            done       <= 0;
            mult_start <= 0;
        end else begin
            case(state)
                STATE_IDLE: begin
                    done <= 0;
                    if(start)
                        state <= STATE_INIT;
                end
                STATE_INIT: begin
                    res_reg  <= 1;                // Initialize result to 1.
                    base_reg <= base % mod_val;    // Reduce base modulo mod_val.
                    exp_reg  <= exponent;
                    state    <= STATE_CHECK;
                end
                STATE_CHECK: begin
                    if(exp_reg == 0) begin
                        if(base_reg == 0 & mod_val==1)
                            result <= 0;
                        else
                            result <= res_reg;
                        state  <= STATE_DONE;
                    end else begin
                        if(exp_reg[0] == 1'b1) begin
                            mult_A   <= base_reg;
                            mult_B   <= base_reg;
                            mult_mod <= mod_val;
                            mult_start <= 1;
                            state <= STATE_WAIT_RESULT;
                        end else begin
                            state <= STATE_MULT_BASE;
                        end
                    end
                end
                STATE_WAIT_RESULT: begin
                    mult_start <= 0;
                    if(mult_done) begin
                        res_reg <= mult_result;
                        state <= STATE_MULT_BASE;
                    end
                end
                STATE_MULT_BASE: begin
                    mult_A   <= base_reg;
                    mult_B   <= res_reg;
                    mult_mod <= mod_val;
                    mult_start <= 1;
                    state <= STATE_WAIT_BASE;
                end
                STATE_WAIT_BASE: begin
                    mult_start <= 0;
                    if(mult_done) begin
                        base_reg <= mult_result;
                        state <= STATE_SHIFT;
                    end
                end
                STATE_SHIFT: begin
                    exp_reg <= exp_reg >> 1; // Shift exponent right.
                    state   <= STATE_CHECK;
                end
                STATE_DONE: begin
                    done <= 1;
                    // Remain in DONE state until start is deasserted.
                    if(!start)
                        state <= STATE_IDLE;
                end
                default: state <= STATE_IDLE;
            endcase
        end
    end
endmodule

context.rtl/modular_multiplier.sv:
`timescale 1ns/1ps
//-----------------------------------------------------------------------------
// modular_multiplier module: Computes (A * B) mod mod_val
// Implements a sequential shift‐and‐add multiplication followed by a
// single modulo operation at the end.
//-----------------------------------------------------------------------------
module modular_multiplier #(
    parameter WIDTH = 8
)(
    input                     clk,
    input                     rst,
    input                     start,
    input      [WIDTH-1:0]    A,
    input      [WIDTH-1:0]    B,
    input      [WIDTH-1:0]    mod_val,
    output reg [WIDTH-1:0]    result,
    output reg                done
);
    // a_reg now stores A extended to 2*WIDTH bits to accommodate shifts.
    reg [2*WIDTH-1:0] a_reg;
    reg [WIDTH-1:0]   b_reg;
    reg [2*WIDTH-1:0] prod;   // Accumulates the full product
    reg [($clog2(WIDTH+1))-1:0] count;  // Iteration counter for WIDTH bits
    reg busy;

    always @(posedge clk) begin
        if(rst) begin
            result  <= 0;
            prod    <= 0;
            a_reg   <= 0;
            b_reg   <= 0;
            count   <= 0;
            busy    <= 0;
            done    <= 0;
        end else begin
            if(start && !busy) begin
                busy    <= 1;
                // Initialize registers.
                // Extend A to 2*WIDTH bits.
                a_reg   <= { {WIDTH{1'b0}}, A };
                b_reg   <= B;
                prod    <= 0;
                count   <= WIDTH;
                done    <= 0;
            end else if(busy) begin
                if(count > 0) begin
                    // If the current LSB of b_reg is 1, add a_reg to the product.
                    if(b_reg[0] == 1'b1)
                        prod <= prod + a_reg;
                    // Shift a_reg left to align with the next bit.
                    a_reg <= a_reg << 1;
                    // Shift b_reg right to process the next bit.
                    b_reg <= b_reg >> 1;
                    count <= count - 1;
                end else begin
                    // Once multiplication is done, perform the modulo operation.
                    result <= prod % mod_val;
                    done   <= 1;
                    busy   <= 0;
                end
            end else begin
                done <= 0;
            end
        end
    end
endmodule

