module low_power_ctrl (
  // Clock/Reset
  input  logic        clk,
  input  logic        reset,

  // Wakeup input
  input  logic        if_wakeup_i,

  // FIFO statuses
  input  logic        wr_fifo_full,
  input  logic        wr_fifo_empty,

  // Write/Read requests
  input  logic        wr_valid_i,
  input  logic        rd_valid_i,

  // Upstream flush interface
  input  logic        wr_done_i,
  output logic        wr_flush_o,

  // Q-channel interface
  input  logic        qreqn_i,
  output logic        qacceptn_o,
  output logic        qactive_o,

  // FIFO push/pop controls
  output logic        wr_fifo_push,
  output logic        wr_fifo_pop
);

  // --------------------------------------------------------
  // Internal signals
  // --------------------------------------------------------
  typedef enum logic [1:0] {
    ST_Q_RUN      = 2'b00,
    ST_Q_REQUEST  = 2'b01,
    ST_Q_STOPPED  = 2'b10,
    ST_Q_EXIT     = 2'b11
  } state_t;

  state_t state_q, nxt_state;

  logic   nxt_qactive;
  logic   qactive_q;

  logic   nxt_qaccept;
  logic   nxt_qacceptn;
  logic   qacceptn_en;
  logic   qacceptn_q;

  // --------------------------------------------------------
  // Gate writes/reads based on FIFO full/empty
  // --------------------------------------------------------
  // The same lines from your original code, but now in the control module:
  assign wr_fifo_push = wr_valid_i & ~wr_fifo_full;
  assign wr_fifo_pop  = rd_valid_i & ~wr_fifo_empty;

  // --------------------------------------------------------
  // QACTIVE signal (same logic as original)
  // --------------------------------------------------------
  // Next-cycle active if the FIFO has data, or a new valid read/write
  assign nxt_qactive = (~wr_fifo_empty) | wr_valid_i | rd_valid_i;

  always_ff @(posedge clk or posedge reset) begin
    if (reset)
      qactive_q <= 1'b0;
    else
      qactive_q <= nxt_qactive;
  end

  assign qactive_o = qactive_q | if_wakeup_i;

  // --------------------------------------------------------
  // State Machine
  // --------------------------------------------------------
  always_comb begin
    nxt_state = state_q;
    case (state_q)
      ST_Q_RUN:
        if (~qreqn_i)
          nxt_state = ST_Q_REQUEST;

      ST_Q_REQUEST:
        // The design goes to ST_Q_STOPPED once we accept => qacceptn=0
        if (~qacceptn_q)
          nxt_state = ST_Q_STOPPED;

      ST_Q_STOPPED:
        // The design goes to ST_Q_EXIT once qreqn_i=1 again
        if (qreqn_i)
          nxt_state = ST_Q_EXIT;

      ST_Q_EXIT:
        // Return to ST_Q_RUN when qacceptn=1
        if (qacceptn_q)
          nxt_state = ST_Q_RUN;
    endcase
  end

  always_ff @(posedge clk or posedge reset) begin
    if (reset)
      state_q <= ST_Q_RUN;
    else
      state_q <= nxt_state;
  end

  // --------------------------------------------------------
  // Flush Control (combinational)
  // --------------------------------------------------------
  // The original requirement: wr_flush_o=1 in ST_Q_REQUEST if wr_done_i=0,
  // then remain high until wr_done_i=1.
  assign wr_flush_o = (state_q == ST_Q_REQUEST) & (~wr_done_i);

  // --------------------------------------------------------
  // QACCEPTn logic
  // --------------------------------------------------------
  // Accept once FIFO is empty + wr_done_i=1 + qreqn_i=0 => qaccept=1 => qacceptn=0
  // Then remain in ST_Q_STOPPED until qreqn_i reasserts => ST_Q_EXIT => eventually qacceptn=1 => ST_Q_RUN
  assign nxt_qaccept  = (wr_done_i & wr_fifo_empty & ~qreqn_i);
  assign nxt_qacceptn = ~nxt_qaccept;

  // Enable capturing qacceptn in ST_Q_REQUEST or ST_Q_EXIT
  assign qacceptn_en = (state_q == ST_Q_REQUEST) | (state_q == ST_Q_EXIT);

  always_ff @(posedge clk or posedge reset) begin
    if (reset)
      qacceptn_q <= 1'b1;
    else if (qacceptn_en)
      qacceptn_q <= nxt_qacceptn;
  end

  assign qacceptn_o = qacceptn_q;

endmodule

context.rtl/sync_fifo.sv:
module sync_fifo #(
  parameter DEPTH   = 8,  // Must be power-of-two for ring-pointer indexing
  parameter DATA_W  = 8
)(
  input  wire              clk,
  input  wire              reset,

  input  wire              push_i,
  input  wire [DATA_W-1:0] push_data_i,

  input  wire              pop_i,
  output wire [DATA_W-1:0] pop_data_o,

  output wire              full_o,
  output wire              empty_o
);

  localparam PTR_W = $clog2(DEPTH);

  logic [PTR_W:0] rd_ptr_q, nxt_rd_ptr;
  logic [PTR_W:0] wr_ptr_q, nxt_wr_ptr;

  // Memory array of size DEPTH=8
  logic [DATA_W-1:0] fifo_mem [0:DEPTH-1];
  logic [DATA_W-1:0] fifo_pop_data;

  assign pop_data_o = fifo_pop_data;

  // Pointer flops
  always_ff @(posedge clk or posedge reset) begin
    if (reset) begin
      rd_ptr_q <= '0;
      wr_ptr_q <= '0;
    end else begin
      rd_ptr_q <= nxt_rd_ptr;
      wr_ptr_q <= nxt_wr_ptr;
    end
  end

  // Next-state logic for pointers
  always_comb begin
    // Default no movement
    nxt_rd_ptr    = rd_ptr_q;
    nxt_wr_ptr    = wr_ptr_q;
    fifo_pop_data = fifo_mem[rd_ptr_q[PTR_W-1:0]];

    case ({pop_i, push_i})
      2'b01: // PUSH
        nxt_wr_ptr = wr_ptr_q + 1;
      2'b10: // POP
        nxt_rd_ptr = rd_ptr_q + 1;
      2'b11: // PUSH + POP
        begin
          nxt_wr_ptr = wr_ptr_q + 1;
          nxt_rd_ptr = rd_ptr_q + 1;
        end
      default: /* 2'b00 */ ;
    endcase
  end

  // Write memory
  always_ff @(posedge clk) begin
    if (push_i) begin
      fifo_mem[wr_ptr_q[PTR_W-1:0]] <= push_data_i;
    end
  end

  // Empty/Full checks
  assign empty_o = (wr_ptr_q == rd_ptr_q);
  assign full_o  = (wr_ptr_q[PTR_W]  != rd_ptr_q[PTR_W]) &&
                   (wr_ptr_q[PTR_W-1:0] == rd_ptr_q[PTR_W-1:0]);

endmodule

context.verif/tb_low_power_channel.sv:
`timescale 1ns / 1ps

module tb_low_power_channel;

  // -------------------------------------------------------------------
  // DUT Interface Signals
  // -------------------------------------------------------------------
  logic         clk;
  logic         reset;

  // DUT inputs
  logic         if_wakeup_i;
  logic         wr_valid_i;
  logic [7:0]   wr_payload_i;
  logic         wr_done_i;
  logic         rd_valid_i;
  logic         qreqn_i;

  // DUT outputs
  wire          wr_flush_o;
  wire [7:0]    rd_payload_o;
  wire          qacceptn_o;
  wire          qactive_o;

  // -------------------------------------------------------------------
  // DUT Instantiation
  // -------------------------------------------------------------------
  low_power_channel dut (
    .clk          (clk),
    .reset        (reset),
    .if_wakeup_i  (if_wakeup_i),
    .wr_valid_i   (wr_valid_i),
    .wr_payload_i (wr_payload_i),
    .wr_flush_o   (wr_flush_o),
    .wr_done_i    (wr_done_i),
    .rd_valid_i   (rd_valid_i),
    .rd_payload_o (rd_payload_o),
    .qreqn_i      (qreqn_i),
    .qacceptn_o   (qacceptn_o),
    .qactive_o    (qactive_o)
  );

  // -------------------------------------------------------------------
  // Clock Generation
  // -------------------------------------------------------------------
  always #5 clk = ~clk;

  // -------------------------------------------------------------------
  // Scoreboard / Tracking
  // -------------------------------------------------------------------
  // We will store written data in a queue and compare with read data
  // to ensure correctness.
  typedef bit [7:0] data_t;
  data_t write_queue[$];
  data_t read_data;

  // Track errors
  integer error_count = 0;

  // Simple mechanism to log errors
  task report_error(string msg);
    begin
      error_count++;
      $display("[ERROR] %s at time %0t", msg, $time);
    end
  endtask

  // -------------------------------------------------------------------
  // Initialization & Reset
  // -------------------------------------------------------------------
  initial begin
    clk          = 0;
    reset        = 0;
    if_wakeup_i  = 0;
    wr_valid_i   = 0;
    wr_payload_i = 0;
    wr_done_i    = 0;
    rd_valid_i   = 0;
    qreqn_i      = 1; // Start in ST_Q_RUN

    // Apply reset
    apply_reset;
    // Run test scenarios
    scenario1_reset_behavior;
    scenario2_write_read;
    scenario3_fifo_overflow_attempt;
    scenario4_fifo_underflow_attempt;
    scenario5_qreq_flush_handshake;
    scenario6_wakeup_signal_test;

    // Optional random/stress test
    scenario7_random_stress;

    // Final summary
    if (error_count == 0) begin
      $display("\nAll tests PASSED!");
    end else begin
      $display("\nTest FAILED with %0d errors!", error_count);
    end

    $finish;
  end

  // -------------------------------------------------------------------
  // Reset Task
  // -------------------------------------------------------------------
  task apply_reset;
    begin
      reset = 1;
      repeat (2) @(posedge clk); // hold reset for a couple of cycles
      reset = 0;
      repeat (2) @(posedge clk);
    end
  endtask

  // -------------------------------------------------------------------
  // Scenario #1: Reset Behavior
  //   Pass/Fail Criteria:
  //   - After reset, qacceptn_o == 1, qactive_o == 0, wr_flush_o == 0
  // -------------------------------------------------------------------
  task scenario1_reset_behavior;
    begin
      $display("\n--- SCENARIO 1: Reset Behavior ---");
      // Right after apply_reset, check signals
      @(negedge clk);
      if (qacceptn_o !== 1'b1)
        report_error("qacceptn_o should be 1 after reset");
      if (qactive_o !== 1'b0)
        report_error("qactive_o should be 0 after reset");
      if (wr_flush_o !== 1'b0)
        report_error("wr_flush_o should be 0 after reset");

      $display("Scenario 1 completed.");
    end
  endtask

  // -------------------------------------------------------------------
  // Scenario #2: Simple Write/Read
  //   Pass/Fail Criteria:
  //   - Data read = Data written (in order).
  //   - No unexpected assertions of wr_flush_o.
  // -------------------------------------------------------------------
  task scenario2_write_read;
    begin
      $display("\n--- SCENARIO 2: Simple Write/Read ---");
      // Write a few data items
      write_data(8'hAA);
      write_data(8'hBB);
      write_data(8'hCC);

      // Now read them back
      read_data_item; // expects 8'hAA
      read_data_item; // expects 8'hBB
      read_data_item; // expects 8'hCC

      // Check no extra flush was triggered
      if (wr_flush_o !== 1'b0)
        report_error("wr_flush_o should not have asserted in normal write/read scenario");

      $display("Scenario 2 completed.");
    end
  endtask

  // Write a data item into the DUT
  task write_data(input [7:0] data_in);
    begin
      @(posedge clk);
      wr_valid_i   = 1'b1;
      wr_payload_i = data_in;
      write_queue.push_back(data_in);
      @(posedge clk);
      wr_valid_i   = 1'b0;
      wr_payload_i = 8'h00; // idle
      @(posedge clk);
    end
  endtask

  // Read a data item from the DUT and check scoreboard
  task read_data_item;
    begin
      // Only attempt read if scoreboard says we have data
      if (write_queue.size() == 0) begin
        report_error("Read requested but scoreboard is empty");
        return;
      end

      // Drive read
      @(posedge clk);
      rd_valid_i = 1'b1;
      @(posedge clk);
      rd_valid_i = 1'b0;

      // The read data will appear combinationally at rd_payload_o
      // We'll sample at the next clock for stable checking
      read_data = rd_payload_o;
      // Compare with scoreboard front
      if (read_data !== write_queue[0]) begin
        report_error($sformatf("Read data mismatch. Expected %h, got %h",
                               write_queue[0], read_data));
      end
      // Pop from scoreboard
      write_queue.pop_front();
      @(posedge clk);
    end
  endtask

  // -------------------------------------------------------------------
  // Scenario #3: FIFO Overflow Attempt
  //   Pass/Fail Criteria:
  //   - Confirm design’s defined behavior. Possibly losing data or ignoring push
  //     if the FIFO is full. Make sure no unexpected lock-ups.
  // -------------------------------------------------------------------
  task scenario3_fifo_overflow_attempt;
    integer i;
    begin
      $display("\n--- SCENARIO 3: FIFO Overflow Attempt ---");
      // FIFO has DEPTH=6 in the DUT. Let's write more than 6 without reads.
      for (i = 0; i < 8; i++) begin
        write_data(i[7:0]);
      end

      // Now read all we can. The scoreboard expects 8 items, but the
      // actual FIFO can only hold 6. If the design does not gate wr_valid,
      // you might see data lost or overwritten.
      // We read 8 times to see what comes out.
      for (i = 0; i < 8; i++) begin
        read_data_item;
      end

      // If the design is not gating writes, you may see mismatch errors.
      // We only confirm it doesn't wedge or produce X states unexpectedly.
      $display("Scenario 3 completed. Check for mismatch errors or stable behavior.");
    end
  endtask

  // -------------------------------------------------------------------
  // Scenario #4: FIFO Underflow Attempt
  //   Pass/Fail Criteria:
  //   - Attempt reading from empty FIFO. Check that no corruption or
  //     unexpected transitions occur.
  // -------------------------------------------------------------------
  task scenario4_fifo_underflow_attempt;
    integer i;
    begin
      $display("\n--- SCENARIO 4: FIFO Underflow Attempt ---");
      // Ensure FIFO is empty: no writes
      // Attempt multiple reads
      for (i = 0; i < 3; i++) begin
        @(posedge clk);
        rd_valid_i = 1'b1;
        @(posedge clk);
        rd_valid_i = 1'b0;
        // Check read data (may remain at a previous or undefined value)
        $display("Read data = %h (expected empty FIFO)", rd_payload_o);
      end

      // As long as the design does not hang or produce spurious flush,
      // we consider this scenario pass if no errors have been reported.
      $display("Scenario 4 completed.");
    end
  endtask

  // -------------------------------------------------------------------
  // Scenario #5: QREQ Handshake and Flush
  //   Pass/Fail Criteria:
  //   - wr_flush_o must assert when qreqn_i goes low (ST_Q_REQUEST)
  //     and remain asserted until wr_done_i is high and FIFO empties.
  //   - qacceptn_o must go low once flush completes in ST_Q_STOPPED.
  // -------------------------------------------------------------------
  task scenario5_qreq_flush_handshake;
    begin
      $display("\n--- SCENARIO 5: QREQ Handshake and Flush ---");
      // 1) Put some data in FIFO, do not read them
      write_data(8'hA0);
      write_data(8'hB1);

      // 2) Deassert wr_done_i, so the flush cannot complete
      wr_done_i = 0;

      // 3) Pull qreqn_i low => request Q-channel
      @(posedge clk);
      qreqn_i = 0;

      // Expect the state machine to move from ST_Q_RUN -> ST_Q_REQUEST
      // Check if wr_flush_o = 1
      // We'll wait a few cycles and check
      repeat (2) @(posedge clk);
      if (wr_flush_o !== 1'b1) begin
        report_error("wr_flush_o should be asserted in ST_Q_REQUEST");
      end

      // 4) Now let the upstream complete: wr_done_i=1 => flush completes
      //    Wait for FIFO to empty as well. Let's do a quick read:
      read_data_item; // read 8'hA0
      read_data_item; // read 8'hB1

      // The FIFO is now empty but we also must keep wr_done_i = 1
      @(posedge clk);
      wr_done_i = 1'b1;

      // Wait a bit to let state machine transition
      repeat (2) @(posedge clk);

      // Now, we expect wr_flush_o = 0 (since flush completed) and
      // qacceptn_o to drive low (since ST_Q_STOPPED).
      if (wr_flush_o !== 1'b0)
        report_error("wr_flush_o should be deasserted after flush completes");

      // Because we are in ST_Q_STOPPED, qacceptn_o should be 1'b0
      // (the design sets qacceptn_o = ~qaccept, so if we accept=1 => qacceptn=0)
      if (qacceptn_o !== 1'b0)
        report_error("qacceptn_o should be 0 in ST_Q_STOPPED");

      // 5) Re-assert qreqn_i => ST_Q_EXIT => eventually qacceptn_o = 1,
      @(posedge clk);
      qreqn_i = 1'b1;
      repeat (2) @(posedge clk);
      repeat(2) @(posedge clk);  // <---- ADD at least 2 cycles of wait
      if (qacceptn_o !== 1'b1)
        report_error("qacceptn_o should go back to 1 in ST_Q_RUN eventually");

      // Return signals to idle
      wr_done_i = 0;
      $display("Scenario 5 completed.");
    end
  endtask

  // -------------------------------------------------------------------
  // Scenario #6: Wakeup Signal Check
  //   Pass/Fail Criteria:
  //   - If if_wakeup_i=1 with an empty FIFO, qactive_o should still assert.
  //   - When if_wakeup_i=0, qactive_o should deassert if no FIFO activity.
  // -------------------------------------------------------------------
  task scenario6_wakeup_signal_test;
    begin
      $display("\n--- SCENARIO 6: Wakeup Signal Check ---");
      // Ensure FIFO is empty from previous scenario
      // No writes or reads
      if_wakeup_i = 1'b1;
      @(posedge clk);
      if (qactive_o !== 1'b1)
        report_error("qactive_o should be high due to wakeup");

      // Now deassert wakeup
      if_wakeup_i = 1'b0;
      repeat (2) @(posedge clk);
      if (qactive_o !== 1'b0)
        report_error("qactive_o should return low when wakeup is cleared and FIFO idle");

      $display("Scenario 6 completed.");
    end
  endtask

  // -------------------------------------------------------------------
  // Scenario #7: Optional Random/Stress (example skeleton)
  //   Pass/Fail Criteria:
  //   - No data mismatches or illegal state machine transitions.
  //   - Potential to uncover corner cases more systematically.
  // -------------------------------------------------------------------

  task scenario7_random_stress;
    integer i;
    begin
      $display("\n--- SCENARIO 7: Random/Stress Test ---");
      for (i = 0; i < 100; i++) begin
        // Random writes
        wr_valid_i   = $urandom_range(0,1);
        wr_payload_i = $urandom_range(0,255);
        if (wr_valid_i) write_queue.push_back(wr_payload_i);

        // Random reads
        rd_valid_i   = $urandom_range(0,1);
        if (rd_valid_i && write_queue.size() > 0) begin
          // scoreboard check after the cycle
        end

        // Random QREQ toggles
        if ($urandom_range(0,50) == 0) qreqn_i = ~qreqn_i;

        // Random wakeup
        if_wakeup_i = $urandom_range(0,1);

        // Random wr_done_i
        wr_done_i  = $urandom_range(0,1);

        @(posedge clk);
      end

      // Turn off writes, reads, wait some cycles
      wr_valid_i   = 0;
      rd_valid_i   = 0;
      if_wakeup_i  = 0;
      wr_done_i    = 1;
      repeat (10) @(posedge clk);

      $display("Scenario 7 completed (Random/Stress).");
    end
  endtask


endmodule

context.rtl/cross_domain_sync.sv:
module cross_domain_sync #(  
    parameter DATA_WIDTH = 16
)(
    input  wire                   clk_dst,
    input  wire                   rst_n,
    input  wire [DATA_WIDTH-1:0] data_src,
    input  wire                   valid_src,

    output reg  [DATA_WIDTH-1:0] data_dst,
    output reg                   valid_dst
);

    reg [DATA_WIDTH-1:0] sync1_data, sync2_data;
    reg                  sync1_valid; 

    always @(posedge clk_dst or negedge rst_n) begin
        if (!rst_n) begin
            sync1_data  <= 0; sync2_data <= 0;
            sync1_valid <= 0;
        end else begin
            sync1_data  <= data_src;
            sync2_data  <= sync1_data;

            sync1_valid <= valid_src;
        end
    end

    always @(posedge clk_dst or negedge rst_n) begin
        if (!rst_n) begin
            data_dst  <= 0;
            valid_dst <= 0;
        end else begin
            data_dst  <= sync2_data;
            valid_dst <= sync1_valid;
        end
    end

endmodule

context.rtl/dsp_input_stage.sv:
module dsp_input_stage #(
    parameter DATA_WIDTH = 16     
)(
    input  wire                   clk,
    input  wire                   rst_n,
    input  wire [DATA_WIDTH-1:0] data_in,
    input  wire                   valid_in,
    input  wire [DATA_WIDTH-1:0] rand_mask,

    output reg  [DATA_WIDTH-1:0] data_out,
    output reg                   valid_out
);

    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            data_out  <= 0;
            valid_out <= 0;
        end else begin
            data_out  <= data_in ^ rand_mask;
            valid_out <= valid_in;
        end
    end

endmodule

context.rtl/dsp_output_stage.sv:
module dsp_output_stage #(
    parameter DATA_WIDTH = 16
)(
    input  wire                   clk,
    input  wire                   rst_n,
    input  wire [DATA_WIDTH-1:0] data_in,
    input  wire                   valid_in,
    input  wire [DATA_WIDTH-1:0] rand_mask,

    output reg  [DATA_WIDTH-1:0] data_out,
    output reg                   valid_out,
    output reg  [31:0]           transfer_count
);

    always @(posedge clk or negedge rst_n) begin
        if (!rst_n) begin
            data_out       <= 0;
            valid_out      <= 0;
            transfer_count <= 0;
        end else begin
            transfer_count <= transfer_count + 1;

            if (valid_in) begin
                data_out  <= data_in ^ rand_mask;
                valid_out <= 1;
            end else begin
                valid_out <= 0;
            end
        end
    end

endmodule

context.rtl/lfsr_generator.sv:
module lfsr_generator #(
    parameter WIDTH = 16,     
    parameter SEED = 16'hACE1
)(
    input  wire clk,
    input  wire rst_n,
    output reg [WIDTH-1:0] lfsr
);

    always @(posedge clk or negedge rst_n) begin
        if (!rst_n)
            lfsr <= SEED;
        else
            lfsr <= {lfsr[WIDTH-2:0], lfsr[WIDTH-1] ^ lfsr[2]};
    end

endmodule

context.rtl/monte_carlo_dsp_monitor_top.sv:
module monte_carlo_dsp_monitor_top #(
    parameter DATA_WIDTH = 16,                     
    parameter SEED_A = 16'hACE1,
    parameter SEED_B = 16'hBEEF
)(
    input  wire                   clk_a,
    input  wire                   clk_b,
    input  wire                   rst_n,
    input  wire [DATA_WIDTH-1:0] data_in_a,
    input  wire                   valid_in_a,
    output wire [DATA_WIDTH-1:0] data_out_b,
    output wire                   valid_out_b,
    output wire [31:0]            cross_domain_transfer_count
);

    wire [DATA_WIDTH-1:0] lfsr_out_a, lfsr_out_b;
    wire [DATA_WIDTH-1:0] data_stage_a;
    wire                  valid_stage_a;
    wire [DATA_WIDTH-1:0] data_cross_b;
    wire                  valid_cross_b;

    // LFSR for clk_a
    lfsr_generator #(.WIDTH(DATA_WIDTH), .SEED(SEED_A)) u_lfsr_a (
        .clk(clk_a),
        .rst_n(rst_n),
        .lfsr(lfsr_out_a)
    );

    // LFSR for clk_b
    lfsr_generator #(.WIDTH(DATA_WIDTH), .SEED(SEED_B)) u_lfsr_b (
        .clk(clk_b),
        .rst_n(rst_n),
        .lfsr(lfsr_out_b)
    );

    // Input stage
    dsp_input_stage #(.DATA_WIDTH(DATA_WIDTH)) u_input_stage (
        .clk(clk_a),
        .rst_n(rst_n),
        .data_in(data_in_a),
        .valid_in(valid_in_a),
        .rand_mask(lfsr_out_a),
        .data_out(data_stage_a),
        .valid_out(valid_stage_a)
    );

    // Cross domain sync
    cross_domain_sync #(.DATA_WIDTH(DATA_WIDTH)) u_cross_sync (
        .clk_dst(clk_b),
        .rst_n(rst_n),
        .data_src(data_stage_a),
        .valid_src(valid_stage_a),
        .data_dst(data_cross_b),
        .valid_dst(valid_cross_b)
    );

    // Output stage
    dsp_output_stage #(.DATA_WIDTH(DATA_WIDTH)) u_output_stage (
        .clk(clk_b),
        .rst_n(rst_n),
        .data_in(data_cross_b),
        .valid_in(valid_cross_b),
        .rand_mask(lfsr_out_b),
        .data_out(data_out_b),
        .valid_out(valid_out_b),
        .transfer_count(cross_domain_transfer_count)
    );

endmodule

context.verif/monte_carlo_dsp_monitor_top_tb.sv:
module monte_carlo_dsp_monitor_top_tb;

  parameter DATA_WIDTH = 16;
  parameter CLK_A_PERIOD = 7;
  parameter CLK_B_PERIOD = 13;
  parameter EXPECTED_MIN_OUTPUTS = 100;

  logic clk_a, clk_b, rst_n;
  logic [DATA_WIDTH-1:0] data_in_a;
  logic valid_in_a;
  wire [DATA_WIDTH-1:0] data_out_b;
  wire valid_out_b;
  wire [31:0] cross_domain_transfer_count;

  bit passed_static  = 0;
  bit passed_random  = 0;
  bit passed_toggle  = 0;
  bit any_fail       = 0;

  monte_carlo_dsp_monitor_top #(.DATA_WIDTH(DATA_WIDTH)) dut (
    .clk_a(clk_a),
    .clk_b(clk_b),
    .rst_n(rst_n),
    .data_in_a(data_in_a),
    .valid_in_a(valid_in_a),
    .data_out_b(data_out_b),
    .valid_out_b(valid_out_b),
    .cross_domain_transfer_count(cross_domain_transfer_count)
  );

  initial clk_a = 0;
  always #(CLK_A_PERIOD / 2) clk_a = ~clk_a;

  initial clk_b = 0;
  always #(CLK_B_PERIOD / 2) clk_b = ~clk_b;

  int actual_output_count = 0;
  always @(posedge clk_b) begin
    if (rst_n && valid_out_b)
      actual_output_count++;
  end

  initial begin
    $display("=== Extended Monte Carlo DSP Monitor TB ===");

    rst_n = 0;
    data_in_a = 0;
    valid_in_a = 0;
    repeat (5) @(posedge clk_a);
    rst_n = 1;
    repeat (5) @(posedge clk_a);

    // === Static patterns ===
    send(16'h0000, "All Zeros");
    send(16'hFFFF, "All Ones");
    send(16'hAAAA, "Alternating 1010");
    send(16'h5555, "Alternating 0101");
    passed_static = 1;

    // === One-hot & inverse one-hot ===
    for (int i = 0; i < DATA_WIDTH; i++) send(1 << i, $sformatf("One-hot %0d", i));
    for (int i = 0; i < DATA_WIDTH; i++) send(~(1 << i), $sformatf("Inverse one-hot %0d", i));

    // === Numeric edge patterns ===
    send(16'h0001, "Min +1");
    send(16'h7FFF, "Mid max");
    send(16'h8000, "MSB only");
    send(16'hFFFE, "All but LSB");
    send(16'h00FF, "Low byte");
    send(16'hFF00, "High byte");

    // === Sequential counter ===
    for (int i = 0; i < 10; i++) send(i, $sformatf("Counter %0d", i));

    // === Wraparound ===
    for (int i = 65530; i < 65536; i++) send(i[15:0], $sformatf("Wraparound %0d", i));

    // === Random burst ===
    repeat (40) begin
      @(posedge clk_a);
      data_in_a = $urandom();
      valid_in_a = 1;
      $display("[RANDOM BURST] Data=0x%04X at %0t", data_in_a, $time);
      @(posedge clk_a);
      valid_in_a = 0;
    end

    // === Random w/ gaps ===
    for (int i = 0; i < 20; i++) begin
      repeat ($urandom_range(1, 4)) @(posedge clk_a);
      data_in_a = $urandom();
      valid_in_a = 1;
      $display("[GAPPED RANDOM] Data=0x%04X at %0t", data_in_a, $time);
      @(posedge clk_a);
      valid_in_a = 0;
    end
    passed_random = 1;

    // === Delayed valid toggle ===
    for (int i = 0; i < 10; i++) begin
      @(posedge clk_a);
      data_in_a = $urandom();
      valid_in_a = 0;
      @(posedge clk_a);
      valid_in_a = 1;
      @(posedge clk_a);
      valid_in_a = 0;
    end

    // === Toggle valid ===
    for (int i = 0; i < 20; i++) begin
      @(posedge clk_a);
      data_in_a = $urandom();
      valid_in_a = (i % 2 == 0);
      $display("[TOGGLE VALID] Valid=%0d Data=0x%04X", valid_in_a, data_in_a);
    end
    passed_toggle = 1;

    // === Repeated pattern ===
    for (int i = 0; i < 10; i++) send(16'h1234, "Repeated 0x1234");

    // === Noise injection ===
    for (int i = 0; i < 10; i++) begin
      @(posedge clk_a);
      data_in_a = $urandom() ^ 16'h00F0;
      valid_in_a = 1;
      $display("[NOISE MASKED] Data=0x%04X", data_in_a);
      @(posedge clk_a);
      valid_in_a = 0;
    end

    // === Slow ramp-up ===
    for (int i = 0; i < 5; i++) begin
      repeat (i + 1) @(posedge clk_a);
      data_in_a = i * 1000;
      valid_in_a = 1;
      $display("[RAMP-UP] Step %0d Data=0x%04X", i, data_in_a);
      @(posedge clk_a);
      valid_in_a = 0;
    end

    // === Completion ===
    valid_in_a = 0;
    data_in_a = 0;
    repeat (200) @(posedge clk_b);

    $display("\nActual valid outputs       : %0d", actual_output_count);
    $display("DUT cross-domain transfers : %0d", cross_domain_transfer_count);

    // === Summary Table ===
    $display("\n=== TEST SUMMARY TABLE ===");
    $display("| %-25s | %-6s |", "Test Section", "Status");
    $display("|---------------------------|--------|");

    if (!passed_static) begin
      $error("| %-25s | %-6s |", "Static Patterns", "FAIL"); any_fail = 1;
    end else $display("| %-25s | %-6s |", "Static Patterns", "PASS");

    if (!passed_random) begin
      $error("| %-25s | %-6s |", "Random Sequences", "FAIL"); any_fail = 1;
    end else $display("| %-25s | %-6s |", "Random Sequences", "PASS");

    if (!passed_toggle) begin
      $error("| %-25s | %-6s |", "Toggle Valid", "FAIL"); any_fail = 1;
    end else $display("| %-25s | %-6s |", "Toggle Valid", "PASS");

    if (actual_output_count != cross_domain_transfer_count) begin
      $error("| %-25s | %-6s |", "Output Count Match", "FAIL");
      $error("Mismatch: Output count (%0d) != DUT counter (%0d)",
        actual_output_count, cross_domain_transfer_count);
      any_fail = 1;
    end else $display("| %-25s | %-6s |", "Output Count Match", "PASS");

    if (actual_output_count < EXPECTED_MIN_OUTPUTS) begin
      $error("| %-25s | %-6s |", "Minimum Output Check", "FAIL");
      $error("Too few outputs: Got %0d, expected at least %0d",
        actual_output_count, EXPECTED_MIN_OUTPUTS);
      any_fail = 1;
    end else $display("| %-25s | %-6s |", "Minimum Output Check", "PASS");

    if (!any_fail)
      $display("TEST CASE PASSED: Captured %0d valid outputs.", actual_output_count);

    $finish;
  end

  task send(input [DATA_WIDTH-1:0] val, input string label);
    @(posedge clk_a);
    data_in_a = val;
    valid_in_a = 1;
    $display("[STIM] %-20s | Data=0x%04X at %0t", label, val, $time);
    @(posedge clk_a);
    valid_in_a = 0;
  endtask

endmodule

context.verif/multiplexer_tb.sv:
`timescale 1ns/1ps

module tb_multiplexer;

  reg clk;
  reg rst_n;
  reg [8*3-1:0] inp;
  reg [1:0] sel;
  reg bypass;
  wire [7:0] out;
  integer i, j;
  reg [7:0] expected;

  multiplexer #(
      .DATA_WIDTH(8),
      .NUM_INPUTS(3),
      .REGISTER_OUTPUT(1),
      .HAS_DEFAULT(1),
      .DEFAULT_VALUE(8'h55)
  ) dut (
      .clk(clk),
      .rst_n(rst_n),
      .inp(inp),
      .sel(sel),
      .bypass(bypass),
      .out(out)
  );

  always #5 clk = ~clk;

  initial begin
    clk = 0; rst_n = 0; inp = 0; sel = 0; bypass = 0;
    repeat(2) @(posedge clk);
    rst_n = 1;
    repeat(2) @(posedge clk);
    for (i = 0; i < 10; i = i + 1) begin
      inp = {($random() & 8'hFF), ($random() & 8'hFF), ($random() & 8'hFF)};
      for (j = 0; j < 4; j = j + 1) begin
        sel = j[1:0];
        bypass = 0;
        #1;
        if (sel < 3) expected = inp[sel*8 +: 8];
        else         expected = 8'h55;
        @(posedge clk);
        @(posedge clk);
        if (out !== expected)
          $display("Time=%0t Sel=%0d Bypass=%0b Inp=%0h Expected=%0h Got=%0h", $time, sel, bypass, inp, expected, out);
        else
          $display("Time=%0t PASSED Sel=%0d Bypass=%0b", $time, sel, bypass);

        bypass = 1;
        #1;
        expected = inp[0 +: 8];
        @(posedge clk);
        @(posedge clk);
        if (out !== expected)
          $display("Time=%0t Sel=%0d Bypass=%0b Inp=%0h Expected=%0h Got=%0h", $time, sel, bypass, inp, expected, out);
        else
          $display("Time=%0t PASSED Sel=%0d Bypass=%0b", $time, sel, bypass);
      end
    end
    $finish;
  end

endmodule

