context.rtl/phase_rotation.sv:
module phase_rotation #(
   parameter NBW_IN_DATA  = 8,
   parameter NBW_COS      = 8,
   parameter NBW_MULT     = NBW_IN_DATA + NBW_COS,
   parameter NBW_SUM      = NBW_MULT + 1,
   parameter NBW_OUT_DATA = NBW_SUM
) (
   input  logic clk,
   input  logic signed [NBW_IN_DATA-1:0]  i_data_re,
   input  logic signed [NBW_IN_DATA-1:0]  i_data_im,
   input  logic signed [NBW_COS-1:0]      i_cos,
   input  logic signed [NBW_COS-1:0]      i_sin,
   output logic signed [NBW_OUT_DATA-1:0] o_data_re,
   output logic signed [NBW_OUT_DATA-1:0] o_data_im
);

   logic signed [NBW_IN_DATA-1:0]  data_re_reg;
   logic signed [NBW_IN_DATA-1:0]  data_im_reg;
   logic signed [NBW_COS-1:0]      cos_reg;
   logic signed [NBW_COS-1:0]      sin_reg;

   logic signed [NBW_MULT-1:0] data_a;
   logic signed [NBW_MULT-1:0] data_b;
   logic signed [NBW_MULT-1:0] data_c;
   logic signed [NBW_MULT-1:0] data_d;

   logic signed [NBW_SUM-1:0] sum_1;
   logic signed [NBW_SUM-1:0] sum_2;

   always_ff @(posedge clk) begin
      data_re_reg <= i_data_re;
      data_im_reg <= i_data_im;
      cos_reg     <= i_cos;
      sin_reg     <= i_sin;
   end

   assign data_a = cos_reg*data_re_reg;
   assign data_b = sin_reg*data_im_reg;
   assign data_c = sin_reg*data_re_reg;
   assign data_d = cos_reg*data_im_reg;

   assign sum_1  = data_a - data_b;
   assign sum_2  = data_c + data_d;

   always_comb begin
      o_data_re = sum_1;
      o_data_im = sum_2;
   end

endmodule

context.rtl/power4.sv:
module power4 #(
   parameter NBW_IN = 8,
   parameter NBW_OUT= NBW_IN*4
)
(
   input  logic clk,
   input  logic rst_async_n,
   input  logic signed [NBW_IN-1:0]  i_data_i,
   input  logic signed [NBW_IN-1:0]  i_data_q,
   output logic signed [NBW_OUT-1:0] o_data_i,  
   output logic signed [NBW_OUT-1:0] o_data_q
);

localparam NBW_SQUARE = 2*NBW_IN;
localparam NBW_FOURTH = 2*NBW_SQUARE;
// Fourth power 
logic signed [NBW_SQUARE-1:0] data_i2;
logic signed [NBW_SQUARE-1:0] data_q2;
logic signed [NBW_FOURTH-1:0] data_i4;
logic signed [NBW_FOURTH-1:0] data_q4;


assign data_i2 = i_data_i*i_data_i;
assign data_i4 = data_i2*data_i2;

assign data_q2 = i_data_q*i_data_q;
assign data_q4 = data_q2*data_q2;

always_ff @(posedge clk or negedge rst_async_n) begin
   if(!rst_async_n) begin
      o_data_i <= 'd0;
      o_data_q <= 'd0;
   end
   else begin
      o_data_i <= data_i4;
      o_data_q <= data_q4;
   end
end


endmodule

context.rtl/saturation.sv:
module saturation #(
   parameter NBW_IN  = 8,
   parameter NBW_OUT = 6
)
(
   input  logic [NBW_IN-1:0]  i_data,
   output logic [NBW_OUT-1:0] o_data
);

localparam NB_TRIM = NBW_IN - NBW_OUT;
localparam NB_REPL = NBW_OUT - 1;

    generate
        if (NB_TRIM == 1) begin
            always_comb begin : trim
                if (i_data[NBW_IN-1] == i_data[NBW_OUT-1]) begin //sat
                    o_data = $signed(i_data[NBW_OUT-1:0]);
                end else begin
                    o_data = $signed({i_data[NBW_IN-1],{NB_REPL{!i_data[NBW_IN-1]}}});
                end
            end

        end else if (NB_TRIM > 1) begin

            always_comb begin : trim
                if ({(NB_TRIM){i_data[NBW_IN-1]}} == i_data[NBW_IN-2:NBW_OUT-1]) begin //sat
                    o_data = $signed(i_data[NBW_OUT-1:0]);
                end else begin
                    o_data = $signed({i_data[NBW_IN-1],{NB_REPL{!i_data[NBW_IN-1]}}});
                end
            end

        end
    endgenerate


endmodule

