localparam NBW_ADDER_TREE_OUT = NBW_ADDER_TREE_IN  + N_ADDER_LEVELS;
localparam NBI_ADDER_TREE_OUT = NBI_ADDER_TREE_IN  + N_ADDER_LEVELS;
localparam NBF_ADDER_TREE_OUT = NBW_ADDER_TREE_OUT - NBI_ADDER_TREE_OUT;

logic [NBW_ADDER_TREE_IN*NS_DATA_IN-1:0] sum_i_1d;
logic [NBW_ADDER_TREE_IN*NS_DATA_IN-1:0] sum_q_1d;

/*Correlation result for i and q data component*/
wire signed [NBW_ADDER_TREE_OUT-1:0] correlation_i;
wire signed [NBW_ADDER_TREE_OUT-1:0] correlation_q;

correlate #(
    .NS_DATA_IN       (NS_DATA_IN       ),
    .NBW_DATA_IN      (NBW_DATA_IN      ),
    .NBW_ADDER_TREE_IN(NBW_ADDER_TREE_IN)
) uu_correlate(
    .i_data_i    (i_data_i    ),
    .i_data_q    (i_data_q    ),
    .i_conj_seq_i(i_conj_seq_i),
    .i_conj_seq_q(i_conj_seq_q),   
    .o_sum_i     (sum_i_1d    ),
    .o_sum_q     (sum_q_1d    )
);

adder_2d_layers  #(
    .NBW_IN            (NBW_ADDER_TREE_IN ),
    .NS_IN             (NS_DATA_IN        ),
    .N_LEVELS          (N_ADDER_LEVELS    ),
    .REGS              (N_REG_LEVELS      ),
    .NBW_ADDER_TREE_OUT(NBW_ADDER_TREE_OUT),
    .NBW_ENERGY        (NBW_ENERGY        )
) uu_adder_2d_layers (
        .clk     (clk            ),
        .i_enable(i_enable       ),
        .i_data_i(sum_i_1d       ),
        .i_data_q(sum_q_1d       ),
        .o_data_i(correlation_i  ),
        .o_data_q(correlation_q  ),
        .o_energy(o_energy)            
);

endmodule

context.rtl/detect_sequence.sv:
module detect_sequence #(
    parameter    NS              =  'd64      ,
    parameter    NBW_PILOT_POS   =  'd06      ,
    parameter    NBW_DATA_IN     =  'd08      ,
    parameter    NBI_DATA_IN     =  'd02      ,
    parameter    NBW_TH_PROC     =  'd10      ,
    parameter    NBW_ENERGY      =  'd10      ,
    parameter    NS_PROC         =  'd23      ,
    parameter    NS_PROC_OVERLAP =  NS_PROC - 1
)
(
    input  logic                                        clk               ,
    input  logic                                        rst_async_n       ,
    input  logic                                        i_valid           ,
    input  logic                                        i_enable          ,
    input  logic                                        i_proc_pol        ,
    input  logic [NBW_PILOT_POS-1:0]                    i_proc_pos        ,
    input  logic [NBW_TH_PROC-1:0]                      i_static_threshold,
    input  logic [NBW_DATA_IN*(NS+NS_PROC_OVERLAP)-1:0] i_data_i          ,
    input  logic [NBW_DATA_IN*(NS+NS_PROC_OVERLAP)-1:0] i_data_q          ,
    output logic                                        o_proc_detected    
);
localparam   PROC_CORR_ADDER_LEVELS   = $clog2(NS_PROC);
localparam   PROC_CORR_REG_LEVELS     = 8'b00000000;
localparam   PIPE_DEPTH              = 3;

logic [NS_PROC-1:0] conj_proc_h[2];
logic [NS_PROC-1:0] conj_proc_v[2];

assign conj_proc_h[1] = 23'b11011001100011010001110;
assign conj_proc_v[1] = 23'b10000101011110000101011;

assign conj_proc_h[0] = 23'b10101010111011101000000;
assign conj_proc_v[0] = 23'b11010110101100100001110;

logic signed [NBW_DATA_IN-1:0]  i_data_i_2d[(NS+NS_PROC_OVERLAP)];
logic signed [NBW_DATA_IN-1:0]  i_data_q_2d[(NS+NS_PROC_OVERLAP)];

always_comb begin
    for (int i=0; i<(NS+NS_PROC_OVERLAP); i++) begin
        i_data_i_2d[i] = $signed(i_data_i[(i+1)*NBW_DATA_IN-1-:NBW_DATA_IN]);
        i_data_q_2d[i] = $signed(i_data_q[(i+1)*NBW_DATA_IN-1-:NBW_DATA_IN]);
    end
end

logic  [PIPE_DEPTH-1:0] proc_enable_dff;

logic  proc_detected_dff;

logic  proc_pol_dff;

logic signed [NBW_DATA_IN-1:0] proc_buffer_i_dff[NS_PROC];
logic signed [NBW_DATA_IN-1:0] proc_buffer_q_dff[NS_PROC];
logic signed [NBW_DATA_IN*NS_PROC-1:0] proc_buffer_i_dff_1d;
logic signed [NBW_DATA_IN*NS_PROC-1:0] proc_buffer_q_dff_1d;
logic                     proc_enable;      
logic [NBW_ENERGY  -1 :0] proc_calc_energy; 
logic [       NS_PROC-1:0] conj_proc_seq[2];

assign proc_enable  = i_valid & i_enable;

always_ff @(posedge clk or negedge rst_async_n) begin : proc_proc_enable_dff
  if(~rst_async_n) 
    proc_enable_dff <= {PIPE_DEPTH{1'b0}};
  else begin
    proc_enable_dff[0] <= proc_enable;
    for(int i = 1 ; i < PIPE_DEPTH ; i++)
      proc_enable_dff[i] <= proc_enable_dff[i-1];
  end
end

always_ff @(posedge clk) begin
    if(proc_enable) begin
      for(int i = 0; i < NS_PROC; i++) begin
        proc_buffer_i_dff[i] <=  i_data_i_2d[i_proc_pos + i];
        proc_buffer_q_dff[i] <=  i_data_q_2d[i_proc_pos + i];
      end
    end
end

always_ff @(posedge clk or negedge rst_async_n) begin
    if(~rst_async_n)
        proc_pol_dff <= 1'b0;
    else      
      if(proc_enable)
        proc_pol_dff <=  i_proc_pol;
end

always_comb begin
  if(proc_pol_dff) begin
    conj_proc_seq[0] = conj_proc_v[0];
    conj_proc_seq[1] = conj_proc_v[1];
  end
  else begin
    conj_proc_seq[0] = conj_proc_h[0];
    conj_proc_seq[1] = conj_proc_h[1];    
  end
end

always_comb begin
  for (int i=0; i<NS_PROC; ++i) begin
    proc_buffer_i_dff_1d[(i+1)*NBW_DATA_IN-1-:NBW_DATA_IN] = proc_buffer_i_dff[i];
    proc_buffer_q_dff_1d[(i+1)*NBW_DATA_IN-1-:NBW_DATA_IN] = proc_buffer_q_dff[i];
  end
end

cross_correlation #(
        .NS_DATA_IN  (NS_PROC     ),
        .NBW_DATA_IN (NBW_DATA_IN ),
        .NBI_DATA_IN (NBI_DATA_IN ),
        .NBW_ENERGY  (NBW_ENERGY  )
  ) uu_cross_correlation(
     .clk         (clk                 ),
     .i_enable    (proc_enable_dff[0]  ),
     .i_data_i    (proc_buffer_i_dff_1d),
     .i_data_q    (proc_buffer_q_dff_1d),
     .i_conj_seq_i(conj_proc_seq[0]    ),
     .i_conj_seq_q(conj_proc_seq[1]    ),
     .o_energy    (proc_calc_energy    )
);

assign proc_detected  =   (proc_calc_energy >= i_static_threshold ) && proc_enable_dff[2]; 

always_ff @(posedge clk or negedge rst_async_n) begin : proc_proc_detected_dff
  if(~rst_async_n) 
     proc_detected_dff <= 1'b0;
  else
     proc_detected_dff <=  proc_detected;
end

assign o_proc_detected = proc_detected_dff;

endmodule

context.rtl/adder_tree.sv:
module adder_tree #(
  parameter NUM_INPUTS = 8,
  parameter DATA_WIDTH = 32
)
(
  input  logic                                     clk,
  input  logic                                     arst_n,
  input  logic                                     valid_in,
  input  logic [DATA_WIDTH-1:0]                    data_in [NUM_INPUTS],
  output logic [DATA_WIDTH+$clog2(NUM_INPUTS)-1:0] sum_out,
  output logic                                     valid_out
);

  // Calculate the number of pipeline stages:
  localparam NUM_STAGES = $clog2(NUM_INPUTS);

  // Pipeline registers for data and valid signals.
  logic [DATA_WIDTH+$clog2(NUM_INPUTS)-1:0] stage_reg [0:NUM_STAGES][0:NUM_INPUTS-1];
  logic valid_stage [0:NUM_STAGES];
  integer i, s, j;

  // Stage 0: Register the input data.
  always_ff @(posedge clk or negedge arst_n) begin
    if (!arst_n) begin
      for (i = 0; i < NUM_INPUTS; i = i + 1)
        stage_reg[0][i] <= '0;
      valid_stage[0] <= 1'b0;
    end
    else if (valid_in) begin
      for (i = 0; i < NUM_INPUTS; i = i + 1)
        // Sign extend if needed.
        stage_reg[0][i] <= {{($clog2(NUM_INPUTS)){data_in[i][DATA_WIDTH-1]}}, data_in[i]};
      valid_stage[0] <= 1'b1;
    end 
    else begin
      valid_stage[0] <= 1'b0;
    end
  end

  // Subsequent stages: each stage halves the number of values.
  generate
    for (genvar s = 1; s <= NUM_STAGES; s = s + 1) begin : stage_pipeline
      localparam int NUM_ELEMS = NUM_INPUTS >> s;
      always_ff @(posedge clk or negedge arst_n) begin
        if (!arst_n) begin
          for (j = 0; j < NUM_ELEMS; j = j + 1)
            stage_reg[s][j] <= '0;
          valid_stage[s] <= 1'b0;
        end
        else if (valid_stage[s-1]) begin
          for (j = 0; j < NUM_ELEMS; j = j + 1)
            stage_reg[s][j] <= stage_reg[s-1][2*j] + stage_reg[s-1][2*j+1];
          valid_stage[s] <= 1'b1;
        end
        else begin
          valid_stage[s] <= 1'b0;
        end
      end
    end
  endgenerate

  assign sum_out  = stage_reg[NUM_STAGES][0];
  assign valid_out = valid_stage[NUM_STAGES];

endmodule

context.rtl/coeff_ram.sv:
module coeff_ram #(
  parameter NUM_COEFFS  = 32,
  parameter DATA_WIDTH  = 16
)
(
  input  logic                          clk,
  input  logic [$clog2(NUM_COEFFS)-1:0] addr,
  output logic [DATA_WIDTH-1:0]         data_out
);

  // Memory array for coefficients.
  logic [DATA_WIDTH-1:0] mem [0:NUM_COEFFS-1];
  integer i;

  // Synchronous read.
  always_ff @(posedge clk) begin
    data_out <= mem[addr];
  end

endmodule

context.rtl/poly_filter.sv:
module poly_filter #(
  parameter  M           = 4,    // decimation factor
  parameter  TAPS        = 8,    // Taps per phase
  parameter  COEFF_WIDTH = 16,   // Coefficient bit width
  parameter  DATA_WIDTH  = 16,   // Sample data bit width
  localparam ACC_WIDTH   = DATA_WIDTH + COEFF_WIDTH + $clog2(TAPS)
)
(
  input  logic                         clk,
  input  logic                         arst_n,
  input  logic [DATA_WIDTH-1:0]        sample_buffer [0:TAPS-1],
  input  logic                         valid_in,
  // The branch's phase number
  input  logic [$clog2(M)-1:0]         phase,
  output logic [ACC_WIDTH-1:0]         filter_out,
  output logic                         valid
);

  // ---- Stage 0: Register the input sample vector and phase. ----
  logic [DATA_WIDTH-1:0] sample_reg [0:TAPS-1];
  logic [$clog2(M)-1:0]  phase_reg;
  logic                  valid_stage0;
  integer i;
  always_ff @(posedge clk or negedge arst_n) begin
    if (!arst_n) begin
      for (i = 0; i < TAPS; i = i + 1)
        sample_reg[i] <= '0;
      phase_reg    <= '0;
      valid_stage0 <= 1'b0;
    end
    else begin
      if (valid_in) begin
        for (i = 0; i < TAPS; i = i + 1)
          sample_reg[i] <= sample_buffer[i];
        phase_reg    <= phase;
        valid_stage0 <= 1'b1;
      end
      else begin
        valid_stage0 <= 1'b0;
      end
    end
  end

  // ---- Stage 1: Coefficient Fetch ----
  logic [COEFF_WIDTH-1:0] coeff [0:TAPS-1];
  genvar j;
  generate
    for (j = 0; j < TAPS; j = j + 1) begin : coeff_fetch
      logic [$clog2(M*TAPS)-1:0] addr;
      assign addr = phase_reg * TAPS + j;
      coeff_ram #(
        .NUM_COEFFS(M*TAPS),
        .DATA_WIDTH(COEFF_WIDTH)
      ) u_coeff_ram (
        .clk     (clk),
        .addr    (addr),
        .data_out(coeff[j])
      );
    end
  endgenerate

  logic valid_stage1;
  always_ff @(posedge clk or negedge arst_n) begin
    if (!arst_n)
      valid_stage1 <= 1'b0;
    else
      valid_stage1 <= valid_stage0;
  end

  // ---- Stage 2: Multiply the registered samples with coefficients ----
  logic [DATA_WIDTH+COEFF_WIDTH-1:0] products [0:TAPS-1];
  integer k;
  always_comb begin
    for (k = 0; k < TAPS; k = k + 1)
      products[k] = sample_reg[k] * coeff[k];
  end

  // ---- Stage 3: Sum the products using the adder_tree ----
  logic [ACC_WIDTH-1:0] sum_result;
  logic                 valid_adder;
  adder_tree #(
    .NUM_INPUTS(TAPS),
    .DATA_WIDTH(DATA_WIDTH+COEFF_WIDTH)
  ) u_adder_tree_filter (
    .clk      (clk),
    .arst_n   (arst_n),
    .valid_in (valid_stage1),
    .data_in  (products),
    .sum_out  (sum_result),
    .valid_out(valid_adder)
  );

  // ---- Stage 4: Output Registration ----
  always_ff @(posedge clk or negedge arst_n) begin
    if (!arst_n) begin
      filter_out <= '0;
      valid      <= 1'b0;
    end
    else begin
      filter_out <= sum_result;
      valid      <= valid_adder;
    end
  end

endmodule

context.rtl/shift_register.sv:
module shift_register #(
  parameter TAPS  = 32,   // TOTAL_TAPS = N * TAPS
  parameter DATA_WIDTH  = 16
)
(
  input  logic                  clk,
  input  logic                  arst_n,
  input  logic                  load,         // Asserted when a new sample is to be shifted in
  input  logic [DATA_WIDTH-1:0] new_sample,
  output logic [DATA_WIDTH-1:0] data_out [0:TAPS-1],
  output logic                  data_out_val  // Indicates that data_out is updated.
);

  // Internal register array for storing samples.
  logic [DATA_WIDTH-1:0] reg_array [0:TAPS-1];
  integer i;

  always_ff @(posedge clk or negedge arst_n) begin
    if (!arst_n) begin
      for (i = 0; i < TAPS; i = i + 1)
        reg_array[i] <= '0;
      data_out_val <= 1'b0;
    end
    else if (load) begin
      reg_array[0] <= new_sample;
      for (i = TAPS-1; i > 0; i = i - 1)
        reg_array[i] <= reg_array[i-1];
      data_out_val <= 1'b1;
    end
    else begin
      data_out_val <= 1'b0;
    end
  end

  // Continuous assignment of the stored register values to the outputs.
  generate
    for (genvar j = 0; j < TAPS; j = j + 1) begin : assign_output
      assign data_out[j] = reg_array[j];
    end
  endgenerate

endmodule

context.rtl/prbs_gen_check.sv:
/**************************************************************************
FILENAME:    prbs_gen_check.sv
DESCRIPTION: This module generates or checks a PRBS pattern.
**************************************************************************/

module prbs_gen_check #(
   parameter CHECK_MODE  = 0,              // 0: uses LFSR structure to generate a PRBS pattern, 1: the incoming data is loaded into prbs registers and compared with locally generated PRBS pattern.
   parameter POLY_LENGTH = 31,             // length of the polynomial.(number of shift register stages)
   parameter POLY_TAP    = 3,              // Intermedite stage that is xored with the last stage to generate the next prbs bit.
   parameter WIDTH       = 16              // Bus size of data_in and data_out.
   )(
   input                         clk     , // clock input
   input                         rst     , // synchronous reset, active high
   input         [WIDTH-1:0]     data_in , // input data to be checked(checker mode), tied to 0 (generator mode)
   output logic  [WIDTH-1:0]     data_out  // generated prbs pattern (generator mode), error found (checker mode).
);

logic [1:POLY_LENGTH] prbs [WIDTH:0];
logic [WIDTH-1:0]     prbs_xor_a;
logic [WIDTH-1:0]     prbs_xor_b;
logic [WIDTH:1]       prbs_msb;
logic [1:POLY_LENGTH] prbs_reg = {(POLY_LENGTH){1'b1}};

assign prbs[0]   = prbs_reg;

genvar i;
generate for(i=0; i<WIDTH; i=i+1) begin
   assign prbs_xor_a[i] = prbs[i][POLY_TAP] ^ prbs[i][POLY_LENGTH];
   assign prbs_xor_b[i] = prbs_xor_a[i] ^ data_in[i];
   assign prbs_msb[i+1] = (CHECK_MODE==0) ? prbs_xor_a[i] : data_in[i];
   assign prbs[i+1]     = {prbs_msb[i+1],prbs[i][1:POLY_LENGTH-1]};
end
endgenerate

always_ff @ (posedge clk) begin
   if(rst) begin
      prbs_reg <= {POLY_LENGTH{1'b1}};
      data_out <= {WIDTH{1'b1}};
   end else begin
      prbs_reg <= prbs[WIDTH];
      data_out <= prbs_xor_b;   
   end
end

endmodule

context.rtl/fsm.sv:
module fsm (
    input clk,
    input reset,
    input [3:0] input_signal,
    input [63:0] config_state_map_flat,
    input [127:0] config_transition_map_flat,
    output reg [7:0] current_state,
    output reg error_flag,
    output reg [7:0] operation_result
);


    reg [7:0] state;
    reg [7:0] next_state;

    wire [7:0] config_state_map0 = config_state_map_flat[7:0];
    wire [7:0] config_state_map1 = config_state_map_flat[15:8];
    wire [7:0] config_state_map2 = config_state_map_flat[23:16];
    wire [7:0] config_state_map3 = config_state_map_flat[31:24];
    wire [7:0] config_state_map4 = config_state_map_flat[39:32];
    wire [7:0] config_state_map5 = config_state_map_flat[47:40];
    wire [7:0] config_state_map6 = config_state_map_flat[55:48];
    wire [7:0] config_state_map7 = config_state_map_flat[63:56];

    always @(posedge clk or posedge reset) begin
        if (reset) begin
            state         <= 0;
            current_state <= 0;
            error_flag    <= 0;
        end else begin
            state         <= next_state;
            current_state <= next_state;
        end
    end

    always @(*) begin
        integer idx;
        idx = (state << 4) + input_signal; 
        next_state = config_transition_map_flat[(idx * 8) + 7 -: 8];

        if (next_state > 8'h7) begin
            error_flag = 1;
            next_state = 0; 
        end else begin
            error_flag = 0;
        end

        case (state)
            8'h0: operation_result = config_state_map0 + input_signal;
            8'h1: operation_result = config_state_map1 - input_signal;
            8'h2: operation_result = config_state_map2 & input_signal;
            8'h3: operation_result = config_state_map3 | input_signal;
            default: operation_result = 8'hFF; 
        endcase
    end

endmodule

context.verif/fsm_tb.sv:
