context.rtl/rgb_color_space_conversion.sv:
module rgb_color_space_conversion (
    input               clk,
    input               rst,

    // Memory ports to initialize (1/delta) values
    input               we,
    input       [7:0]   waddr,
    input      [24:0]   wdata,

    // Input data with valid.
    input               valid_in,
    input       [7:0]   r_component,
    input       [7:0]   g_component,
    input       [7:0]   b_component,

    // HSV Output values
    output reg [11:0]   hsv_channel_h,  // Output in fx10.2 format, degree value = (hsv_channel_h)/4
    output reg [12:0]   hsv_channel_s,  // Output in fx1.12 format. % value = (hsv_channel_s/4096)*100
    output reg [11:0]   hsv_channel_v,  // % value = (hsv_channel_v/255) * 100

    // HSL Output values
    output reg [11:0]   hsl_channel_h,  // Output in fx10.2 format, degree value = (hsl_channel_h)/4
    output reg [12:0]   hsl_channel_s,  // Output in fx1.12 format. % value = (hsl_channel_s/4096)*100
    output reg [11:0]   hsl_channel_l,  // % value = (hsl_channel_l/255) * 100

    output reg          valid_out
);

    reg      [7:0]    valid_in_shreg;
    reg signed [12:0] pre_hue;
    reg      [11:0]   i_max, i_min, stage1_max, stage1_min, stage1_b;
    reg       [8:0]   hue_degrees_offset;
    reg       [2:0]   i_max_r, i_max_g, i_max_b;

    reg      [12:0]   g_sub_b_shreg;
    reg      [12:0]   b_sub_r_shreg;
    reg      [12:0]   r_sub_g_shreg;
    reg      [11:0]   i_max_shreg;
    reg      [11:0]   i_min_shreg;

    wire     [12:0]   saturation_result;
    wire     [12:0]   hsl_saturation_result;
    wire     [24:0]   inv_i_max, inv_delta_i;
    wire     [11:0]   almost_hue;
    reg signed [11:0] hue;

    assign valid_out = valid_in_shreg[7];
    assign hsv_channel_h = hue;
    assign hsv_channel_s = saturation_result;
    assign hsv_channel_v = i_max;

    assign hsl_channel_h = hue;
    assign hsl_channel_s = hsl_saturation_result;

    reg signed [12:0] g_sub_b, b_sub_r, r_sub_g, delta_i, max_plus_min;

    // Internally upscaled 12-bit values for fixed point precision
    wire [11:0] r_scaled = {4'b0000, r_component}; // Scale 8-bit to 12-bit
    wire [11:0] g_scaled = {4'b0000, g_component}; // Scale 8-bit to 12-bit
    wire [11:0] b_scaled = {4'b0000, b_component}; // Scale 8-bit to 12-bit

    // Subtraction logic, to find difference of inputs and delta value
    // Calculate g-b, b-r, r-g and max-min values to be used in h calculation
    always @(posedge clk or posedge rst) begin
        if (rst) begin
            g_sub_b <= 13'd0;
            b_sub_r <= 13'd0;
            r_sub_g <= 13'd0;
            delta_i <= 13'd0;
            max_plus_min <= 13'd0;
        end else begin
            g_sub_b <= $signed(g_scaled) - $signed(b_scaled);
            b_sub_r <= $signed(b_scaled) - $signed(r_scaled);
            r_sub_g <= $signed(r_scaled) - $signed(g_scaled);
            delta_i <= $signed(i_max) - $signed(i_min);
            max_plus_min <= $signed(i_max) + $signed(i_min);
        end
    end

    ////////////////////////////// HSL Channel Calculations ///////
    always @(posedge clk or posedge rst) begin
        if (rst) begin
            hsl_channel_l <= 'd0;
        end else begin
            hsl_channel_l <= max_plus_min[12:1]; // Divide by 2.. ignoring fraction part.
        end
    end

    wire [8:0]  double_L;               // 9-bit to handle overflow (max 510)
    reg  [8:0]  abs_2L_255;             // Absolute difference result
    wire [8:0]  hsl_channel_s_denom;    // 1-|2L-1|. This is Denominator of s formula
    wire [24:0] inv_hsl_channel_s_denom;// inverse of (1-|2L-1|).

    assign double_L = max_plus_min[8:0]; // Equivalent to L * 2 = i_max+i_min

    always @(posedge clk or posedge rst) begin
        if (rst) begin
            abs_2L_255 <= 'd0;
        end else begin
            if (double_L >= 9'd255)
                abs_2L_255 <= (double_L - 9'd255);
            else
                abs_2L_255 <= (9'd255 - double_L);
        end
    end

    assign hsl_channel_s_denom = 9'd255 - abs_2L_255;  // abs_2L_255 is always <= 254

    // Memory to store 1/delta values (256 values)
    // 0,1/1,1/2,1/3...1/255)
    // These values are used to multiply with (g-b)/(b-r)/(r-g) for calculation
    // h value. It is easy to store inverse values and do multiplication
    // than division.
    multi_port_ram inverse_component_inst (
        .clk(clk),
        .we(we),
        .waddr(waddr),
        .wdata(wdata),
        .raddr_a(i_max[7:0]),
        .rdata_a(inv_i_max),
        .raddr_b(delta_i[7:0]),
        .rdata_b(inv_delta_i),
        .raddr_c(hsl_channel_s_denom[7:0]),
        .rdata_c(inv_hsl_channel_s_denom)
    );

    // Pre hue constant multiplier for h calculation
    // Multiply with 60 degrees.
    // Used 2 stage pipeline
    localparam signed [6:0] CONST_60 = 7'd60;
    reg signed [18:0] pre_hue_prod;

    always @(posedge clk or posedge rst) begin
        if (rst) begin
            pre_hue_prod <= 19'd0;
        end else begin
            pre_hue_prod <= pre_hue * CONST_60;
        end
    end

    // HSL Channel Saturation calculation multiplier
    saturation_mult hsl_saturation_mult_0 (
        .clk(clk),
        .rst(rst),
        .a(inv_hsl_channel_s_denom), // Read inverted value from memory port1
        .b(delta_i[7:0]),   // Delta value (max-min)
        .result(hsl_saturation_result)
    );

    // Saturation calculation multiplier
    saturation_mult hsv_saturation_mult_0 (
        .clk(clk),
        .rst(rst),
        .a(inv_i_max),               // Read inverted value from memory port1
        .b(delta_i[7:0]),  // Delta value (max-min)
        .result(saturation_result)
    );

    // h value calculation multiplier
    hue_mult hue_mult_inst (
        .clk(clk),
        .rst(rst),
        .dataa(pre_hue_prod),        // Product from constant 60 multiplication
        .datab(inv_delta_i),         // Read inverted data from memory port2
        .result(almost_hue)
    );

    // Final h value addition logic
    always @(posedge clk or posedge rst) begin
        if (rst)
            hue <= 'd0;
        else
            hue <= $signed(almost_hue) + $signed({1'b0, {hue_degrees_offset, 2'd0}});
    end

    // Pipelining registers to help in each stage of data processing
    // Help with multiplications and additions
    always @(posedge clk or posedge rst) begin
        if (rst) begin
            // Reset all registers and shift registers
            g_sub_b_shreg <= 0;
            b_sub_r_shreg <= 0;
            r_sub_g_shreg <= 0;
            i_max_shreg <= 0;
            i_min_shreg <= 0;
        end else begin
            // Normal operation when reset is not asserted
            g_sub_b_shreg <= g_sub_b;
            b_sub_r_shreg <= b_sub_r;
            r_sub_g_shreg <= r_sub_g;
            i_max_shreg <= i_max;
            i_min_shreg <= i_min;
        end
    end

    // Calculate max and min values
    // Shift valid in for total latency cycles
    // and assign to output valid when output data is ready
    always @(posedge clk or posedge rst) begin
        if (rst) begin
            valid_in_shreg <= 0;
            stage1_max <= 0;
            stage1_min <= 0;
            stage1_b <= 0;
            i_max_r <= 0;
            i_max_g <= 0;
            i_max_b <= 0;
            i_max <= 0;
            i_min <= 0;
        end else begin
            valid_in_shreg <= {valid_in_shreg[6:0], valid_in};
            i_max_r[2] <= i_max_r[1];
            i_max_g[2] <= i_max_g[1];
            i_max_b[2] <= i_max_b[1];

            if (valid_in) begin
                stage1_b <= b_component;
                if (r_component > g_component) begin
                    stage1_max <= r_component;
                    stage1_min <= g_component;
                    i_max_r[0] <= 1;
                    i_max_g[0] <= 0;
                    i_max_b[0] <= 0;
                end else begin
                    stage1_max <= g_component;
                    stage1_min <= r_component;
                    i_max_r[0] <= 0;
                    i_max_g[0] <= 1;
                    i_max_b[0] <= 0;
                end
            end

            if (valid_in_shreg[0]) begin
                if (stage1_max > stage1_b) begin
                    i_max      <= stage1_max;
                    i_max_r[1] <= i_max_r[0];
                    i_max_g[1] <= i_max_g[0];
                    i_max_b[1] <= i_max_b[0];
                end else begin
                    i_max      <= stage1_b;
                    i_max_r[1] <= 0;
                    i_max_g[1] <= 0;
                    i_max_b[1] <= 1;
                end

                if (stage1_min < stage1_b) i_min <= stage1_min;
                else                       i_min <= stage1_b;
            end
        end
    end

    // Select degree value to add for h calculation
    always @(posedge clk or posedge rst) begin
        if (rst) begin
            pre_hue <= 'd0;
            hue_degrees_offset <= 'd0;
        end else begin
            if (valid_in_shreg[2]) begin
                if (i_max_shreg == i_min_shreg) begin
                    pre_hue <= 0;
                    hue_degrees_offset <= 9'd0;
                end else if ((i_max_r[2]) && (~g_sub_b_shreg[12])) begin
                    pre_hue <= g_sub_b_shreg;
                    hue_degrees_offset <= 9'd0;
                end else if ((i_max_r[2]) && (g_sub_b_shreg[12])) begin
                    pre_hue <= g_sub_b_shreg;
                    hue_degrees_offset <= 9'd360;
                end else if (i_max_g[2]) begin
                    pre_hue <= b_sub_r_shreg;
                    hue_degrees_offset <= 9'd120;
                end else if (i_max_b[2]) begin
                    pre_hue <= r_sub_g_shreg;
                    hue_degrees_offset <= 9'd240;
                end
            end
        end
    end
endmodule

// Write port to initialize 1/delta values, and two read ports.
// 1. Read port --> read 1/delta address
// 2. Read port --> read 1/max address
// 3. Read port --> read 1/hsl_channel_s_denom
// Memory is used to store inverted values (0 to 1/255) such that multiplication can be
// performed easily than division.
module multi_port_ram (
    input               clk,
    input               we,
    input       [7:0]   waddr,
    input      [24:0]   wdata,
    input       [7:0]   raddr_a,
    output reg [24:0]   rdata_a,
    input       [7:0]   raddr_b,
    output reg [24:0]   rdata_b,
    input       [7:0]   raddr_c,
    output reg [24:0]   rdata_c
);

    reg [24:0] ram [0:255];

    always @(posedge clk) begin
        if (we) begin
            ram[waddr] <= wdata;
        end
    end

    always @(posedge clk) begin
        rdata_a <= ram[raddr_a];
    end

    always @(posedge clk) begin
        rdata_b <= ram[raddr_b];
    end

    always @(posedge clk) begin
        rdata_c <= ram[raddr_c];
    end
endmodule

// This is used to multiply delta value with inverted cmax value from memory
// (used to calculate s, saturation)
module saturation_mult (
    input  wire         clk,
    input  wire         rst,
    input  wire [24:0]  a,
    input  wire [7:0]   b,
    output [12:0]       result
);

    reg [24:0] A_reg;
    reg [7:0] B_reg;
    reg [30:0] mult_result;
    reg [18:0] rounded_result;

    always @(posedge clk or posedge rst) begin
        if (rst) begin
            A_reg <= 25'd0;
            B_reg <= 8'd0;
        end else begin
            A_reg <= a;
            B_reg <= b;
        end
    end

    always @(posedge clk or posedge rst) begin
        if (rst) begin
            mult_result <= 'd0;
        end else begin
            mult_result <= A_reg * B_reg;
        end
    end

    always @(posedge clk or posedge rst) begin
        if (rst) begin
            rounded_result <= 'd0;
        end else begin
            rounded_result <= mult_result[30:12] + mult_result[11];
        end
    end

    assign result = rounded_result[12:0];
endmodule

//used for h, hue calculation
module hue_mult (
    input               clk,
    input               rst,
    input signed [18:0] dataa,
    input      [24:0]   datab,
    output reg signed [11:0] result
);

    reg signed [43:0] mult_stage1;

    always @(posedge clk or posedge rst) begin
        if (rst)
            mult_stage1 <= 44'd0;
        else
            mult_stage1 <= $signed(dataa) * $signed({1'b0, datab});
    end

    always @(posedge clk or posedge rst) begin
        if (rst)
            result <= 12'd0;
        else
            result <= mult_stage1[33:22];
    end
endmodule

context.rtl/APBGlobalHistoryRegister.v:
module APBGlobalHistoryRegister (
    // APB clock & reset
    input  wire         pclk,  //APB clock input used for all synchronous operations.
    input  wire         presetn,  // Asynchronous reset for system initialization.

    // APB signals
    input  wire [9:0]   paddr,  //Address bus for accessing internal CSR registers.
    input  wire         pselx,  //APB select signal, indicates CSR/memory selection.
    input  wire         penable,  //APB enable signal, marks transaction progression.
    input  wire         pwrite, //Write-enable signal. High for writes, low for reads.
    input  wire [7:0]   pwdata, // Write data bus for sending data to CSR registers or memory.
    input  wire         history_shift_valid,  
    input  wire         clk_gate_en,  
    output reg          pready, // Ready signal, driven high to indicate the end of a transaction.
    output reg  [7:0]   prdata, // Read data bus for retrieving data from the module.
    output reg          pslverr,  //Error signal, asserted on invalid addresses.
    output reg          history_full, 
    output reg          history_empty,  
    output reg          error_flag, 
    output reg          interrupt_full, 
    output reg          interrupt_error 
);

    //---------------------------------------------
    // Parameter Definitions
    //---------------------------------------------
    // Register address map
    localparam ADDR_CTRL_REG     = 10'h0;  // 0x0
    localparam ADDR_TRAIN_HIS    = 10'h1;  // 0x1
    localparam ADDR_PREDICT_HIS  = 10'h2;  // 0x2

    localparam WIDTH             = 8;

    //---------------------------------------------
    // Internal Registers (CSR)
    //---------------------------------------------
    reg [WIDTH-1:0] control_register;
    reg [WIDTH-1:0] train_history;
    reg [WIDTH-1:0] predict_history;
    //---------------------------------------------
    // Internal wires
    //---------------------------------------------
    wire        predict_valid;
    wire        predict_taken;
    wire        train_mispredicted;
    wire        train_taken;
    //---------------------------------------------
    // APB Read/Write Logic
    //---------------------------------------------
    wire apb_valid;
    assign apb_valid = pselx & penable;    // Indicates active APB transaction
    assign pclk_gated = !clk_gate_en&pclk;
    // By spec, no wait states => PREADY always high after reset
    always @(posedge pclk_gated or negedge presetn) begin
      if (!presetn) begin
        pready   <= 1'b0;
        pslverr  <= 1'b0;
      end else begin
        // PREADY is always asserted (no wait states) once out of reset
        pready   <= 1'b1;
        // If transaction is valid, check address range
        if (apb_valid) begin
          // Check if address is valid (0x0 through 0x2 are used, everything else => PSLVERR)
          if (paddr > ADDR_PREDICT_HIS) begin
            pslverr <= 1'b1;
          end
          else begin
            pslverr  <= 1'b0;
          end
        end
      end
    end

    // Handle writes to CSR or memory
    // Note: The design writes immediately in the cycle when penable=1.
    always @(posedge pclk_gated or negedge presetn) begin
      if (!presetn) begin
        // Reset all registers
        control_register  <= 0;
        train_history     <= 0;
      end else begin
        if (apb_valid && pwrite) begin
          case (paddr)
              ADDR_CTRL_REG:    control_register[3:0]  <= pwdata[3:0];
              ADDR_TRAIN_HIS:   train_history[6:0]     <= pwdata[6:0];
              // If the address is outside defined range => PSLVERR is set, no write
          endcase
        end
      end
    end

    // Handle read from CSR or memory
    always @(posedge pclk_gated or negedge presetn) begin
      if (!presetn) begin
        prdata <= 0;
      end 
      else begin
        if (apb_valid) begin
          case (paddr)
            ADDR_CTRL_REG:    prdata <= {4'b0,control_register[3:0]};
            ADDR_TRAIN_HIS:   prdata <= {1'b0,train_history[6:0]};
            ADDR_PREDICT_HIS: prdata <= predict_history;
            default:          prdata <= 0; // Invalid => PSLVERR, but can set prdata to 0
          endcase
        end
        else begin
          // When no valid read, clear prdata
          prdata <= 0;
        end
      end
    end


    //---------------------------------------------
    // GHSR Behavior
    //---------------------------------------------

    assign  predict_valid       = control_register[0];     // valid branch prediction
    assign  predict_taken       = control_register[1];     // predicted direction (1=taken, 0=not taken)
    assign  train_mispredicted  = control_register[2];     // branch misprediction occurred
    assign  train_taken         = control_register[3];     // actual branch direction for mispredicted branch



    always @(posedge history_shift_valid or negedge presetn) begin
      if (!presetn) begin
        // 1) active low Asynchronous reset
        //    Clear the entire history register.
        predict_history <= 0;
      end
      else begin
        // 2) Misprediction Handling (highest priority)
        //    If a misprediction is flagged, restore the old history from train_history
        //    and incorporate the correct outcome (train_taken) as the newest bit.
        if (train_mispredicted) begin
          predict_history <= {train_history[WIDTH-2:0], train_taken};
        end
        // 3) Normal Prediction Update
        //    If the prediction is valid and there is no misprediction,
        //    shift in predict_taken at the LSB (bit[0] is the youngest branch).
        else if (predict_valid) begin
          // "Shifting in from the LSB" while keeping the newest branch in predict_history[0]
          // is typically done by moving predict_history[31:1] up one bit
          // and placing predict_taken in bit[0].
          predict_history <= {predict_history[WIDTH-2:0], predict_taken};
        end
      end
    end

    always @(*) begin
      error_flag=pslverr;
      interrupt_error=pslverr;
      if(predict_history==8'hff) begin
        history_full=1'b1;
        interrupt_full=1'b1;
        history_empty=1'b0;
      end
      else if (predict_history==8'h00) begin
        history_full=1'b0;
        interrupt_full=1'b0;
        history_empty=1'b1;
      end
      else begin
        history_full=1'b0;
        interrupt_full=1'b0;
        history_empty=1'b0;
      end
    end

endmodule

context.verif/signed_comparator_tb.sv:
`timescale 1ns/1ps

module tb_signed_comparator;

reg clk;
reg rst_n;
reg enable;
reg bypass;
reg signed [15:0] a;
reg signed [15:0] b;
wire gt, lt, eq;

localparam DATA_WIDTH       = 16;
localparam REGISTER_OUTPUT  = 1;
localparam ENABLE_TOLERANCE = 1;
localparam TOLERANCE        = 2;
localparam SHIFT_LEFT       = 1;

signed_comparator #(
  .DATA_WIDTH(DATA_WIDTH),
  .REGISTER_OUTPUT(REGISTER_OUTPUT),
  .ENABLE_TOLERANCE(ENABLE_TOLERANCE),
  .TOLERANCE(TOLERANCE),
  .SHIFT_LEFT(SHIFT_LEFT)
) dut (
  .clk(clk),
  .rst_n(rst_n),
  .enable(enable),
  .bypass(bypass),
  .a(a),
  .b(b),
  .gt(gt),
  .lt(lt),
  .eq(eq)
);

always #5 clk = ~clk;

initial begin
  clk = 0;
  rst_n = 0;
  enable = 0;
  bypass = 0;
  a = 0;
  b = 0;
  repeat(2) @(posedge clk);
  rst_n = 1;
  repeat(2) @(posedge clk);

  test_case( 100,  100, 0, 1);
  test_case(  10,  -10, 0, 1);
  test_case(  -5,    5, 0, 1);
  test_case(  50,   52, 0, 1);  // near difference -> tolerance
  test_case(  51,   52, 0, 1);  // difference 1 -> eq due to TOLERANCE=2
  test_case(  53,   50, 0, 1);  // difference 3 -> not within tolerance
  test_case( 123, -123, 1, 1);  // bypass => eq=1
  test_case( 500, -500, 0, 0);  // enable=0 => eq=0,gt=0,lt=0
  repeat(2) @(posedge clk);

  integer i;
  for (i = 0; i < 5; i = i + 1) begin
    test_case($random, $random, $random%2, $random%2);
  end

  $finish;
end

task test_case;
  input signed [15:0] a_val;
  input signed [15:0] b_val;
  input bypass_val;
  input enable_val;
begin
  a = a_val;
  b = b_val;
  bypass = bypass_val;
  enable = enable_val;
  @(posedge clk);
  @(posedge clk);

  check_output(a_val, b_val, bypass_val, enable_val);
end
endtask

task check_output;
  input signed [15:0] a_val;
  input signed [15:0] b_val;
  input bypass_val;
  input enable_val;
  reg signed [DATA_WIDTH-1:0] a_shift, b_shift;
  reg signed [DATA_WIDTH:0] diff_abs;
  reg exp_gt, exp_lt, exp_eq;
begin
  a_shift = a_val <<< SHIFT_LEFT;
  b_shift = b_val <<< SHIFT_LEFT;
  diff_abs = (a_shift - b_shift);
  if (diff_abs < 0) diff_abs = -diff_abs;

  if (bypass_val) begin
    exp_gt = 0; exp_lt = 0; exp_eq = 1;
  end else if (!enable_val) begin
    exp_gt = 0; exp_lt = 0; exp_eq = 0;
  end else begin
    if (ENABLE_TOLERANCE && (diff_abs <= TOLERANCE)) begin
      exp_gt = 0; exp_lt = 0; exp_eq = 1;
    end else if (a_shift > b_shift) begin
      exp_gt = 1; exp_lt = 0; exp_eq = 0;
    end else if (a_shift < b_shift) begin
      exp_gt = 0; exp_lt = 1; exp_eq = 0;
    end else begin
      exp_gt = 0; exp_lt = 0; exp_eq = 1;
    end
  end

  if (gt !== exp_gt || lt !== exp_lt || eq !== exp_eq) begin
    $display("Time=%0t FAIL: a=%d b=%d bypass=%b en=%b SHIFT_LEFT=%0d TOL=%0d => gt=%b lt=%b eq=%b (exp: %b %b %b)",
      $time, a_val, b_val, bypass_val, enable_val, SHIFT_LEFT, TOLERANCE,
      gt, lt, eq, exp_gt, exp_lt, exp_eq);
  end else begin
    $display("Time=%0t PASS: a=%d b=%d bypass=%b en=%b SHIFT_LEFT=%0d TOL=%0d => gt=%b lt=%b eq=%b",
      $time, a_val, b_val, bypass_val, enable_val, SHIFT_LEFT, TOLERANCE, gt, lt, eq);
  end
end
endtask

endmodule

context.rtl/sorting_engine.sv:
module sorting_engine #(parameter WIDTH = 8)(
    input                     clk,
    input                     rst,
    input                     start,      // Start the sort when start=1
    input  [8*WIDTH-1:0]      in_data,    // 8 inputs, each WIDTH bits
    output reg                done,
    output reg [8*WIDTH-1:0]  out_data    // 8 outputs, sorted ascending
);

  // FSM state encoding
  localparam IDLE      = 3'd0,
             LOAD      = 3'd1,
             SORT_PAIRS= 3'd2,
             MERGE_2_1 = 3'd3,  // Merge first two sorted pairs into a 4-element group
             MERGE_2_2 = 3'd4,  // Merge second two sorted pairs into a 4-element group
             MERGE_4   = 3'd5,  // Merge the two 4-element groups into final 8-element sorted list
             DONE      = 3'd6;

  reg [2:0] state;

  // Internal storage for data at different stages.
  reg [WIDTH-1:0] stage0 [7:0];           // Loaded input data
  reg [WIDTH-1:0] sorted_pairs [7:0];     // After pair compare–swap
  reg [WIDTH-1:0] merge4_right [3:0];      // First 4–element sorted group (from indices 0–3)
  reg [WIDTH-1:0] merge4_left [3:0];     // Second 4–element sorted group (from indices 4–7)
  reg [WIDTH-1:0] final_sorted [7:0];     // Final 8–element sorted result

  // Merge pointers and counter used for sequential merging
  reg [3:0] merge_count;  // Counts how many outputs have been merged in current merge stage
  reg [2:0] ptr1, ptr2;   // Pointers for the two arrays being merged

  integer i; // loop variable for for–loops

  always @(posedge clk or posedge rst) begin
    if (rst) begin
      state      <= IDLE;
      done       <= 0;
      out_data   <= 0;
      merge_count<= 0;
      ptr1       <= 0;
      ptr2       <= 0;
    end else begin
      case (state)
        // Wait for the start signal.
        IDLE: begin
          done <= 0;
          if (start)
            state <= LOAD;
        end

        // Capture the 8 input elements from the flat bus into an array.
        LOAD: begin
          for (i = 0; i < 8; i = i + 1) begin
            stage0[i] <= in_data[i*WIDTH +: WIDTH];
          end
          state <= SORT_PAIRS;
        end

        // Stage 1: Compare-swap each adjacent pair.
        // The 8 numbers are divided into 4 pairs: indices {0,1}, {2,3}, {4,5}, {6,7}.
        SORT_PAIRS: begin
          // Pair 0
          if (stage0[0] <= stage0[1]) begin
            sorted_pairs[0] <= stage0[0];
            sorted_pairs[1] <= stage0[1];
          end else begin
            sorted_pairs[0] <= stage0[1];
            sorted_pairs[1] <= stage0[0];
          end
          // Pair 1
          if (stage0[2] <= stage0[3]) begin
            sorted_pairs[2] <= stage0[2];
            sorted_pairs[3] <= stage0[3];
          end else begin
            sorted_pairs[2] <= stage0[3];
            sorted_pairs[3] <= stage0[2];
          end
          // Pair 2
          if (stage0[4] <= stage0[5]) begin
            sorted_pairs[4] <= stage0[4];
            sorted_pairs[5] <= stage0[5];
          end else begin
            sorted_pairs[4] <= stage0[5];
            sorted_pairs[5] <= stage0[4];
          end
          // Pair 3
          if (stage0[6] <= stage0[7]) begin
            sorted_pairs[6] <= stage0[6];
            sorted_pairs[7] <= stage0[7];
          end else begin
            sorted_pairs[6] <= stage0[7];
            sorted_pairs[7] <= stage0[6];
          end
          // Initialize pointers for first merge stage (MERGE_2_1)
          ptr1        <= 0;
          ptr2        <= 0;
          merge_count <= 0;
          state       <= MERGE_2_1;
        end

        // Stage 2a: Merge the first two sorted pairs (indices 0–1 and 2–3)
        MERGE_2_1: begin
          // Use ptr1 for sorted_pairs[0:1] and ptr2 for sorted_pairs[2:3].
          if ((ptr1 < 2) && (ptr2 < 2)) begin
            if (sorted_pairs[ptr1] <= sorted_pairs[ptr2+2]) begin
              merge4_right[merge_count] <= sorted_pairs[ptr1];
              ptr1 <= ptr1 + 1;
            end else begin
              merge4_right[merge_count] <= sorted_pairs[ptr2+2];
              ptr2 <= ptr2 + 1;
            end
          end else if (ptr1 < 2) begin
            merge4_right[merge_count] <= sorted_pairs[ptr1];
            ptr1 <= ptr1 + 1;
          end else if (ptr2 < 2) begin
            merge4_right[merge_count] <= sorted_pairs[ptr2+2];
            ptr2 <= ptr2 + 1;
          end
          // Check if 4 elements have been merged.
          if (merge_count == 3) begin
            merge_count <= 0;
            ptr1        <= 0;
            ptr2        <= 0;
            state       <= MERGE_2_2;
          end else begin
            merge_count <= merge_count + 1;
          end
        end

        // Stage 2b: Merge the second two sorted pairs (indices 4–5 and 6–7)
        MERGE_2_2: begin
          // Use ptr1 for sorted_pairs[4:5] and ptr2 for sorted_pairs[6:7].
          if ((ptr1 < 2) && (ptr2 < 2)) begin
            if (sorted_pairs[ptr1+4] <= sorted_pairs[ptr2+6]) begin
              merge4_left[merge_count] <= sorted_pairs[ptr1+4];
              ptr1 <= ptr1 + 1;
            end else begin
              merge4_left[merge_count] <= sorted_pairs[ptr2+6];
              ptr2 <= ptr2 + 1;
            end
          end else if (ptr1 < 2) begin
            merge4_left[merge_count] <= sorted_pairs[ptr1+4];
            ptr1 <= ptr1 + 1;
          end else if (ptr2 < 2) begin
            merge4_left[merge_count] <= sorted_pairs[ptr2+6];
            ptr2 <= ptr2 + 1;
          end
          // Check if merge of 4 elements is complete.
          if (merge_count == 3) begin
            merge_count <= 0;
            ptr1        <= 0;
            ptr2        <= 0;
            state       <= MERGE_4;
          end else begin
            merge_count <= merge_count + 1;
          end
        end

        // Stage 3: Merge the two 4–element groups (merge4_right and merge4_left)
        MERGE_4: begin
          if ((ptr1 < 4) && (ptr2 < 4)) begin
            if (merge4_right[ptr1] <= merge4_left[ptr2]) begin
              final_sorted[merge_count] <= merge4_right[ptr1];
              ptr1 <= ptr1 + 1;
            end else begin
              final_sorted[merge_count] <= merge4_left[ptr2];
              ptr2 <= ptr2 + 1;
            end
          end else if (ptr1 < 4) begin
            final_sorted[merge_count] <= merge4_right[ptr1];
            ptr1 <= ptr1 + 1;
          end else if (ptr2 < 4) begin
            final_sorted[merge_count] <= merge4_left[ptr2];
            ptr2 <= ptr2 + 1;
          end
          // Check if all 8 elements have been merged.
          if (merge_count == 7) begin
            merge_count <= 0;
            state       <= DONE;
          end else begin
            merge_count <= merge_count + 1;
          end
        end

        // Final state: pack the final_sorted array back into out_data and assert done.
        DONE: begin
          for (i = 0; i < 8; i = i + 1) begin
            out_data[i*WIDTH +: WIDTH] <= final_sorted[i];
          end
          done  <= 1;
          // Optionally, return to IDLE (or hold in DONE) so a new sort can begin.
          state <= IDLE;
        end

        default: state <= IDLE;
      endcase
    end
  end

endmodule

module sorting_engine #(parameter WIDTH = 8)(
    input                     clk,
    input                     rst,
    input                     start,      // Start the sort when start=1
    input  [8*WIDTH-1:0]      in_data,    // 8 inputs, each WIDTH bits
    output reg                done,
    output reg [8*WIDTH-1:0]  out_data    // 8 outputs, sorted ascending
);

  // FSM state encoding
  localparam IDLE      = 3'd0,
             LOAD      = 3'd1,
             SORT_PAIRS= 3'd2,
             MERGE_2_1 = 3'd3,  // Merge first two sorted pairs into a 4-element group
             MERGE_2_2 = 3'd4,  // Merge second two sorted pairs into a 4-element group
             MERGE_4   = 3'd5,  // Merge the two 4-element groups into final 8-element sorted list
             DONE      = 3'd6;

  reg [2:0] state;

  // Internal storage for data at different stages.
  reg [WIDTH-1:0] stage0 [7:0];           // Loaded input data
  reg [WIDTH-1:0] sorted_pairs [7:0];     // After pair compare–swap
  reg [WIDTH-1:0] merge4_right [3:0];      // First 4–element sorted group (from indices 0–3)
  reg [WIDTH-1:0] merge4_left [3:0];     // Second 4–element sorted group (from indices 4–7)
  reg [WIDTH-1:0] final_sorted [7:0];     // Final 8–element sorted result

  // Merge pointers and counter used for sequential merging
  reg [3:0] merge_count;  // Counts how many outputs have been merged in current merge stage
  reg [2:0] ptr1, ptr2;   // Pointers for the two arrays being merged

  integer i; // loop variable for for–loops

  always @(posedge clk or posedge rst) begin
    if (rst) begin
      state      <= IDLE;
      done       <= 0;
      out_data   <= 0;
      merge_count<= 0;
      ptr1       <= 0;
      ptr2       <= 0;
    end else begin
      case (state)
        // Wait for the start signal.
        IDLE: begin
          done <= 0;
          if (start)
            state <= LOAD;
        end

        // Capture the 8 input elements from the flat bus into an array.
        LOAD: begin
          for (i = 0; i < 8; i = i + 1) begin
            stage0[i] <= in_data[i*WIDTH +: WIDTH];
          end
          state <= SORT_PAIRS;
        end

        // Stage 1: Compare-swap each adjacent pair.
        // The 8 numbers are divided into 4 pairs: indices {0,1}, {2,3}, {4,5}, {6,7}.
        SORT_PAIRS: begin
          // Pair 0
          if (stage0[0] <= stage0[1]) begin
            sorted_pairs[0] <= stage0[0];
            sorted_pairs[1] <= stage0[1];
          end else begin
            sorted_pairs[0] <= stage0[1];
            sorted_pairs[1] <= stage0[0];
          end
          // Pair 1
          if (stage0[2] <= stage0[3]) begin
            sorted_pairs[2] <= stage0[2];
            sorted_pairs[3] <= stage0[3];
          end else begin
            sorted_pairs[2] <= stage0[3];
            sorted_pairs[3] <= stage0[2];
          end
          // Pair 2
          if (stage0[4] <= stage0[5]) begin
            sorted_pairs[4] <= stage0[4];
            sorted_pairs[5] <= stage0[5];
          end else begin
            sorted_pairs[4] <= stage0[5];
            sorted_pairs[5] <= stage0[4];
          end
          // Pair 3
          if (stage0[6] <= stage0[7]) begin
            sorted_pairs[6] <= stage0[6];
            sorted_pairs[7] <= stage0[7];
          end else begin
            sorted_pairs[6] <= stage0[7];
            sorted_pairs[7] <= stage0[6];
          end
          // Initialize pointers for first merge stage (MERGE_2_1)
          ptr1        <= 0;
          ptr2        <= 0;
          merge_count <= 0;
          state       <= MERGE_2_1;
        end

        // Stage 2a: Merge the first two sorted pairs (indices 0–1 and 2–3)
        MERGE_2_1: begin
          // Use ptr1 for sorted_pairs[0:1] and ptr2 for sorted_pairs[2:3].
          if ((ptr1 < 2) && (ptr2 < 2)) begin
            if (sorted_pairs[ptr1] <= sorted_pairs[ptr2+2]) begin
              merge4_right[merge_count] <= sorted_pairs[ptr1];
              ptr1 <= ptr1 + 1;
            end else begin
              merge4_right[merge_count] <= sorted_pairs[ptr2+2];
              ptr2 <= ptr2 + 1;
            end
          end else if (ptr1 < 2) begin
            merge4_right[merge_count] <= sorted_pairs[ptr1];
            ptr1 <= ptr1 + 1;
          end else if (ptr2 < 2) begin
            merge4_right[merge_count] <= sorted_pairs[ptr2+2];
            ptr2 <= ptr2 + 1;
          end
          // Check if 4 elements have been merged.
          if (merge_count == 3) begin
            merge_count <= 0;
            ptr1        <= 0;
            ptr2        <= 0;
            state       <= MERGE_2_2;
          end else begin
            merge_count <= merge_count + 1;
          end
        end

        // Stage 2b: Merge the second two sorted pairs (indices 4–5 and 6–7)
        MERGE_2_2: begin
          // Use ptr1 for sorted_pairs[4:5] and ptr2 for sorted_pairs[6:7].
          if ((ptr1 < 2) && (ptr2 < 2)) begin
            if (sorted_pairs[ptr1+4] <= sorted_pairs[ptr2+6]) begin
              merge4_left[merge_count] <= sorted_pairs[ptr1+4];
              ptr1 <= ptr1 + 1;
            end else begin
              merge4_left[merge_count] <= sorted_pairs[ptr2+6];
              ptr2 <= ptr2 + 1;
            end
          end else if (ptr1 < 2) begin
            merge4_left[merge_count] <= sorted_pairs[ptr1+4];
            ptr1 <= ptr1 + 1;
          end else if (ptr2 < 2) begin
            merge4_left[merge_count] <= sorted_pairs[ptr2+6];
            ptr2 <= ptr2 + 1;
          end
          // Check if merge of 4 elements is complete.
          if (merge_count == 3) begin
            merge_count <= 0;
            ptr1        <= 0;
            ptr2        <= 0;
            state       <= MERGE_4;
          end else begin
            merge_count <= merge_count + 1;
          end
        end

        // Stage 3: Merge the two 4–element groups (merge4_right and merge4_left)
        MERGE_4: begin
          if ((ptr1 < 4) && (ptr2 < 4)) begin
            if (merge4_right[ptr1] <= merge4_left[ptr2]) begin
              final_sorted[merge_count] <= merge4_right[ptr1];
              ptr1 <= ptr1 + 1;
            end else begin
              final_sorted[merge_count] <= merge4_left[ptr2];
              ptr2 <= ptr2 + 1;
            end
          end else if (ptr1 < 4) begin
            final_sorted[merge_count] <= merge4_right[ptr1];
            ptr1 <= ptr1 + 1;
          end else if (ptr2 < 4) begin
            final_sorted[merge_count] <= merge4_left[ptr2];
            ptr2 <= ptr2 + 1;
          end
          // Check if all 8 elements have been merged.
          if (merge_count == 7) begin
            merge_count <= 0;
            state       <= DONE;
          end else begin
            merge_count <= merge_count + 1;
          end
        end

        // Final state: pack the final_sorted array back into out_data and assert done.
        DONE: begin
          for (i = 0; i < 8; i = i + 1) begin
            out_data[i*WIDTH +: WIDTH] <= final_sorted[i];
          end
          done  <= 1;
          // Optionally, return to IDLE (or hold in DONE) so a new sort can begin.
          state <= IDLE;
        end

        default: state <= IDLE;
      endcase
    end
  end

endmodule

