`timescale 1ns / 1ps

module tb_swizzler;

parameter NUM_LANES = 4;
parameter DATA_WIDTH = 8;
parameter REGISTER_OUTPUT = 0;
parameter ENABLE_PARITY_CHECK = 1;

reg clk;
reg rst_n;
reg bypass;
reg [NUM_LANES*DATA_WIDTH-1:0] data_in;
reg [NUM_LANES*$clog2(NUM_LANES)-1:0] swizzle_map_flat;
wire [NUM_LANES*DATA_WIDTH-1:0] data_out;
wire parity_error;

swizzler #(
    .NUM_LANES(NUM_LANES),
    .DATA_WIDTH(DATA_WIDTH),
    .REGISTER_OUTPUT(REGISTER_OUTPUT),
    .ENABLE_PARITY_CHECK(ENABLE_PARITY_CHECK)
) dut (
    .clk(clk),
    .rst_n(rst_n),
    .bypass(bypass),
    .data_in(data_in),
    .swizzle_map_flat(swizzle_map_flat),
    .data_out(data_out),
    .parity_error(parity_error)
);

logic [DATA_WIDTH-1:0] input_lanes [NUM_LANES-1:0];
logic [DATA_WIDTH-1:0] expected_lanes [NUM_LANES-1:0];
logic [DATA_WIDTH-1:0] output_lanes [NUM_LANES-1:0];
logic [$clog2(NUM_LANES)-1:0] swizzle_map [NUM_LANES-1:0];

integer i;

initial begin
    clk = 0;
    forever #5 clk = ~clk;
end

initial begin
    rst_n = 0;
    bypass = 0;
    data_in = 0;
    swizzle_map_flat = 0;
    #15;
    rst_n = 1;

    test_bypass();
    test_identity();
    test_reverse();
    test_custom();

    $display("All tests completed.");
    $finish;
end

task test_bypass;
    $display("Test Case: Bypass Mode");
    bypass = 1;
    for (i = 0; i < NUM_LANES; i++) begin
        input_lanes[i] = i + 1;
    end
    flatten_input();
    @(posedge clk);
    @(posedge clk);
    unpack_output();
    for (i = 0; i < NUM_LANES; i++) begin
        expected_lanes[i] = input_lanes[i];
    end
    check_output("Bypass");
endtask

task test_identity;
    $display("Test Case: Identity Mapping");
    bypass = 0;
    for (i = 0; i < NUM_LANES; i++) begin
        swizzle_map[i] = i;
        input_lanes[i] = i + 10;
    end
    flatten_swizzle_map();
    flatten_input();
    @(posedge clk);
    @(posedge clk);
    unpack_output();
    for (i = 0; i < NUM_LANES; i++) begin
        expected_lanes[i] = input_lanes[i];
    end
    check_output("Identity");
endtask

task test_reverse;
    $display("Test Case: Reverse Mapping");
    bypass = 0;
    for (i = 0; i < NUM_LANES; i++) begin
        swizzle_map[i] = NUM_LANES - 1 - i;
        input_lanes[i] = i + 20;
    end
    flatten_swizzle_map();
    flatten_input();
    @(posedge clk);
    @(posedge clk);
    unpack_output();
    for (i = 0; i < NUM_LANES; i++) begin
        expected_lanes[i] = input_lanes[NUM_LANES - 1 - i];
    end
    check_output("Reverse");
endtask

task test_custom;
    $display("Test Case: Custom Mapping");
    bypass = 0;
    swizzle_map[0] = 2;
    swizzle_map[1] = 0;
    swizzle_map[2] = 3;
    swizzle_map[3] = 1;
    input_lanes[0] = 8'hAA;
    input_lanes[1] = 8'hBB;
    input_lanes[2] = 8'hCC;
    input_lanes[3] = 8'hDD;
    flatten_swizzle_map();
    flatten_input();
    @(posedge clk);
    @(posedge clk);
    unpack_output();
    expected_lanes[0] = input_lanes[2];
    expected_lanes[1] = input_lanes[0];
    expected_lanes[2] = input_lanes[3];
    expected_lanes[3] = input_lanes[1];
    check_output("Custom");
endtask

task flatten_input;
    for (i = 0; i < NUM_LANES; i++) begin
        data_in[(i+1)*DATA_WIDTH-1 -: DATA_WIDTH] = input_lanes[i];
    end
endtask

task flatten_swizzle_map;
    for (i = 0; i < NUM_LANES; i++) begin
        swizzle_map_flat[(i+1)*$clog2(NUM_LANES)-1 -: $clog2(NUM_LANES)] = swizzle_map[i];
    end
endtask

task unpack_output;
    for (i = 0; i < NUM_LANES; i++) begin
        output_lanes[i] = data_out[(i+1)*DATA_WIDTH-1 -: DATA_WIDTH];
    end
endtask

task check_output(input string test_name);
    for (i = 0; i < NUM_LANES; i++) begin
        if (output_lanes[i] !== expected_lanes[i]) begin
            $display("[%s] ERROR: Lane %0d: Expected %h, Got %h", test_name, i, expected_lanes[i], output_lanes[i]);
        end else begin
            $display("[%s] PASS: Lane %0d = %h", test_name, i, output_lanes[i]);
        end
    end
    if (ENABLE_PARITY_CHECK) begin
        if (parity_error) begin
            $display("[%s] PARITY ERROR DETECTED", test_name);
        end else begin
            $display("[%s] Parity check passed", test_name);
        end
    end
endtask

endmodule

context.rtl/swizzler.sv:
`timescale 1ns/1ps

module swizzler #(
  parameter integer NUM_LANES           = 4,
  parameter integer DATA_WIDTH          = 8,
  parameter integer REGISTER_OUTPUT     = 0,
  parameter integer ENABLE_PARITY_CHECK = 0,
  parameter integer OP_MODE_WIDTH       = 2,
  parameter integer SWIZZLE_MAP_WIDTH   = $clog2(NUM_LANES)+1
)(
  input  wire                           clk,
  input  wire                           rst_n,
  input  wire                           bypass,
  input  wire [NUM_LANES*DATA_WIDTH-1:0]  data_in,
  input  wire [NUM_LANES*SWIZZLE_MAP_WIDTH-1:0] swizzle_map_flat,
  input  wire [OP_MODE_WIDTH-1:0]         operation_mode,
  output reg  [NUM_LANES*DATA_WIDTH-1:0]  data_out,
  output reg                            parity_error,
  output reg                            invalid_mapping_error
);

  // Unpack data_in into lanes
  wire [DATA_WIDTH-1:0] lane_in [0:NUM_LANES-1];
  genvar gi;
  generate
    for (gi = 0; gi < NUM_LANES; gi = gi + 1) begin : UNPACK_INPUT
      assign lane_in[gi] = data_in[DATA_WIDTH*(gi+1)-1 : DATA_WIDTH*gi];
    end
  endgenerate

  // Unpack swizzle_map_flat into swizzle_map array
  wire [SWIZZLE_MAP_WIDTH-1:0] swizzle_map [0:NUM_LANES-1];
  generate
    for (gi = 0; gi < NUM_LANES; gi = gi + 1) begin : UNPACK_SWIZZLE
      assign swizzle_map[gi] = swizzle_map_flat[SWIZZLE_MAP_WIDTH*(gi+1)-1 : SWIZZLE_MAP_WIDTH*gi];
    end
  endgenerate

  // Invalid mapping detection
  wire [NUM_LANES-1:0] invalid_map_flag;
  generate
    for (gi = 0; gi < NUM_LANES; gi = gi + 1) begin : INVALID_CHECK
      assign invalid_map_flag[gi] = (swizzle_map[gi] >= NUM_LANES) ? 1'b1 : 1'b0;
    end
  endgenerate
  wire invalid_mapping_detected = |invalid_map_flag;

  // Remap lanes according to swizzle_map or bypass
  wire [DATA_WIDTH-1:0] swizzled [0:NUM_LANES-1];
  generate
    for (gi = 0; gi < NUM_LANES; gi = gi + 1) begin : REMAP
      // Use lower bits of swizzle_map to index valid lanes.
      assign swizzled[gi] = bypass ? lane_in[gi] : lane_in[ swizzle_map[gi][$clog2(NUM_LANES)-1:0] ];
    end
  endgenerate

  // Pipeline Stage 1: Capture swizzled lanes
  reg [DATA_WIDTH-1:0] swizzle_reg [0:NUM_LANES-1];
  integer i;
  always @(posedge clk or negedge rst_n) begin
    if (!rst_n)
      for (i = 0; i < NUM_LANES; i = i + 1)
        swizzle_reg[i] <= {DATA_WIDTH{1'b0}};
    else
      for (i = 0; i < NUM_LANES; i = i + 1)
        swizzle_reg[i] <= swizzled[i];
  end

  // Pipeline Stage 2: Capture operation mode and invalid mapping status
  reg [OP_MODE_WIDTH-1:0] op_reg;
  reg op_invalid_reg;
  always @(posedge clk or negedge rst_n) begin
    if (!rst_n) begin
      op_reg <= {OP_MODE_WIDTH{1'b0}};
      op_invalid_reg <= 1'b0;
    end else begin
      op_reg <= operation_mode;
      op_invalid_reg <= invalid_mapping_detected;
    end
  end

  // Bit reversal function
  function automatic [DATA_WIDTH-1:0] bit_reverse;
    input [DATA_WIDTH-1:0] in;
    integer k;
    begin
      bit_reverse = {DATA_WIDTH{1'b0}};
      for (k = 0; k < DATA_WIDTH; k = k + 1)
        bit_reverse[k] = in[DATA_WIDTH-1-k];
    end
  endfunction

  // Pipeline Stage 3: Final output stage with bit reversal
  reg [DATA_WIDTH-1:0] final_reg [0:NUM_LANES-1];
  integer m;
  always @(posedge clk or negedge rst_n) begin
    if (!rst_n)
      for (m = 0; m < NUM_LANES; m = m + 1)
        final_reg[m] <= {DATA_WIDTH{1'b0}};
    else
      for (m = 0; m < NUM_LANES; m = m + 1)
        final_reg[m] <= bit_reverse(swizzle_reg[m]);
  end

  // Pack final_reg into a flat output vector
  wire [NUM_LANES*DATA_WIDTH-1:0] final_packed;
  genvar q;
  generate
    for (q = 0; q < NUM_LANES; q = q + 1) begin : PACK_FINAL
      assign final_packed[DATA_WIDTH*(q+1)-1 : DATA_WIDTH*q] = final_reg[q];
    end
  endgenerate

  generate
    if (REGISTER_OUTPUT) begin : REG_FINAL
      always @(posedge clk or negedge rst_n) begin
        if (!rst_n)
          data_out <= {NUM_LANES*DATA_WIDTH{1'b0}};
        else
          data_out <= final_packed;
      end
    end else begin : COMB_FINAL
      always @* begin
        data_out = final_packed;
      end
    end
  endgenerate

  // Updated parity error calculation using a generate block
  generate
    if (ENABLE_PARITY_CHECK) begin : GEN_PARITY
      // Calculate parity from final_reg if parity check is enabled.
      wire [NUM_LANES-1:0] final_parity;
      genvar p;
      for (p = 0; p < NUM_LANES; p = p + 1) begin : PARITY_CALC
        assign final_parity[p] = ^final_reg[p];
      end
      wire computed_parity = |final_parity;
      always @* begin
        parity_error = computed_parity;
      end
    end else begin : NO_PARITY
      // Drive parity_error to 0 when parity check is disabled.
      always @* begin
        parity_error = 1'b0;
      end
    end
  endgenerate

  // Pass the invalid mapping flag
  always @* begin
    invalid_mapping_error = op_invalid_reg;
  end

endmodule

context.verif/swizzler_tb.sv:
`timescale 1ns/1ps

module tb_swizzler;
  parameter NUM_LANES = 4;
  parameter DATA_WIDTH = 8;
  parameter REGISTER_OUTPUT = 1;
  parameter ENABLE_PARITY_CHECK = 0;
  parameter OP_MODE_WIDTH = 2;
  parameter SWIZZLE_MAP_WIDTH = 3;

  reg clk;
  reg rst_n;
  reg bypass;
  reg [NUM_LANES*DATA_WIDTH-1:0] data_in;
  reg [NUM_LANES*SWIZZLE_MAP_WIDTH-1:0] swizzle_map_flat;
  reg [OP_MODE_WIDTH-1:0] operation_mode;
  wire [NUM_LANES*DATA_WIDTH-1:0] data_out;
  wire parity_error;
  wire invalid_mapping_error;

  swizzler #(
    .NUM_LANES(NUM_LANES),
    .DATA_WIDTH(DATA_WIDTH),
    .REGISTER_OUTPUT(REGISTER_OUTPUT),
    .ENABLE_PARITY_CHECK(ENABLE_PARITY_CHECK),
    .OP_MODE_WIDTH(OP_MODE_WIDTH),
    .SWIZZLE_MAP_WIDTH(SWIZZLE_MAP_WIDTH)
  ) dut (
    .clk(clk),
    .rst_n(rst_n),
    .bypass(bypass),
    .data_in(data_in),
    .swizzle_map_flat(swizzle_map_flat),
    .operation_mode(operation_mode),
    .data_out(data_out),
    .parity_error(parity_error),
    .invalid_mapping_error(invalid_mapping_error)
  );

  reg [DATA_WIDTH-1:0] expected [0:NUM_LANES-1];
  reg [DATA_WIDTH-1:0] out_lane [0:NUM_LANES-1];
  integer i;

  function [DATA_WIDTH-1:0] bit_reverse;
    input [DATA_WIDTH-1:0] in;
    integer j;
    reg [DATA_WIDTH-1:0] out;
    begin
      out = 0;
      for(j = 0; j < DATA_WIDTH; j = j + 1)
        out[j] = in[DATA_WIDTH-1-j];
      bit_reverse = out;
    end
  endfunction

  function [DATA_WIDTH-1:0] get_lane;
    input integer index;
    begin
      get_lane = data_out[DATA_WIDTH*(index+1)-1 -: DATA_WIDTH];
    end
  endfunction

  initial begin
    clk = 0;
    forever #5 clk = ~clk;
  end

  initial begin
    rst_n = 0;
    bypass = 0;
    data_in = 0;
    swizzle_map_flat = 0;
    operation_mode = 0;
    #12;
    rst_n = 1;
    repeat (5) @(posedge clk);
    // TEST 1: Bypass disabled, identity mapping with bit reversal.
    data_in = {8'h04, 8'h03, 8'h02, 8'h01};
    swizzle_map_flat = {3'b011, 3'b010, 3'b001, 3'b000};
    bypass = 0;
    operation_mode = 0;
    repeat (5) @(posedge clk);
    for(i = 0; i < NUM_LANES; i = i + 1)
      out_lane[i] = get_lane(i);
    expected[0] = bit_reverse(8'h01);
    expected[1] = bit_reverse(8'h02);
    expected[2] = bit_reverse(8'h03);
    expected[3] = bit_reverse(8'h04);
    if(out_lane[0]==expected[0] && out_lane[1]==expected[1] &&
       out_lane[2]==expected[2] && out_lane[3]==expected[3])
      $display("TEST 1 PASS");
    else
      $display("TEST 1 FAIL: Expected %h %h %h %h, Got %h %h %h %h",
               expected[0], expected[1], expected[2], expected[3],
               out_lane[0], out_lane[1], out_lane[2], out_lane[3]);
    if(invalid_mapping_error==0)
      $display("TEST 1 INVALID MAPPING PASS");
    else
      $display("TEST 1 INVALID MAPPING FAIL");

    // TEST 2: Reverse mapping.
    data_in = {8'hAA, 8'hBB, 8'hCC, 8'hDD};
    swizzle_map_flat = {3'b000, 3'b001, 3'b010, 3'b011};
    bypass = 0;
    operation_mode = 0;
    repeat (5) @(posedge clk);
    for(i = 0; i < NUM_LANES; i = i + 1)
      out_lane[i] = get_lane(i);
    // Expected output is reversed compared to input lane order.
    expected[0] = bit_reverse(8'hAA);
    expected[1] = bit_reverse(8'hBB);
    expected[2] = bit_reverse(8'hCC);
    expected[3] = bit_reverse(8'hDD);
    if(out_lane[0]==expected[0] && out_lane[1]==expected[1] &&
       out_lane[2]==expected[2] && out_lane[3]==expected[3])
      $display("TEST 2 PASS");
    else
      $display("TEST 2 FAIL: Expected %h %h %h %h, Got %h %h %h %h",
               expected[0], expected[1], expected[2], expected[3],
               out_lane[0], out_lane[1], out_lane[2], out_lane[3]);

    // TEST 3: Bypass mode active.
    data_in = {8'h11, 8'h22, 8'h33, 8'h44};
    swizzle_map_flat = {3'b001, 3'b000, 3'b011, 3'b010};
    bypass = 1;
    operation_mode = 0;
    repeat (5) @(posedge clk);
    for(i = 0; i < NUM_LANES; i = i + 1)
      out_lane[i] = get_lane(i);
    expected[0] = bit_reverse(8'h44);
    expected[1] = bit_reverse(8'h33);
    expected[2] = bit_reverse(8'h22);
    expected[3] = bit_reverse(8'h11);
    if(out_lane[0]==expected[0] && out_lane[1]==expected[1] &&
       out_lane[2]==expected[2] && out_lane[3]==expected[3])
      $display("TEST 3 PASS");
    else
      $display("TEST 3 FAIL: Expected %h %h %h %h, Got %h %h %h %h",
               expected[0], expected[1], expected[2], expected[3],
               out_lane[0], out_lane[1], out_lane[2], out_lane[3]);

    // TEST 4: Invalid mapping detection.
    data_in = {8'h55, 8'h66, 8'h77, 8'h88};
    swizzle_map_flat = {3'b011, 3'b010, 3'b001, 3'b100};
    bypass = 0;
    operation_mode = 0;
    repeat (5) @(posedge clk);
    if(invalid_mapping_error==1)
      $display("TEST 4 PASS: Invalid mapping detected");
    else
      $display("TEST 4 FAIL: Invalid mapping not detected");
    $finish;
  end

endmodule

